insert into humour_items values("","2","C'est le devoir de chaque homme de rendre au monde au moins autant qu'il en a reçu.<br/><br/>Albert Einstein");
insert into humour_items values("","2","C'est le rôle essentiel du professeur d'éveiller la joie de travailler et de connaître.<br/><br/>Albert Einstein");
insert into humour_items values("","2","C'est un véritable miracle de voir que les méthodes modernes d'instruction n'ont pas encore entièrement étouffé la saine curiosité intellectuelle ; cette petite plante délicate, en plus d'un encouragement, a surtout besoin de liberté ; sans quoi elle s'étiole et ne manque pas de périr.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Ce qui est incompréhensible, c'est que le monde soit compréhensible.<br/><br/>Albert Einstein");
