insert into humour_items values("","2","Ce qui m'intéresse vraiment c'est de savoir si Dieu avait un quelconque choix en créant le monde.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Celui qui ne peut plus éprouver ni étonnement ni surprise est pour ainsi dire mort : ses yeux sont éteints.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Deux choses sont infinies : l'univers et la bêtise humaine ; en ce qui concerne l'univers, je n'ai pas acquis la certitude absolue.<br/><br/>Albert Einstein");
