insert into humour_items values("","2","Le hasard, c'est Dieu qui se promène incognito.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Le mental intuitif est un don sacré et le mental rationnel est un serviteur fidèle. Nous avons créé une société qui honore le serviteur et a oublié le don.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Le monde est dangereux à vivre ! Non pas tant à cause de ceux qui font le mal, mais à cause de ceux qui regardent et laissent faire.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Le problème aujourd'hui n'est pas l'énergie atomique, mais le cœur des hommes.<br/><br/>Albert Einstein");
