insert into humour_items values("","2","Ne vous inquiétez pas pour vos difficultés en mathématiques, les miennes sont encore plus grandes.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Placez votre main sur un poêle une minute et ça vous semble durer une heure. Asseyez vous auprès d'une jolie fille une heure et ça vous semble durer une minute. C'est ça la relativité.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Prisonniers sans le savoir de leur propre égoïsme, les êtres humains se sentent en état d’insécurité, isolés et privés de la naïve, simple et pure joie de vivre.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Quiconque prétend s'ériger en juge de la vérité et du savoir s'expose à périr sous les éclats de rire des dieux puisque nous ignorons comment sont réellement les choses et que nous n'en connaissons que la représentation que nous en faisons.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Rien n'est plus proche du vrai que le faux.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Si j'avais seulement su, j'aurais fait serrurier.<br/><br/>Albert Einstein");
insert into humour_items values("","2","Si la relativité se révèle juste, les allemands diront que je suis allemand, les suisses que je suis citoyen suisse, et les français que je suis un grand homme de science. Si la relativité se révèle fausse, les français diront que je suis suisse, les suisses que je suis allemand et les allemands que je suis juif.<br/><br/>Albert Einstein");
