INSERT INTO `resource_online` VALUES (22, 1, '2006-03-16 14:04:26', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 1 ', 'Annuaire de NAVIKID - Un site pour les kids de 8 à 15 ans', 'L''Annuaire de NAVIKID est réservé aux sites ayant un rapport direct avec les enfants de 8 à 14 ans. Si vous voulez enrichir la liste déjà existante, allez dans [Ajouter Site]. Le Webmaster de Navikid se réserve néanmoins le droit de retenir le site ou les sites que vous proposez.', '', ' Annuaire sites ', 'http://www.navikid.net/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (23, 1, '2006-03-16 14:20:21', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'Le musée amusant de France 5', 'Jouer avec les couleurs, les techniques, les histoires de tableaux. Découvrez les peintres des musées.\r\nNicolas Poussin, Henri Rousseau, Marc Chagall, Paolo Ucello, Georges de la Tour, Jean Siméon Chardin, Georges Seurat, Claude Monet, Louise Moillon, Henri Matisse, Giuseppe Arcimboldo', 'France 5', ' Couleurs  techniques  histoires arts  tableaux  peintres ', 'http://www.france5.fr/zouzous/museeamusant/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (24, 1, '2006-03-16 14:33:19', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'L''art en s''amusant', 'Les enfants exposent leurs œuvres ou vous présentent leur création \r\n\r\nA travers le parcours jeu « le monde du tableau », les enfants découvrent « la recette » des peintres. Espace, composition, lumière, couleurs, touche… n’ont plus de secrets. En atelier, les ingrédients sont là. La feuille blanche se transforme en une marine vivante et colorée. Bonne activité des élèves.', 'Fondation Bemberg', ' Le portrait  scènes de genre  nature morte  histoire de la peinture ', 'http://www.bemberg-educatif.org/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (25, 1, '2006-03-16 14:44:06', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 20 ', ' 1 ', 'Les couleurs de l''orchestre', 'Les instruments et leurs épisodes, les choeurs et les épisodes,une encyclopédie musicale, l''opéra et Verdi.\r\nInstruments de musique, violon, flûte, Contrebasse, Violoncelle,Violon Alto, Hautbois,Clarinette, Trompette, Percussions, Cor\r\n\r\nVidéos, Vocabulaire, et jeux pour tous les instruments de musique.', 'Eva Martínez i Carrasco (CAP CANAL) http://www.capcanal.com/couleurs/credits.htm', ' Instruments de musique  violon  flûte  Contrebasse  Violoncelle Violon Alto  Hautbois Clarinette ', 'http://www.capcanal.com/couleurs/index.htm', 'fr.png', 'N', 0, 'O');
