INSERT INTO `resource_online` VALUES (34, 1, '2006-03-18 18:43:21', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 18 ', ' 1 ', '1, 2, droits... La Convention Internationale des Droits de l’Enfant entre à l’éc', 'Parue dans le n°163-164 de la revue Animation &amp; Education, cette fiche reprend point par point les articles de la CIDE en lien avec les Instructions Officielles. A chaque article de la CIDE correspond une liste d''activités ou de dispositifs coopératifs pour la classe.', 'OCCE', ' Droits enfant  citoyenneté  OCCE ', 'http://www.occe.coop/federation/files/actions/droit/123-la%20CIDE.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (35, 1, '2006-03-18 18:46:02', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 1 ', ' 18 ', ' 1 ', 'Estime de soi au cycle 1', 'Parue dans le n°174-175 de la revue Animation &amp; Education, cette fiche, réalisée à partir du travail de Solange Rioux, aborde la question de &quot;L''Estime de soi&quot; au cycle 1.\r\nCette fiche propose des pistes autour des conditions requises, des situations et dispositifs et des supports institutionnels.', 'OCCE', ' Education citoyenneté ', 'http://www.occe.coop/federation/files/actions/droit/Estime-de-soi.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (36, 1, '2006-03-18 18:48:30', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 18 ', ' 1 ', 'L''estime de soi au cycle 2 et au cycle 3', 'Parue dans le n°181-182 de la revue Animation &amp; Education, cette fiche, aborde la question de &quot;L''Estime de soi&quot; au cycle 2 et 3.\r\nCette fiche propose des pistes autour des conditions requises, des situations et dispositifs et des supports institutionnels.', 'OCCE', ' Education citoyenneté  estime  respect ', 'http://www.occe.coop/federation/files/actions/droit/estime_de_soi_c2_et_3.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (37, 1, '2006-03-18 18:54:59', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 18 ', ' 1 ', 'La convention internationale des droits de l''enfant', 'Parue dans le n°187-188 de la revue Animation &amp; Education, ce document, présente à l''enseignant et à l''élève, les Droits de l''Enfant en s''appuyant sur la CIDE. Cette fiche propose quelques repères et pistes, pour que la classe devienne un lieu où les élèves s''approprient leurs droits.', 'OCCE', ' Citoyenneté  droits enfants  OCCE ', 'http://www.occe.coop/federation/files/actions/droit/4pages3.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (38, 1, '2006-03-18 18:59:54', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 2 ', 'Site de l''office central de la coopérative de l''école', 'L’Office Central de la Coopération à l’Ecole pour enseigner  dès l’école, les principes et les vertus de la Coopération. S’appuyant sur un certain nombre de spécificités directement issues de la doctrine coopérative et associative (l’aide, l’entraide, le tutorat, la cotisation volontaire, le projet, le conseil de coopérative, par exemple).', 'OCCE', ' OCCE  coopérative  conseils ', 'http://www.occe.coop/federation/index.htm', 'fr.png', 'N', 0, 'O');
