INSERT INTO `resource_online` VALUES (41, 1, '2006-07-23 07:58:17', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 3 ', 'Guide du débutant de l''académie de Créteil (Inspection académique du Val de Marn', 'Guide abordant tous les points généraux pour la prise de fonction à la sortie de l''IUFM\r\nPrise de contact avec l''école\r\nAménager sa classe à l''école maternelle\r\nAménager sa classe en école élémentaire\r\nOutils des élèves\r\nPremier jour de classe à l''école maternelle\r\nPremier jour de classe à l''école élémentaire\r\nGestion du groupe classe\r\nAffichages dans la classe\r\nUtiliser un manuel\r\nL''enseignant et les intervenants dans l''école\r\nRelations avec les parents\r\nRéunion de rentrée avec les parents\r\nLes grands repères de l''année\r\nBibliographie', 'Inspection académique du Val de Marne', ' prise de fonctionnement accompagnement premier jour  gestion  affichage manuel ', 'http://www.ac-creteil.fr/ia94/guide_du_debutant/', 'fr.png', 'N', 0, 'O');
