INSERT INTO `resource_online` VALUES (64, 1, '2006-03-29 00:11:47', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 16 ', ' ', 'Les mystères du corps humain', 'Vous le côtoyez tous les jours, ce corps. Mais, vous êtes-vous déjà demandé les nombreux &quot;comment&quot; et &quot;pourquoi&quot;? Comment la nourriture se transforme dans votre système digestif? Comment pouvez-vous apercevoir ce qui se trouve près de vous? Pourquoi avons-nous besoin de respirer? Comment bébé se transforme dans le ventre de maman?\r\nDocuments  : schéma à compléter, résumé à retenir, évaluation.', 'bouche, mastication, aliments, salive,œsophage, estomac, sucs digestifs, intestin', ' Système respiratoire  système digestif ', 'http://www.corps.dufouraubin.com/home.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (65, 1, '2006-03-29 00:23:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 9 ', ' 16 ', ' 1 ', 'Os et muscles', 'Documents  : radiographie, schéma à compléter.\r\nMots clés : squelette, muscles, tendons, articulations, se déplacer.', 'inrp', ' squelette  muscles  tendons  articulations  se déplacer. ', 'http://venus.inrp.fr/lamap/activites/locomotion/sequence/mouvement/sommaire.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (66, 1, '2006-03-29 16:22:36', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 9 ', ' 1 ', 'Utiliser le CLIC  de l''ordinateur', 'Cycles 1-2, apprendre le clic, le double clic et le clic-glissé. Savoir les utiliser avec initiative.', '', ' souris clic ordinateur logiciel ', 'http://dan.gros.free.fr/logiciels/Clic.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (67, 1, '2006-03-29 16:27:37', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 7 ', ' 1 ', 'Logiciel Egal', 'Logiciel gratuit permettant de comparer des quantités au cycle 1', '', ' nombres  numération  collection  comparer ', 'http://dan.gros.free.fr/logiciels/egal.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (68, 1, '2006-03-29 16:30:17', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel FicheMot', 'Cycle 2 rentrez vos phrases, le logiciel fabrique des jeux sur les lettres, mots, phrases. Logiciel ouvert, utilisable de la MS au CE1 (il ne fait rien sans VOS textes) qui permet de faire les 9 &quot;jeux&quot; annoncés (très progressifs, les derniers sont difficiles) à partir des phrases rentrées (au moins 3 phrases de 4 mots, un éditeur de fichiers est intégré)', '', ' Exercices  phrases  lectures  mots ', 'http://dan.gros.free.fr/logiciels/Fichmot.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (69, 1, '2006-03-29 16:35:11', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 9 ', ' 4 ', 'Logiciel Association d''idées', 'Faire des associations \r\nCycle 2, tout ce qui va par 2 se mélange, il faut réorganiser les paires. (Jusqu''à 9 paires, utilisation possible de police cursive).Un logiciel ouvert qui permet de mettre par deux des étiquettes. (Il ne fait rien sans les exercices que vous y rentrerez ! Un éditeur est fourni dans le logiciel.)\r\nL''écriture est plus grosse que dans &quot;paires&quot;, et un menu permet de changer la graphie (cursive ou scripte) : on peut mettre 2 mots identiques, script à gauche et cursive à droite...\r\nExemples : 2 identiques (lettres, mots, signes...) 2 complémentaires (nombres, objets, déterminant + nom...) 2 contraires, 2 accords, 2 événements (associations d''idées), un titre de livre et le héros principal...\r\nJusqu''à 9 paires simultanées, les élèves peuvent faire les exercices eux-mêmes pour leurs camarades.', '', ' Association  lecture  logique ', 'http://dan.gros.free.fr/logiciels/Paires_CP.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (70, 1, '2006-03-29 16:38:57', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciels Puzzles (cycle 2 et 3)', 'Puzzle (cycles 2 et 3)\r\n\r\nUn logiciel qui permet de faire réaliser des puzzles géométrique progressifs.\r\n\r\nPas d''installation : copier le dossier complet sur le disque dur, et lancer\r\n\r\nSuivi de l''élève (cycles 2 et 3)\r\n\r\n3 formulations de réussite possibles, 4 pages à ne pas donner d''un coup !', '', '  ', 'http://dan.gros.free.fr/logiciels/Puzzle.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (71, 1, '2006-03-29 17:00:14', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 1 ', 'Logiciel pour réviser les tables', 'Pour s''exercer aux tables (ou s''évaluer).Cycles 2-3, entraînement personnel de l''élève sur les tables de son choix.\r\nCe programme permet de choisir le type d''opération voulu, la table à travailler (ou l''ensemble des tables)\r\nIl donne un bilan de travail effectué.\r\nTable d''additions, de multiplications, de soustractions, de divisions', '', 'tables  mathématiques  tables d''addition  tables de multiplications  tables de soustraction', 'http://dan.gros.free.fr/logiciels/Tables.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (72, 1, '2006-03-29 17:03:33', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 19 ', ' 4 ', 'Logiciel pour faire des associations', 'Cycle 3, tout ce qui va par 2 se mélange, il faut réorganiser les paires. (Jusqu''à 15 paires).\r\nUn logiciel ouvert qui permet de mettre par deux des étiquettes. (Il ne fait rien sans les exercices que vous y rentrerez ! Un éditeur est fourni dans le logiciel.)\r\nExemples : 2 identiques (lettres, mots, signes...) 2 complémentaires (nombres, objets, sujet + verbe...) 2 contraires, 2 accords, 2 événements (une date et un événement, un pays et sa capitale...), un mot et sa traduction (voir exemple pour collège sur les verbes irréguliers)\r\n\r\nEn verrouillant l''ordre, on peut reconstituer un texte puzzle. En changeant l''ordre des paires, on peut jouer sur l''effet &quot;thème / version&quot;... On peut répondre en cliquant sur la proposition ou en tapant au clavier.\r\n\r\nJusqu''à 15 paires simultanées, les élèves peuvent faire les exercices eux-mêmes pour leurs camarades.', 'Gros', 'Association d''idées  exerciseurs', 'http://dan.gros.free.fr/logiciels/Paires.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (73, 1, '2006-03-29 17:15:28', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel Calcul Mental au cycle 3', 'Cycle 3, 39 niveaux de calcul mental sur les nombres entiers.\r\n\r\nPour s''exercer au calcul mental au cycle 3. (surtout CM1 et CM2)\r\nBasé sur le principe de &quot;l''excellence&quot; (on ne passe que si on fait un sans-faute) il demande une forte concentration.', 'Daniel Gros', ' Calcul mental ', 'http://dan.gros.free.fr/logiciels/Calcul_mental.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (74, 1, '2006-03-29 17:49:58', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Calcul mental pour les nombres décimaux', 'Pour s''exercer au calcul mental sur les décimaux au cycle 3. (surtout CM1 et CM2)\r\nBasé sur le principe de &quot;l''excellence&quot; (on ne passe que si on fait un sans-faute) il demande une forte concentration.', 'Daniel Gros', ' Calcul mental  nombres décimaux ', 'http://dan.gros.free.fr/logiciels/Nb_decim.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (75, 1, '2006-03-29 17:54:04', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel de logique', '10 niveaux progressifs d''énigmes &quot;topologiques&quot; qui doivent être résolues de manière logique et déductive. La mémorisation du parcours est parfois nécessaire (intéressant au niveau de &quot;l''imagerie mentale&quot;). L''esthétique est simple et explicite. Tous niveaux à partir de la GS.', 'Daniel Gros', ' Logique  déduction  problèmes  mémorisation ', 'http://dan.gros.free.fr/logiciels/Enigmes.html', 'fr.png', 'N', 0, 'O');
