INSERT INTO `resource_online` VALUES (84, 1, '2006-04-04 10:23:15', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 9 10 11 13 ', ' 2 3 11 22 ', ' 4 ', 'PrimLangues', 'PrimLangues, un portail et un site de ressources propres pédagogiques pour l''enseignement des langues dans le primaire : des ressources diversifiées, accompagnées de commentaires (textes officiels, ouvrages de références, sitographies ...) et des propositions de séquences pédagogiques.', 'Ministère de l''Education Nationale', ' langue  allemand  anglais  arabe  espagnol  russe  portugais  italien ', 'http://www.primlangues.education.fr', 'fr.png', 'N', 0, 'O');
