INSERT INTO `resource_online` VALUES (97, 1, '2006-04-30 16:04:01', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 5 ', ' 8 ', ' 4 ', 'Le petit déjeuner d''Ali', 'Pour lire l''histoire d''Ali c''est très simple, dans chaque page qui apparaît à l''écran, clique sur un des objets animés et celui-ci te mènera à la page suivante de l''histoire. Attention, tous les objets ne marchent pas forcement, il faut trouver le bon !Pour commencer clique sur une des animations proposées', '', ' Petit Déjeuner histoire animée ', 'http://vbelmokhtar.free.fr/ali/page0.htm', 'fr.png', 'N', 0, 'O');
