INSERT INTO `resource_online` VALUES (100, 1, '2006-04-30 16:13:53', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 20 ', ' 4 ', 'Tralalere le site musical des 3-10 ans', 'le site musical des 3-10 ans\r\n\r\ntralalere.com est un site ludo-culturel et musical destiné aux enfants, qui réunit une majorité d''artistes francophones. Adapté aux 3-10 ans par son ergonomie conviviale et animée, le site est personnalisé en fonction du profil de l''enfant.', '', ' musique  solfège ', 'http://www.tralalere.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (101, 1, '2006-04-30 16:17:38', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 5 ', ' 9 ', ' 4 ', 'Les jeux de Lulu', 'Logique, stratégie;Autour du nombre;Des lettres, des mots...;Orientation, déplacement; \r\n Logique, stratégie;puzzles;mois de l''année;jeux\r\n\r\n@ Où se cache Tobi ? *\r\n@ Où se cache Babou ?\r\n@ Le bon ordre * *\r\n@ La guirlande de fleurs * \r\n@ Le bon rythme * *\r\n@ Qui manque ?\r\n@ Même chapeau, même forme ou même couleur *\r\n@ Et si on rangeait... * * * du nouveau\r\n@ Le mystère des coffres * *\r\n\r\nDes lettres, des mots...\r\n\r\n@ La promenade du prénom *\r\n@ Les abécédaires * * °\r\n@ Alphabet * * \r\n@ Lulu et le loup * \r\n@ Lili fait les courses *\r\n@ Memory * * °\r\n@ Pour qui...? (le jeu des rimes) * * °\r\n@ Quelques mots * * * °\r\n@ Paires : des animaux sauvages (niv. 3) *\r\n@ Le bon choix * * °\r\n@ Classement... * * °\r\n\r\nObservation', '', ' Logique  stratégie Autour du nombre  Des lettres  des mots... Orientation  déplacement ', 'http://perso.wanadoo.fr/jeux.lulu/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (102, 1, '2006-04-30 16:20:59', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 13 ', ' 1 ', 'Les jeux de Lulu-mathématiques', 'Autour des math...\r\n\r\n@ Le nombre mystérieux\r\n@ Le nombre mystérieux\r\n@ Même quantité *\r\n@ Les jetons\r\n@ La promenade des nombres * \r\n@ Où se cache Minou ?\r\n@ Tables de multiplication * *\r\n@ La pièce d''or\r\n@ Les quatre nombres *\r\n@ Le Taxateur', '', ' Tables problèmes ', 'http://perso.wanadoo.fr/jeux.lulu/#outils', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (103, 1, '2006-04-30 16:27:54', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 5 ', ' 9 ', ' 4 ', 'Le cercle enchanté', 'Le Cercle enchanté s''adresse à l''enfant de quatre et de cinq ans pour construire le  concept du nombre. \r\nComplice de deux personnages, Mélisse et Serpolet, qui vivent l''âge de l''imaginaire et du merveilleux, l''enfant passera naturellement de l''univers symbolique à celui des signes propres aux différents langages.', '', ' Contruction du nombres:numération ', 'http://station05.qc.ca/css/cercle/accueil.html', 'fr.png', 'N', 0, 'O');
