INSERT INTO `resource_online` VALUES (112, 1, '2006-07-20 19:56:41', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 19 ', ' 1 ', 'Projets images et projets Piccolo', 'Pinnacle, le créateur du logiciel Studio a  conçu un projet &quot;Piccolo&quot;, qui fournit une mallette composée du logiciel, d''une webcam, d''un dossier enseignant et d''une trentaine de dossiers enfants leur expliquant le vocabulaire, les techniques d''animation, la préparation du projet, des conseils, l''utilisation du logiciel avec le scenario, ...Côté sponsor, ils\r\nfournissent également une version démo du logiciel à chaque enfant !', '', ' images webcam animations ', 'http://www.pinnaclesys.com/PublicSite/fr/Products/Education/', 'fr.png', 'N', 0, 'O');
