INSERT INTO `resource_online` VALUES (114, 1, '2006-07-20 20:10:04', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 1 ', 'Logiciels sur les homophones', 'Voici le 3ème exercice interactif concernant les homophones. Le thème est consacré à : a - as - à et ou - où\r\nIl y a 250 phrases prises aléatoirement par groupes de 20 questions. En cas d''erreur, l''E reçoit la règle de même que la réponse correcte.', 'Michel Neroucheff', ' Homonophones ', 'http://neroucheffmichel.ozt4u.net/html/homophone03/homophone03.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (115, 1, '2006-07-20 20:20:41', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 1 ', 'Enquêtes du policier Lafouille', 'Textes avec enquêtes policières. A partir d''indices dans des textes, les élèves doivent chercher le coupable. Lecture facile pour des élèves du cycle 3. \r\nRéponses auto-correctives jointes aux textes.', '', ' Enquêtes policières ', 'http://pedagogie.ac-aix-marseille.fr/etablis/ecoles/rustrel/pedago/lafouine.doc', 'fr.png', 'N', 0, 'O');
