INSERT INTO `resource_online` VALUES (123, 1, '2006-07-20 22:50:40', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Les difficultés en conjugaison', 'Des mots croisés et des situations pour s''approprier la conjugaison des verbes.\r\nL''imparfait\r\nLes verbes en CER\r\n\r\nLes verbes en GER\r\n\r\n\r\nLes verbes en YER\r\n\r\n\r\nLes verbes en IER\r\n\r\nLe présent\r\nLes verbes en DRE\r\n\r\nLes verbes en NDRE\r\n\r\n\r\nLes verbes en IR\r\n\r\n\r\nLes verbes en OUDRE\r\n\r\nLe futur\r\nDes verbes de tous les groupes\r\n\r\n\r\n\r\nL''impératif présent\r\nDes verbes de tous les groupes\r\n\r\n\r\nLe conditionnel présent\r\nDes verbes de tous les groupes', '', ' conjugaison présent:futur:imparfait conditionnel impératif ', 'http://www.scalpa.info/orl_conj.php', 'fr.png', 'N', 0, 'O');
