INSERT INTO `resource_online` VALUES (134, 1, '2006-07-20 23:41:42', '0000-00-00 00:00:00', 0, 0, ' ', ' 8 ', ' ', ' 1 ', 'Séquane .. un site pour produire des exercices interactifs', 'Ce site est consacré aux systèmes-auteurs permettant de produire des \r\nexercices interactifs sur le Web et, notamment, à Hot Potatoes et Netquiz, JClic\r\n Vous trouverez ici entre autres la traduction des tutoriels, des articles, \r\ndes manuels, des forums et  des liens vers d''autres ressources concernant \r\nles générateurs d''exercices pour le Web et les TICE.', '', 'système auteur exerciseur générateur d''exercices', 'http://www.sequane.com', 'fr.png', 'N', 0, 'O');
