INSERT INTO `resource_online` VALUES (144, 1, '2006-07-21 11:24:58', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 1 ', 'Sites des dictionnaires Harry Potter', 'Dictionnaires de l''univers francophone de Harry Potter (noms propres, noms communs, français-anglais, Potter pour tous, personnages, plantes-potions, formules-sorts, livres magiques) en forme de dictionnaire papier.', '', ' dictionnaire noms propres noms communs ', 'http://harrydico.net/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (145, 1, '2006-07-21 11:31:45', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 16 ', ' 1 ', 'Dictionnaire des animaux', 'Dictionnaire bien adapté pour les élèves de l''école primaire. Les animaux sont présentés simplement avec une entrée &quot;visuelle&quot;, le régime alimentaire ... On peut avoir un lien direct sur un site pour avoir plus de renseignements.Possibilité d''écouter et d''avoir le vocabulaire du bruit de l''animal.\r\nUn jeu sur les animaux est également disponible. &quot;Clique sur la lettre qui correspond au nom de l''animal que tu vois en photo.&quot;', '', ' dictionnaire animaux bruit ', 'http://tizours.free.fr/dicoanimaux/table.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (146, 1, '2006-07-21 11:37:44', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Bréviare d''orthographe française BOF', 'Un aide-mémoire sur l''orthographe et la grammaire. \r\n\r\nBOF est un fichier d''aide (graticiel au format HLP) sur les règles et leurs exceptions concernant le langage écrit. Il a été conçu comme un complément au dictionnaire. \r\nQuestion : comment se souvenir \r\n      - que pot-au-feu est invariable ? \r\n      - que l''on écrit un mille-pattes, mais un mille-feuille ? \r\n      - que l''on écrit un portefeuille, mais un porte-monnaie ? \r\n      - que l''on écrit de-ci de-là, mais deçà delà ? \r\n      - que l''on écrit « les grosses gens sont toujours joyeux » ?', 'Marc Goldstein', ' ORL orthographe règles orthographe ', 'http://mapage.noos.fr/mp2/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (147, 1, '2006-07-21 11:40:12', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Dictionnaire de conjugaison', 'Pour connaître toutes les conjugaisons d''un verbe (tous les temps, tous les modes) et imprimer sa fiche, saisissez le verbe recherché dans la fenêtre et l''affaire est jouée !', 'Auteur de &quot;Ca peut servir&quot;', ' conjugaison verbes temps modes ', 'http://www.capeutservir.com/verbes/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (148, 1, '2006-07-21 11:46:06', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Dictionnaire des synonymes ( lexique français)', 'Taper un mot dans la fenêtre du dictionnaire des synonymes. Les élèves trouvent rapidement des synonymes', '', ' dictionnaire synonymes ', 'http://www.dictionnaire-synonymes.com/', 'fr.png', 'N', 0, 'O');
