INSERT INTO `resource_online` VALUES (150, 1, '2006-07-21 11:51:13', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'DIctionnaire des synonymes de l''Université de Caen', 'Ce dictionnaire des synonymes contient approximativement  49 000 entrées  et  396 000 relations synonymiques . La base de départ est constituée de sept dictionnaires classiques (Bailly, Benac, Du Chazaud, Guizot, Lafaye, Larousse et Robert) dont ont été extraites les relations synonymiques.', 'Université de Caen', ' dictionnaire synonymes ', 'http://elsap1.unicaen.fr/dicosyn.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (151, 1, '2006-07-21 11:54:40', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Dictionnaire - Grammaire interactive', 'Les réponses à vos questions d''orthographe, de grammaire, des tests...\r\nFenêtre pour la recherche', '', ' grammaire dictionnaire ', 'http://grammaire.reverso.net/', 'fr.png', 'N', 0, 'O');
