INSERT INTO `resource_online` VALUES (154, 1, '2006-07-21 12:10:17', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Vérificateur orthographe  : écrire un nombre en lettres', 'Vérificateur en ligne pour savoir écrire à bon escient les nombres en toute lettre. Rappel des règles à côté de la réponse', '', ' orthographe vérificateur nombres lettres ', 'http://www.leconjugueur.com/frnombre.php', 'fr.png', 'N', 0, 'O');
