INSERT INTO `resource_online` VALUES (156, 1, '2006-07-21 12:25:25', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Le dictionnaire tout en un', 'Un tout-en-un comme on les aime (orthographe, synonymes, conjugaison, encyclopédie). Simple, pratique et efficace.', '', ' dictionnaire orthographe synonymes  conjugaison encyclopédie ', 'http://www.le-dictionnaire.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (157, 1, '2006-07-21 12:26:51', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Le dictionnaire multi-fonctions de TV5', 'Saisissez votre requête et validez pour obtenir la définition ou cliquez sur l''onglet de votre choix pour les autres fonctionnalités. (synonyme, dictionnaire Anglais Français ou Français Anglais)', '', ' dictionnaire ', 'http://dictionnaire.tv5.org/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (158, 1, '2006-07-21 12:51:13', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Orthonet', 'Saisissez le mot dont vous recherchez l''orthographe : \r\n(tapez UN SEUL MOT. Sous sa forme standard: nom au singulier, verbe à l''infinitif)\r\n\r\nLEXIQUE \r\nConsultation rapide du lexique grâce au formulaire sur cette page (formes difficiles, accords douteux, conjugaisons)\r\n\r\nLe Lexique propose également des informations sur l''orthographe des nombres, des éléments de grammaire, des éléments sur l''orthographe nouvelle...  \r\n  QUESTIONS  \r\nQuand le LEXIQUE ne suffit pas, des linguistes attendent votre question ; réponse rapide, précise, documentée. \r\n  CORRECTIONS \r\nUn texte (court) à contrôler ? il vous est rendu, orthographe vérifiée et corrigée.', '', ' orthographe ORL ', 'http://www.sdv.fr/orthonet/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (159, 1, '2006-07-21 13:25:11', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Accord des adjectifs (Mémento)', 'Synthèse sur les accords des adjectifs numéraux et adjectifs de couleur\r\n  1.  Attribut ou Épithète\r\n   2. Écriture des nombres\r\n   3. Accord des adjectifs numéraux\r\n   4. Accord de demi et de nu\r\n   5. Accord de possible\r\n   6. Accord des adjectifs de couleur', '', '  ', 'http://www.csrs.qc.ca/Goeland/proj/envolee/matieres/francais/caprices/adjectifs2.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (160, 1, '2006-07-21 13:33:29', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Exemple de journal pour le cycle 3', 'Le petit Merrillon est un journal qui connaît un vif succès.  Une mise en ligne est prévue qu''un tirage papier', '', ' journal écrits journalistiques ', 'http://petitmerrillon.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (161, 1, '2006-07-21 13:36:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 16 ', ' 1 ', 'Fiche pour apprendre à classer les être vivants', 'Observer et comparer des caractères morphologiques chez les animaux\r\n• Approche de la notion d''espèce\r\n• Distinguer trier, ranger et classer\r\n• Comprendre et utiliser les principes de la classification scientifique\r\n• Retenir et savoir re-investir avec des échantillons différents les caractères exclusifs de quelques classes (poils &gt;mammifères, plumes &gt;oiseaux ...) (cycle 2)\r\n• Savoir reconstruire les liens de parenté (la phylogénie) d''un échantillon d''espèces et les relier au concept d''évolution (cycle 3)', '', ' classification animaux ', 'http://www.perigord.tm.fr/%7Eecole-scienc/PAGES/MONVIV/Classif/SoMod.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (162, 1, '2006-07-21 13:41:07', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 16 ', ' 3 ', 'Textes et ressources pour les sciences à l''école EDUSCOL', 'Consulter les textes de référence\r\nAccéder à des ressources nationale\r\nAccéder à des ressources académiques, d''IUFM, départementales, de circonscriptions ou d''écoles\r\nAccéder à des ressources non institutionnelles conformes aux programmes et aux prescriptions du plan de rénovation (Rubrique en projet)', '', '  ', 'http://eduscol.education.fr/D0027/EXSACC.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (163, 1, '2006-07-21 13:43:41', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 4 ', ' 16 ', ' 1 ', 'Vingt-six fiches pour les sciences éditées par le CNDP', 'Les 26 fiches connaissances 2002  éditées par le SCÉRÉN-CNDP dans le cadre des documents d''application des programmes 2002 sont une actualisation et une extension des 20 fiches connaissances diffusées en 1998 dans toutes les circonscriptions d''IEN et élaborées par un groupe mis en place par la direction des écoles.', '', '  ', 'http://www.cndp.fr/textes_officiels/ecole/sciences-fiches-c2c3.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (164, 1, '2006-07-21 13:53:53', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 16 ', ' 3 ', 'Enseigner les sciences en Périgord', 'Ce site est un site départemental du réseau &quot;La Main à la Pâte&quot;.\r\nLe site ariégeois des sciences à l''école propose des liens vers les  cahiers d''expérience  développés dans certains départements. De nombreux liens pour les sciences à l''école', 'IA du Périgord', ' cahier expériences sciences ', 'http://pedagogie.ac-toulouse.fr/ariege-education/sciences09/php/article.php3?id_article=14', 'fr.png', 'N', 0, 'O');
