INSERT INTO `resource_online` VALUES (171, 1, '2006-07-21 15:30:06', '0000-00-00 00:00:00', 0, 0, ' ', ' 12 ', ' ', ' 2 3 ', 'Skype pour téléphoner gratuitement', 'Logiciel permettant la téléphonie sur Internet d''une façon gratuite. Utisation possible pour les directeurs et voire même pour les élèves pour les échanges &quot;inter-classes&quot;.', '', ' communication  téléphonie Internet:Skype ', 'http://telecharger-skype-fr.com/telephone-gratuit/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (172, 1, '2006-07-21 16:29:28', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Les mots les plus fréquents de la langue française Echelle Dubois Buyse', 'Liste des mots de l''échelle Dubois-Buyse\r\nliste-mots-alphabetique.txt\r\nliste-mots-nature-frequence.txt\r\nliste-mots-par-frequence.txt\r\nliste-mots-par-frequence.xls\r\néchelle Dubois-Buyse', '', ' orthographe orthographe lexicale échelle Dubois Buyse ', 'http://www.scalpa.info/orl_ortho.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (173, 1, '2006-07-21 16:33:09', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 8 ', ' 1 ', 'Exploiter un roman au cycle 3 - Exploit de GARA de Chantal Crétois', 'Présentation du roman &quot;SCALPA&quot; de Chantal Crétois / Bruno Pilorget.\r\nRésumé, intrigues et pistes d''exploitation pour le cycle 3.\r\n\r\nRésumé:\r\nAujourd''hui, pour la première fois, le jeune Gara participe à la chasse aux bisons. Il doit se montrer aussi courageux que son père, le chef de la tribu, qui à l''âge de treize ans a terrassé un ours deux fois plus gros que lui. Seulement voilà, un bison charge le jeune garçon. Pris de panique, il s''enfuit vers le campement, mettant en danger les femmes et les enfants. La punition tombe comme un couperet : Gara est chassé de la tribu. Pourra-t-il survivre loin des siens et retrouver la confiance de son père?', 'Auteur de Scalpa', ' roman littérature scalpa Chantal Crétois ', 'http://www.scalpa.info/gara.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (174, 1, '2006-07-21 16:38:02', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 1 ', 'Exploiter un roman au cycle 3 - Charlie et la Chocolaterie de Roald Dahl', 'Pistes d''exploitation du roman de Roald Dahl  Charlie et la chocolaterie', 'Auteur de Scalpa', ' littérature roman Roald Dahl Charlie et la chocolaterie ', 'http://www.scalpa.info/charlie.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (175, 1, '2006-07-21 17:00:45', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 1 ', 'Exploiter un roman au cycle 3 -Le mystère des mots-croisés de Senecal Didier / C', 'Pistes d''exploitation de cette oeuvre de Senecal Didier et Chavanat Didier\r\n\r\nRESUME\r\n\r\nLouis Mortier mène une vie oisive, sans se soucier de ses innombrables kilos en trop. Il est riche, si riche qu''il peut se permettre de ne pas travailler et passer ses journées à assouvir sa passion pour les mots croisés. Mais un beau matin il reçoit une lettre postée de Bangkok, en Thaïlande. Louis Mortier pâlit en découvrant son contenu : une grille de mots croisés qui fait allusion à des événements de sa vie qu''il croyait oubliés... Quel est ce serment qu''il a trahi ? Cette statue qu''il a volée ? Le passe-temps de Louis Mortier devient une machine à remonter le temps vers un passé où se croisent cupidité, mystère, trahison et vengeance...', 'Auteur de Scalpa', ' Senecal Didier Chavanat Didier roman littérature ', 'http://www.scalpa.info/senecal.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (176, 1, '2006-07-21 17:04:42', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 1 ', 'Exploiter un roman au cycle 3-Le fermier Gilles de Ham de TOLKIEN / Sabatier / L', 'Pistes et exploitations du roman au cycle 3', 'Site de Scalpa', ' TOLKIEN  Sabatier Ledoux romans littérature ', 'http://www.scalpa.info/tolkien.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (177, 1, '2006-07-21 17:08:34', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 1 ', 'Exploiter une nouvelle d''alphonse Daudet au cycle 3', 'Exploitation d''une nouvelle d''Alphonse Daudet, au cycle3.', 'Auteur de Scalpa', ' Nouvelles lecture Alphonse Daudet sous-préfet littérature ', 'http://www.scalpa.info/daudet.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (178, 1, '2006-07-21 17:11:43', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 1 ', 'Exploiter le conte du Petit Poucet au cycle 3', 'Pistes d''exploitation pour aborder le conte du Petit Poucet au cycle 3.', 'Auteur du site de Scalpa', ' Contes Petit-Poucet littérature lecture Perrault ', 'http://www.scalpa.info/poucet.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (179, 1, '2006-07-21 17:15:20', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' ', 'Exploiter une BD  au cycle 3- Calvin et Hobbes', 'Pistes d''exploitation d''une BD ... Cavin et Hobbes\r\n\r\nL''enfance est pleine de désillusions et pleine d''inventions aussi. Heureusement Calvin, petit garçon de 6 ans, un peu turbulent, déborde d''imagination. Aidé de son fidèle tigre en peluche et compagnon Hobbes, il sait rendre la vie piquante et drôle. Tout le monde y passe : les parents, les filles, la maîtresse d''école. Mais la principale activité de ce tendre Calvin est d''animer le club le D.E.F.I. (Dehors énormes filles informes), dont Calvin et Hobbes sont seuls membres actifs, et ils se déchaînent sur la pauvre Susie Derkins.', 'Auteur du site de Scalpa', ' littérature BD bandes dessinées Calvin Hobbes ', 'http://www.scalpa.info/calvin.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (180, 1, '2006-07-21 17:17:49', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 1 ', 'Oeuvre de Raymond Queneau au cycle 3', 'Exploitation de l''ouvrage de Raymond Queneau', 'Auteur de Scalpa', ' littérature Raymond Queneau poésies ', 'http://www.scalpa.info/queneau.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (181, 1, '2006-07-21 17:27:51', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 1 ', 'Poésies d''André Breton au cycle 3', 'Des pistes d''exploitation comme le faisait Breton. \r\nApproche du surréalisme. \r\nUne présentation (succincte mais prolongée en arts-plastiques) du mouvement surréaliste et après un travail réalisé sur Queneau exploitation de à la création poétique à partir de collage d''extraits de journaux comme le faisait Breton', 'Auteur de Scalpa', ' Breton poésies littérature surréalisme ', 'http://www.scalpa.info/breton.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (182, 1, '2006-07-21 17:35:50', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 1 ', 'If de Rudyard Kipling / Guillaume Reynard', 'Exploitation de &quot;If&quot; de  Rudyard Kipling.\r\nPropostion de piste d''exploitation pour interpréter les strophes de ce texte célèbre.', 'Site de Scalpa', ' littérature poésie  Rudyard Kipling ', 'http://www.scalpa.info/kipling.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (183, 1, '2006-07-21 17:38:14', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 3 ', ' 13 ', ' 1 ', 'Liste des compétences pour la numération à l''école élémentaire', 'Liste des compétences attendues lors de la scolarité d''un élève d''après les programmes 2002.', '', ' Compétences numération liste programmes 2002 ', 'http://www.scalpa.info/numeration.php', 'fr.png', 'N', 0, 'O');
