INSERT INTO `resource_online` VALUES (185, 1, '2006-07-21 17:49:34', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 13 ', ' 4 ', 'Calcul réfléchi', 'Calcul réfléchi : les poignées de main\r\n\r\nCalcul réfléchi : les trois nombres qui se suivent\r\n\r\nCalcul réfléchi : trouver la valeur de A et la valeur de B sachant la somme A + B et la différence A - B.\r\n\r\nCalcul réfléchi : calculer des additions en utilisant les complémentaires.', 'Auteur de Scalpa', ' calcul réfléchi exercices ', 'http://www.scalpa.info/calcul.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (186, 1, '2006-07-21 17:57:18', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 3 ', ' 13 ', ' 1 ', 'Liste des compétences pour la géométrie à l''école élémentaire', 'Liste des compétences attendues pour la géométrie - Programmes 2002', 'Ministère EN', ' géométrie compétences programmes 2002 ', 'http://www.scalpa.info/geometrie.php', 'fr.png', 'N', 0, 'O');
