INSERT INTO `resource_online` VALUES (188, 1, '2006-07-21 18:03:17', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 3 ', ' 13 ', ' 1 ', 'Liste des compétences pour les problèmes  à l''école élémentaire', 'RESOLUTION DE PROBLEMES\r\n\r\nEXPLOITATION DE DONNÉES NUMÉRIQUES\r\nProblèmes relevant des quatre opérations\r\nProportionnalité\r\nOrganisation et représentation de données numériques', 'Ministère EN', ' Problèmes proportionnalité données numériques ', 'http://www.scalpa.info/probleme.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (189, 1, '2006-07-21 18:16:00', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 13 ', ' 3 ', 'Les problèmes de recherche pour les élèves du cycle 3', 'Animation pédagogique de Sandrine Guilbert, Conseillère pédagogique circo. de Mortain', 'Sandrine Guilbert', ' problèmes de recherche ', 'http://www.scalpa.info/anipeda_probleme_2006.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (190, 1, '2006-07-21 18:18:44', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel:  carré magique', 'il s''agit de s''entraîner au calcul réfléchi en complétant ces carrés maléfiques. 10 EUROS\r\nDifficultés graduées selon les niveaux du CP au CM2', '', ' calcul réfléchi carré magique ', 'http://www.scalpa.info/carre.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (191, 1, '2006-07-21 18:21:25', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel : Calculer en chiffres romains', 'Des nombres aléatoirement tirés par l''ordinateur, dans la fourchette choisie par l''enfant, lui sont proposés afin qu''il les retranscrive dans une des deux écritures.\r\nL''élève doit transposer des nombres en numération classique en chiffres romains.\r\nGraduations des exercices\r\n5 Euros', '', ' numération chiffres romains ', 'http://www.scalpa.info/romain.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (192, 1, '2006-07-21 18:25:46', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel : Décomposer et réduire des nombres', 'Il faut soit écrire la décomposition d''un nombre (entier ou décimal) en respectant l''ordre décroissant de valeur de chaque chiffre liée à sa position dans le nombre : exemple pour 245, il faudra écrire (2x100)+(4x10)+(5x1) bien que tout autre décomposition soit valable 200+40+5, 200+45, (4x10)+(2x100)+(5x1) etc.. Mais dans un premier temps respectons l''ordre ... cela facilite la mise en place du mécanisme !\r\nPrix : 5 Euros', '', 'numération  décomposition d''un nombre  décimal  entier', 'http://www.scalpa.info/decomposer.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (193, 1, '2006-07-21 18:38:47', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel : Ecrire des nombres entendus', 'Un nombre est dit par Merlin, les enfants doivent l''écrire en chiffres. Au bout de dix bonnes réponses, un jeu de labyrinthe leur est proposé.Maintenant Merlin parle aussi anglais...', '', ' numération orthographe dictée de nombres dictée en anglais ', 'http://www.scalpa.info/entendus.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (194, 1, '2006-07-21 18:42:56', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel : calcul mental', 'Logiciel d''entraînement et de test des compétences en calcul mental à utiliser après une période d''apprentissage en classe dans laquelle on aura mis en évidence les techniques les plus efficaces pour chacun en fonction du type d''opérations à réaliser. Il s''agit avec ce logiciel de travailler toutes les techniques opératoires sous forme de calcul mental. L''utilisateur peut effectuer tous les réglages permettant de varier la difficulté du calcul :       \r\n\r\n            taille du nombre à droite du signe opératoire\r\n         \r\n\r\n            taille du nombre à gauche du signe opératoire\r\n          \r\n\r\n            signe opératoire\r\n\r\nA la fin du jeu, on revient à la fenêtre des résultats sur laquelle on découvre son score, le temps mis pour réaliser les quarante opérations.\r\n\r\nIndication des scores', '', ' Calcul mental ', 'http://www.scalpa.info/mental.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (195, 1, '2006-07-21 18:46:19', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel : Comparer des nombres', 'Comparer des nombres entiers ou décimaux : Il faut trouver un nombre compris entre deux autres.\r\nIndication des scores. Prix 3 Euros', '', ' calcul  mathématiques numération  comparer  nombres entiers nombres décimaux ', 'http://www.scalpa.info/comparer.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (196, 1, '2006-07-21 18:51:11', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciels : des nombres en lettres', 'Ecrire un nombre en lettres : l''ordinateur choisit aléatoirement un nombre et le présente écrit en chiffres. L''enfant doit l''écrire en lettres. Indication des scores - 3 Euros', '', ' dictée de nombres numération logiciel  mathématiques ', 'http://www.scalpa.info/lettres.php', 'fr.png', 'N', 0, 'O');
