INSERT INTO `resource_online` VALUES (202, 1, '2006-07-21 21:08:28', '0000-00-00 00:00:00', 0, 0, ' ', ' 7 ', ' ', ' 3 ', 'SPINOO Moteur de recherche', 'SPINOO est un moteur de recherche dédié aux sites éducatifs institutionnels français.\r\nMis au point par le CNDP, il vous permet de trouver toutes les ressources pédagogiques, documentaires et administratives présentes dans l''ensemble de ces sites.', '', ' Moteur de recherche sites éducatifs institutionnels ', 'http://www.spinoo.fr/default.asp', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (203, 1, '2006-07-22 10:43:46', '0000-00-00 00:00:00', 0, 0, ' ', ' 1 ', ' ', ' 3 ', 'Site national &quot;Bien Lire à l''école&quot;', 'Des outils, des fiches pratiques, des documents théoriques, des adresses utiles pour mieux répondre aux difficultés des enfants en lecture. Un accès aux Actualités sur le thème de la lecture. \r\nDes interviews et des manifestations avec une veille sur le domaine de la lecture.', 'CNDP et Ministère de l''Education Nationale', ' lecture fiches théorie ', 'http://www.bienlire.education.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (204, 1, '2006-07-22 10:55:07', '0000-00-00 00:00:00', 0, 0, ' ', ' 7 ', ' ', ' 3 4 ', 'Devoirs à la maison : 50 ans de travail au noir', 'Un texte tiré du BD de l''IA59 du Nord. Dans ce dossier sont développés les points suivants.\r\n\r\n    * la législation sur les devoirs à la maison\r\n    * 7 types d’arguments\r\n    * Quel travail, le soir à la maison ?\r\n    * La démarche d’apprentissage des leçons\r\n    * L''exemple d''une leçon d''histoire', '', ' devoirs apprendre une leçon ', 'http://netia59a.ac-lille.fr/~siteia/bulletin_departemental/pdf/95_dossier.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (205, 1, '2006-07-22 11:30:41', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 8 10 ', ' 2 3 11 22 ', ' 1 ', 'Site national et institutionnel Primlangues', 'Site national institutionnel pour les langues du CIEP et du Ministère.\r\nOutils didactiques pour l''enseignement des langues.De nombreuses ressources pour toutes les langues', 'CIEP Ministère de l''Education Nationale', ' langues vivantes italien allemand anglais arabe turc ', 'http://www.primlangues.education.fr/php/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (206, 1, '2006-07-22 12:40:24', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 11 ', ' 2 3 11 22 ', ' 1 ', 'A l''ombre d''un olivier ... un album + un CD avec des chansons arabes et berbères', '*  Favret H., Lerasle M., Novi N.\r\n    * Coll. « Comptines du monde »\r\n    * Didier jeunesse\r\n    * 2001\r\n\r\n29 comptines, berceuses, rondes, chansons arabes et berbères.\r\n\r\nPour une approche culturelle \r\nPlouf tizen tizen - Sardine à l’huile - Minga touri - Param param - Ouahad jouj tlata - Hada li sraq ljaja - Dab alfar dbibatou - Un gros lézard est passé par là – Maymouna - Tita tita tita - Bisiklat klat klat - Atas atas amimmi - Ykhallik ! y’alli – Boughanja - Achta ta ta ta ta - Ya mtar khalti - Aoulad alharrata - Lalla fatima – Boudjaghlallou - Sannat lahmar -', 'Favret H., Lerasle M., Novi N.', ' livre CD chansons comptines arabe berbère ', 'http://www.primlangues.education.fr/php/suggestion_polyglotte.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (207, 1, '2006-07-22 12:44:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 2 3 11 22 ', ' 1 ', 'A puffin year of stories and poems', 'A puffin year of stories and poems  est un  album avec des poésies en anglais sur le thème des saisons. Poèmes classés en fonction des 4 saisons.', 'Editions Puffin Books', ' saisons langues vivantes LV anglais ', 'http://www.primlangues.education.fr/php/suggestion_polyglotte.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (208, 1, '2006-07-22 12:45:55', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 2 3 11 22 ', ' 1 ', 'Alice prépare une surprise / Alice bereitet eine Überraschung vor', 'Album  bilingue : français – allemand\r\nPrésentation. Alice prépare un gâteau pour sa maman. Elle rassemble les ingrédients et se dépêche de tout mélanger. Mais tout ne se passe pas comme prévu. La surprise sera-t-elle vraiment réussie ?', 'rede K., Furlano C Coll. « P’tit Bil Editions Zoom', ' Langue vivante allemand conte album livre bilingue ', 'http://www.primlangues.education.fr/php/suggestion_polyglotte.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (209, 1, '2006-07-22 13:02:48', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 4 ', 'Le travail des élèves à l''école et en dehors de l''école', 'Rapport établi à la demande du Haut Conseil de l’évaluation de l’école. Ce rapport a été réalisé par Dominique Glasman, professeur de sociologie à l''Université de Savoie, chercheur au Laboratoire Langages.\r\nUn chapitre est consacré à chacun des thèmes suivants :\r\n- les devoirs à la maison \r\n- les jeux éducatifs \r\n- les cours particuliers \r\n- le coaching scolaire \r\n- l''accompagnement scolaire\r\n- les devoirs de vacances \r\n- une bibliographie générale.', 'Dominique Glasman', ' coaching:accompagnement scolarité devoirs jeux éducatifs cours particuliers ', 'http://www.bienlire.education.fr/04-media/a-glasman.asp', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (210, 1, '2006-07-22 13:08:02', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 3 4 ', 'Fiches accompagnement à la scolarité', 'Ensemble de fiches permettant d''apporter des conseils sur l''accompagnement à la scolarité\r\nLes fiches familiales, rédigées par une équipe interministérielle et partenariale, avec l’appui d’experts, cherchent à soutenir les familles dans leur effort pour créer des conditions favorables à la scolarité de leurs enfants. Elles proposent des pistes pour l’action et visent à faciliter les échanges, ainsi que la recherche d’informations.', 'Equipe interministérielle', ' accompagnement scolarité  fiches relation famille-Ecole ', 'http://www.bienlire.education.fr/02-atelier/fiche.asp?theme=2200&amp;id=1410', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (211, 1, '2006-07-22 15:16:04', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 4 ', 'L’Entraide Scolaire Amicale pour combattre les inégalités sociales face à la réu', 'La nécessité d’une action extra-scolaire\r\nUne action citoyenne\r\nComment est organisée l’Entraide Scolaire Amicale ?\r\nQue peuvent apporter les bénévoles de l’Entraide Scolaire Amicale ?\r\nD’autres associations', 'Laurence Jung (professeur)', ' réussite scolaire accompagnement scolaire ', 'http://www.bienlire.education.fr/02-atelier/fiche.asp?theme=2200&amp;id=1384', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (212, 1, '2006-07-22 15:22:18', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 3 4 ', 'Les clubs coups de pouce', 'Présentation de l’APFÉE (Association pour favoriser une école efficace) a été créée en 1984 à l''initiative d''une équipe d''enseignants soucieux d''agir contre l''échec scolaire.', 'Georges Vissac (directeur de l’APFEE), Jean-Pierre Minassian (vice-président de Savoir Lire)', ' APFEE accompagnement scolarité ', 'http://www.bienlire.education.fr/02-atelier/fiche.asp?theme=2200&amp;id=1145', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (213, 1, '2006-07-22 15:27:11', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 3 4 ', 'Association &quot;lire et faire lire&quot;', 'Lire et faire lire est un programme d''ouverture à la lecture et de solidarité intergénérationnelle. C’est aussi le nom de l’association loi 1901 créée en décembre 1999 qui organise ce programme (http://www.lireetfairelire.org/).', 'Lire et faire lire', ' solidarité intergénérationnelle lire et faire lire  accompagnement scolaire  scolarité ', 'http://www.bienlire.education.fr/02-atelier/fiche.asp?theme=2200&amp;id=1138', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (214, 1, '2006-07-22 15:36:01', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 3 ', 'Des médiations au cycle 2 autour des ouvrages documentaires', 'Textes théoriques  puis  quatre scenarii pédagogiques en première partie\r\n-Un éléphant dans le réseau\r\n-Les états de l’eau pour lire l’image\r\n-Ça pousse dans les albums documentaires\r\n-Un parcours très chouette\r\nDeuxième partie : des outils pour les maîtres\r\nComparer et choisir des ouvrages documentaires\r\nUne aide à la programmation\r\nUn rappel des textes officiels\r\nBibliographie sélective', 'Lucie Dessailly (IEN)', ' lecture documents médiations lecture documentaire ', 'http://www.bienlire.education.fr/02-atelier/fiche.asp?theme=1200&amp;id=1402', 'fr.png', 'N', 0, 'O');
