INSERT INTO `resource_online` VALUES (224, 1, '2006-07-22 17:42:56', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' ', 'L''album Jeunesse support parallèle  privilégier', 'Le travail en parallèle sur les deux supports (manuel de lecture et albums de jeunesse) permet d’appréhender différentes facettes de la lecture. Exemples de pistes d''exploitation', 'Arlette Weber (maître formateur)', '  ', 'http://www.bienlire.education.fr/02-atelier/fiche.asp?theme=1120&amp;id=1110', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (225, 1, '2006-07-22 18:01:29', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 3 ', ' 8 ', ' 3 ', 'Parcours de formation en lecture au CP', 'Série de modules de formation en ligne du TFL\r\nCinq  parcours constituent le socle des indispensables pour le CP\r\n\r\n1- Connaissance du code\r\n\r\n2- Entrer dans les textes, la compréhension\r\n\r\n3- Méthodes d''apprentissage et manuels\r\n\r\n4- Découverte de la culture écrite\r\n\r\n5- L’évaluation de la lecture', 'TFL', ' lecture CP méthodes évaluation connaissance code ', 'http://www.uvp5.univ-paris5.fr/TFL/TFL.asp', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (226, 1, '2006-07-22 18:05:45', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 3 ', ' 8 ', ' 3 ', 'Apprendre à lire en 10 pages et quarante notions', 'Points évoqués\r\n        Maîtriser la langue orale\r\n	Apprendre ce que c''est que lire avant de savoir lire\r\n	Apprendre à lire et savoir lire\r\n	Apprendre à identifier les mots\r\n	En chemin vers la compréhension du texte lu : l''importance de la syntaxe\r\n	Apprendre à comprendre\r\n	Les difficultés d''apprentissage\r\n	Pluridisciplinarité et polyvalence\r\n	Évaluation des performances', 'Alain Bentolila', ' lecture CP méthodes évaluation connaissance code ', 'http://www.uvp5.univ-paris5.fr/TFL/TFL.asp', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (227, 1, '2006-07-22 18:09:41', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' ', ' 8 ', ' 3 ', 'Notions théoriques', '1-	Culture de l''écrit et composants de l''apprentissage\r\n2-	Structure de la langue\r\n3-	Oral et écrit\r\n4-	Identification des mots\r\n5-	Compréhension en lecture\r\n6-	Lecture experte\r\n7-	Pédagogie\r\n8-	Didactique\r\n9-	Français langue seconde\r\n10-	Difficultés et handicap', 'Bentolila', ' écrit oral mots compréhension lecture experte FLS lecture ', 'http://www.uvp5.univ-paris5.fr/TFL/TFL.asp', 'fr.png', 'N', 0, 'O');
