INSERT INTO `resource_online` VALUES (231, 1, '2006-07-22 18:32:57', '0000-00-00 00:00:00', 0, 0, ' ', ' 3 ', ' ', ' 1 ', 'Banque d''outils pour l''évaluation', 'Banque d´outils d´aide à l´évaluation diagnostique \r\nNom d''utilisateur: outils / Mot de passe: dpd\r\nAccès à de nouveaux ouils avec les entrées suivantes\r\n \r\n    * La discipline concernée,\r\n    * la capacité ou famille de compétences visée,\r\n    * le niveau pour lequel il a été conçu,\r\n    * le titre,\r\n    * le résumé qui apporte un complément d''information (activité demandée aux élèves, caractéristiques particulières du support d’évaluation utilisé…).', 'banqoutils.education.gouv.fr', ' outils évaluation diagnostique ', 'http://www.banqoutils.education.gouv.fr/', 'fr.png', 'N', 0, 'O');
