INSERT INTO `resource_online` VALUES (237, 1, '2006-07-22 21:28:54', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 3 ', ' 8 ', ' 1 ', 'Les outils d''aides à l''évaluation pour la lecture au Cycle 2', 'Le livret Lire au CP - Repérer les difficultés pour mieux agir , a été  diffusé en 2003 et  fait référence à de nombreuses fiches d''évalution de grande section (GS) et de cours préparatoire (CP) qui se trouvent sur le site banqoutils,  www.banqoutils.education.gouv.fr.\r\nVous trouvez là toutes les fiches d''évaluation citées dans le livret.', 'DESCO Editeur CNDP', ' Evaluation cycle2 lecture CP ', 'http://eduscol.education.fr/D0135/liens-vers-banqoutils.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (238, 1, '2006-07-22 21:46:58', '0000-00-00 00:00:00', 0, 0, ' ', ' 7 ', ' ', ' 3 4 ', 'Prévenir l''illettrisme', 'Documents DESCO\r\nLa collection &quot;Prévenir l''illettrisme&quot; propose des outils d''information et guides méthodologiques brefs sur des thèmes en relation avec les difficultés scolaires en lecture et en écriture.\r\n\r\n    * Prévenir l''illettrisme numéro 1 - répondre à des besoins éducatifs particuliers\r\n    * Prévenir l''illettrisme numéro 2 - apprendre à lire avec un trouble du langage', 'DESCO Editeur CNDP', 'illettrisme prévevir l''illettrisme trouble du langage lecture écriture difficultés', 'http://eduscol.education.fr/D0135/notes-ill.htm', 'fr.png', 'N', 0, 'O');
