INSERT INTO `resource_online` VALUES (326, 1, '2006-07-23 15:58:24', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Rallye scientifique sur le thème de la reprodution', 'Un rallye pour mettre en évidence le principe de la reproduction par métamorphose.\r\nLa grenouille-Le papillon-La mouche\r\nLes élèves ont leur disposition des pages Web très simples avec du texte et des photographies. Ils doivent répondre à un questionnaire en ligne à partir des indices trouvés dans le texte.', 'académie de Créteil', ' reproduction sciences du vivant lecture documentaire métamorphose mouche papillon grenouille ', 'http://www.ac-creteil.fr/lecturecollinet/m%E9tamorphose/accueil.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (327, 1, '2006-07-23 16:03:00', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Rallye scientifique sur le thème de la respiration', 'Un rallye pour mettre en évidence le principe de la rrespiration.Les élèves ont leur disposition des pages Web très simples avec du texte et des photographies. Ils doivent répondre à un questionnaire en ligne à partir des indices trouvés dans le texte.', 'académie de Créteil', ' respiration rallye scientifique lecture documentaire B2I ', 'http://www.ac-creteil.fr/lecturecollinet/respiration/accueil.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (328, 1, '2006-07-23 16:07:43', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Rallye scientifique sur le thème de l''alimentation des poissons', 'La forte compétition qui existe entre les poissons les a obligés à utiliser toutes les possibilités pour obtenir espace et nourriture. Ils occupent donc des milieux variés et consomment une grande variété de nourriture.\r\nOn peut les classer de la manière suivante :1. les végétariens  2. Les mangeurs d''animaux peu mobiles 3. Les carnassiers\r\nMais beaucoup de poissons mangent un peu de tout.', 'académie de Créteil', ' Alimentation chaîne alimentaire régime alimentaire lecture documentaire B2I ', 'http://www.ac-creteil.fr/lecturecollinet/poisson/accueil.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (329, 1, '2006-07-23 16:11:35', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Rallye scientifique sur le thème de la reprodution des poissons', 'Un rallye pour mettre en évidence le principe de la reproduction des poissons.', 'académie de Créteil', ' reproduction sciences du vivant lecture documentaire poissons ', 'http://www.ac-creteil.fr/lecturecollinet/reproduction/accueil.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (330, 1, '2006-07-26 21:00:21', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' ', 'Comptines et chants trandionnels  en ligne', 'A la volette \r\nAh vous dirais-je maman\r\nAlouette \r\nAu clair de la lune \r\nC''est la mère Michel \r\nCadet Rousselle \r\nFais dodo Colas \r\nFrère Jacques \r\nGentil coquelicot \r\nIl court le furet \r\nIl était un petit navire \r\nl était une bergère \r\nIl pleut bergère\r\nJ''ai du bon tabac \r\nJ''ai perdu le do\r\nLe bon roi Dagobert \r\nMalbrough s''en va-t''en guerre \r\nMon âne \r\nNous n''irons plus au bois \r\nSavez-vous planter les choux (\r\nSur le pont d''Avignon \r\nTrois jeunes tambours', '', ' Chants comptines ', 'http://www.chez.com/kolymbia/index2.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (331, 1, '2006-07-26 21:04:32', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 11 ', ' 20 ', ' 1 ', 'Chants et comptines traditionnels', 'A Paris \r\nAgadou dou dou \r\nAh le petit vin blanc \r\nAllez viens boire \r\nAuprès de ma blonde \r\nCe n''est qu''un aurevoir \r\nDouce nuit, Sainte nuit\r\nDu rhum, des femmes \r\nEn passant par la Lorraine \r\nFélicie aussi \r\nFrench Cancan \r\nFrou-Frou \r\nJoyeux anniversaire  	  	  	La chenille \r\nLa danse des canards \r\nLa java bleue \r\nLa java des gaulois \r\nLa plus bath des javas \r\nMarche nuptiale \r\nMinuit chrétien \r\nMon beau sapin \r\nNe pleure pas Jeannette \r\nOn a soif \r\nPetit Papa Noël \r\nSous les ponts de Paris \r\nVive le vent (Jingle Bells)', 'PAROLES - les Textes de la Chanson Française', ' chansons comptines ', 'http://www.chez.com/kolymbia/index2.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (332, 1, '2006-07-26 22:30:51', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 10 ', ' 1 ', 'Utiliser Abiword à l''école', 'Abiword est un traitement de texte rapide et complet, avec une interface simple, des possibilités de mise en forme de pages évoluées. Facile à utiliser même dans les petites classes. Abiword a les fonctionnalités principales comme Word, mais gratuit et beaucoup moins gourmand en place (- de 10 Mo avec les pilotes divers).Facile pour un passage en douceur vers des logiciels standards.De plus il gère les formats .txt .doc .html .rtf', 'L’équipe de développement d’AbiWord; fiche descriptive Mdrechsler', ' Traitement de textes B2I Abiword logiciel libre tutoriel ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=115', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (333, 1, '2006-07-26 22:42:50', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 21 ', ' 4 ', 'Simple Paint', 'Simple Paint 1.02 est un logiciel gratuit (gratuiciel ou freeware) conçu pour les enfants dès l''école maternelle.  Il fonctionne avec toutes les versions de Windows et offre tous les outils de base pour dessiner sur son ordinateur. Il comprend un palette de 40 couleurs, un pinceau de diverses épaisseurs, une gomme, un pot de peinture et quinze motifs (cliparts) qu''il est possible d''intégrer dans son dessin', 'Johannes Wallroth', ' arts logiciel freeware ', 'http://www.jedessine.com/TELECHARGER/simplepaint.zip', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (334, 1, '2006-07-26 23:07:55', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 19 ', ' 4 ', 'Utiliser Tuxpaint à l''école', 'Tux Paint peut être utilisé pour apprendre aux jeunes enfants à dessiner avec un ordinateur. Une aide contextuelle est apportée par un petit Tux (pingouin mascotte) qui distille (à l’écrit, en bas de la zone de dessin) des explications sur les différentes fonctions, ainsi que des encouragements. La zone de dessin est fixe. On peut faire du dessin libre en variant l’épaisseur du trait, les couleurs.On peut sélectionner différentes formes géométriques prédéfinies, inclure du texte, On peut aussi utiliser des effets spéciaux : mosaïque, miroir (afin de pouvoir introduire la symétrie en mathématiques), négatif, arc-en-ciel, étincelles, ...', 'Bill Kendrick', ' arts visuels logiciel tampons formes géométriques B2I logiciel libre tutoriel ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=44', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (335, 1, '2006-07-26 23:17:25', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 13 ', ' 4 ', 'Le logiciel Symcolor pour travailler la symétrie au cycle 1 et au cycle 2', 'Symcolor est un logiciel destiné à la construction du principe de symétrie axiale chez l’enfant à partir de la grande section en maternelle.\r\nLe logiciel est composé d’un éditeur qui permet la création d’images à partir de formes géométriques, qui seront utilisées pour les activités de symétrie.\r\n\r\nOn lance ensuite les activités de symétrie. Il s’agit alors de choisir une couleur et de peindre chaque élément de l’image conformément au modèle. Le logiciel comprend une série d’images mais il est préférable que l’enfant produise lui-même ses dessins, réalise les symétries à partir de ses dessins pour se situer dans une démarche d’expérimentation (aller-retour entre la phase de dessin et de coloriage par symétrie).', 'AbulEdu et son équipe', ' symétrie mathématiques logiciel logiciels libres ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=505', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (336, 1, '2006-07-26 23:27:51', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 16 ', ' 4 ', 'La Balance Virtuelle : Pratique de la pesée, Cycles II et III', 'Logiciel éducatif gratuit de simulation de balance  pour la pratique de la pesée, \r\npour  Windows 9x, ME, 2000, XP.', 'Pujol Y', ' balance  simulation logiciel ', 'http://pujol.club.fr/freeware/balance.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (337, 1, '2006-07-26 23:31:14', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel pour la reproduction d''un quadrillage', 'Logiciel éducatif gratuit pour pratiquer les  reproductions sur quadrillages, \r\npour  Windows 9x, ME, 2000, XP.', 'Y Pujol', ' logiciel freeware quadrillage reprodution géométrie ', 'http://pujol.club.fr/freeware/quadri.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (338, 1, '2006-07-26 23:33:29', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel du jeu de mémory', 'Jeu de Mémo textuel ouvert et gratuit, pour les jeux de lecture,  le travail de la mémoire. \r\nLudique et efficace, ce jeu est très apprécié des enfants de Cycle 2.', 'Y Pujol', ' mémory lecture mémoire ', 'http://pujol.club.fr/freeware/memoryx.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (339, 1, '2006-07-26 23:36:13', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel pour pratiquer les  symétries axiales sur quadrillages', 'Logiciel pour pratiquer les  symétries axiales sur quadrillages pour  Windows 9x, ME, 2000, XP.', 'Y Pujol', ' logiciel symétrie axiale quadrillage freeware géométrie ', 'http://pujol.club.fr/freeware/symax.html', 'fr.png', 'N', 0, 'O');
