INSERT INTO `resource_online` VALUES (342, 1, '2006-07-26 23:53:52', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel de lecture et compréhension avec des textes pour les CP-CE1', 'Logiciel de lecture et compréhension avec des textes pour les CP-CE1', 'Yvan Pujol', ' lecture compréhension freeware ', 'http://pujol.club.fr/freeware/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (343, 1, '2006-07-26 23:56:45', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel pour compter avec les doigts', 'L''activité proposée permet à l''élève de mémoriser les &quot;constellations&quot; avec les doigts.\r\nLa mise en situation permet à l''élève de se représenter la collection à travers les doigts de la main.', 'Yvan Pujol', ' numération constellation doigts logiciel en ligne ', 'http://pujol.club.fr/exercices/main-num/entree.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (344, 1, '2006-07-27 00:00:16', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Générateur et gestionnaire en ligne d''exercices de lecture à questionnaire', 'Nouveau: \r\nTextes &amp; Questions 5.0: Générateur et gestionnaire en ligne d''exercices de lecture à questionnaire', 'Yvan Pujol', ' exerciseur exercices questionnaire lecture ', 'http://pujol.club.fr/historique.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (345, 1, '2006-07-27 00:02:37', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel du jeu de la tour', 'Le jeu &quot;Les Tours&quot; permet de travailler en utilisant les déplacements codés, il est conçu pour les élèves de cycle II. \r\nLes joueurs  peuvent choisir la couleur de la tour pilotée dans l''espace &quot;Choisir la couleur&quot;. \r\nDeux modes de commandes sont utilisables pour déplacerles tours: \r\n-Commande directe en cliquant sur les flèches \r\n-Ligne de commande et tapant  une chaîne de caractères composée des lettres DGHB (pour Droite Gauche Haut Bas) dans la zone de saisie, suivie d''un click sur le bouton: &quot;Exécuter&quot; ; majuscules et minuscules sont acceptées.', 'Yvan Pujol', ' calcul ', 'http://pujol.club.fr/exercices/tours/toursprog.htm#', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (346, 1, '2006-07-27 00:07:02', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel de mémory', 'Ce mémory, dérivé du célèbre jeu de mémo est un jeu de lecture et de mémoire. \r\nDans le mémo classique, il faut trouver 2 fois le même dessin. \r\nDans ce jeu, il faut retrouver les textes formant des paires. \r\nLes paires sont constituées par des associations logiques diverses, chacune constituant un thème de jeu.', 'Yvan Pujol', ' logiciel mémory calcul logiciel en ligne ', 'http://pujol.club.fr/exercices/memo/memojava.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (347, 1, '2006-07-27 00:17:39', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' ', 'Logiciel Baby Majhong', 'Baby Majhong \r\nLe but de ce jeu est de constituer des paires :\r\n- Images / Lettre (initiale du mot\r\n- Lettre majuscule / Lettre minuscule\r\n- Image / Image (mots ayant la même initiale)\r\n\r\nLogiciel gratuit', 'Laurent CHRETIEN', '  ', '', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (348, 1, '2006-07-27 00:19:29', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel de l''atelier', 'Aperçu de l''heure\r\nle compte est bon \r\nAddition/Soustraction \r\nle pendu  \r\nLogiciel éducatif pour enfant de 4-8 ans.Il comprend une section addition &amp; soustraction, une section pendu, une section compte et bon, une section apprentissage de l''heure.', 'Chrétien', ' lecture heure addition soustraction pendu compte est bon freeware ', 'http://freeperso.free.fr/freeware.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (349, 1, '2006-07-27 14:07:16', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' ', ' 7 ', ' 1 ', 'Des albums en maternelle', 'Site dédié à la littérature Jeunesse. Exploitation d''albums à la l''école  maternelle.\r\n des activités imprimables pour les PS, MS, GS, en lecture, maths, graphisme, perception,  des comptines, des chansons, des poésies,  des idées en motricité, arts plastiques, activités manuelles,  des jeux en ligne : puzzles modulables de 2 à 60 pièces, tableaux à double entrée, associations, mémory, des liens vers des pages en rapport avec le thème de l''album\r\nune bibliographie ou des albums à mettre en réseau', '', ' albums maternelle puzzles tableaux à double entrée associations mémory graphisme comptines chansons ', 'http://materalbum.free.fr/', 'fr.png', 'N', 0, 'O');
