INSERT INTO `resource_online` VALUES (369, 1, '2006-07-28 11:13:46', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 3 ', 'Réseau école et nature', 'Le Réseau Ecole et Nature est un réseau français mettant en relation un grand nombre d''acteurs de l''éducation à l''environnement : des éducateurs (enseignants, animateurs...), des agents de l''Etat et des collectivités publiques, des acteurs du secteur privé, des porteurs de projets, des responsables de structures d''éducation, etc... afin de mieux travailler ensemble dans le sens d''un développement durable', 'réseau école et nature', 'nature écologie éducation à l''environnement développement durable formations', 'http://www.ecole-et-nature.org/papyrus.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (370, 1, '2006-07-28 11:17:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 16 ', ' 4 ', 'Krissnature, un site participatif pour faire aimer la nature (faune et flore)', 'Kriss Nature est un site &quot;participatif&quot;... Vous aimez la nature (faune et flore) , les problèmes d''environnement, de pollution ne vous laissent pas indifférent ? Inscrivez-vous, mettez Kriss Nature dans vos favoris et participez à l''aventure de Kriss Nature en proposant vos propres textes et photos...Glossaire avec des termes propres à la botanique.\r\nLes Mantes -Le guide entomologique -Les blattes -Vidéothèque-Effet de serre et Energies renouvelables -La Terre en Partage -Le Paon du jour - Les Tulipes - Encyclopédie des cactus -Les Dahlias -Les Amphibiens  -Au pays des orchidées - LE LOUP : haï et aimé  -Les chenilles -Les araignées- Le traité des Orchidées- Les coccinelles   kriss 2939        \r\nGuide des chants d''oiseaux d Europe Occidentale- La Nature méditerranéenne en France- La garrigue grandeur nature', 'Krissnature', ' nature animaux végétaux faune flore biologie environnement ', 'http://krissnature.com/nature/', 'fr.png', 'N', 0, 'O');
