INSERT INTO `resource_online` VALUES (390, 1, '2006-07-28 13:51:09', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 3 4 ', 'Site pour les classes en CLIN', 'Site pour les classes en  CLIN. La clin est une classe qui accueille des élèves migrants non francophones primo-arrivants, avec pour objectif principal leur intégration rapide dans une classe francophone ordinaire.Roland Kara  présente ici &quot;une partie de son  travail afin de susciter un échange d''idées et d''outils avec les collègues de clin et d''apporter un soutien aux collègues débutants qui se sentiraient quelque peu perdus face à l''ampleur de la tâche qui leur incombe&quot;.', 'Roland Kara', ' CLIN FLE français langue étrangère oral compréhension ', 'http://roland.kara.chez-alice.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (391, 1, '2006-07-28 13:58:18', '0000-00-00 00:00:00', 0, 0, ' ', ' 12 ', ' ', ' 1 ', 'Outils pour la classe CLIN', 'Préparations générales\r\nles apprentissages en clin \r\ntravail à l''oral et exercices systématiques\r\nprogrammation d''éveil avec ponts interdisciplinaires', 'Roland Kara', ' CLIN outils oral passeport ', 'http://roland.kara.chez-alice.fr/do/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (392, 1, '2006-07-28 14:06:55', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 12 ', ' 8 ', ' 4 ', 'Outils de lecture de l''association &quot;en jeux&quot;', 'SYL LA MOT matériel en vente de l''association en jeux pour des élèves de CP ou de GS pour l''apprentissage de la lecture.\r\nJeux pour rentrer dans la combinatoire', 'Antoine STROEBEL', ' lecture syllabe GS CP combinatoire ', 'http://en-jeux.site.voila.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (393, 1, '2006-07-28 14:16:14', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le grand atelier des petis poètes', 'Le grand atelier des petis poètes est un espace :\r\n    * de rencontre\r\n    * d''expression\r\n    * d''échange\r\n    * de collaboration\r\nautour de la Poésie francophone à l''école.', 'Jean-Pierre Aubertin', ' poésies lecture écriture ', 'http://www.ac-nancy-metz.fr/petitspoetes/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (394, 1, '2006-07-28 14:20:38', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Echelle d''acquisition lexicale  Dubois-Buyse', 'L''échelle Dubois-Buyse regroupe 3787 mots d''usage courant.\r\n\r\nL''intérêt de cette Echelle réside dans le fait que la maîtrise orthographique de ses mots, choisis pour leur très fréquente utilisation dans les écrits spontanés, a été testée sur un échantillon très important.\r\nCes mots sont regroupés en 2 catégories : \r\n- les mots-outils (&quot;0&quot; dans la rubrique &quot;échelon&quot; et &quot;Outil&quot; dans la rubrique &quot;Niveau&quot;) qui représentent à eux seuls environ 40% du vocabulaire écrit courant, \r\n- les autres mots étalonnés en degrés de difficulté, les échelons (exemple : les enfants sont censés acquérir en CP les mots des échelons 1 à 9). \r\nVous pouvez rechercher un échelon précis ou travailler sur un intervalle, ou recherche le &quot;niveau&quot; donné d''un mot donné dans la base.', 'Jean-Mars Deleuze', 'orthographe lexicale usage liste échelle Dubois-Buyse base échelle d''acquisition lexicale base', 'http://jeanmarc.deleuze.free.fr/echelleDB/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (395, 1, '2006-07-28 14:30:05', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 4 ', ' 21 ', ' 1 ', 'Site de la petite souris pour les apprentis lecteurs', 'Fiches relatives au domaine de la lecture, préparations de séquences ou fiches d''exercices  \r\nAccès à une  bibliothèque d''images, de polices, de comptines, de chants', '', ' lecture cycle 2 images comptines  chants polices exercices ', 'http://lps13.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (396, 1, '2006-07-28 14:33:48', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 4 ', ' 8 ', ' 4 ', 'Site de la petite souris pour les apprentis lecteurs', 'Accès aux fiches élèves pour les sons pour la lecture au CP ou CE1 ( Cycle 2).\r\nFiches sur les sons\r\na -\r\n i -\r\n o -\r\n é -\r\n u -\r\n e -\r\n f -\r\n s -\r\n ch -\r\n v -\r\n j -\r\n m - \r\nr -\r\n ou -\r\n l -\r\n n -\r\n è -\r\n oi -\r\n p -\r\n t -\r\n c -\r\n d -\r\n on -\r\n b -\r\n g -\r\n gn -\r\n in -\r\n an/en am/em -\r\n z -\r\n ph -\r\n au-eau -\r\n eu-oeu -\r\n ai-ei -\r\n ette -\r\n tr-dr -\r\n fl-cl -\r\n ia-io-ie -\r\n car -\r\n que-qui -\r\n gue-gui -\r\n er-ez -\r\n ge-gi -\r\n ce-ci -\r\n er-es-ec -\r\n ien -\r\n ier-ion -\r\n ain -ein -\r\n oin -\r\n h muet -\r\n s-d-t muets -\r\n ill -\r\n -ail eil -\r\n y -oy -ay -\r\n k-w -', '', ' lecture sons CP ', 'http://lps13.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (397, 1, '2006-07-28 14:37:55', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 4 ', ' 8 ', ' 4 ', 'Table de lecture systématique pour la lecture au cycle 2', 'entraînement oral systématique', 'lps13.free.fr', ' cycle 2 lecture entraînement exercices ', 'http://lps13.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (398, 1, '2006-07-28 14:40:49', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 4 ', ' 8 ', ' 4 ', 'Site de la petite souris- Activités de tri pour les apprentis lecteurs', 'Activités de tri. Manipulations d''images à découper\r\n  Prélecture syllabes - Oppositions phonétiques   p / b  t / d  c / g  f / v  s / z  ch / j  / pr / br  tr / dr  cr / gr  fr / vr   pl / bl  cl / gl  /Tris a / i / o / é  on / in  p / b / d    z / j  b / d', 'site de la petite souris', ' lecture cycle 2 tri discrimination oppositions phonétiques syllabes images ', 'lps13.free.fr', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (399, 1, '2006-07-28 14:45:12', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 4 ', ' 8 ', ' 4 ', 'Site de la petite souris - Des mots outils', 'série de mots-outils. Découpage et manipulations', 'Auteur du site de la petite souris', ' mots outils  lecture cycle 2 ', 'http://lps13.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (400, 1, '2006-07-28 14:50:14', '0000-00-00 00:00:00', 0, 0, ' ', ' 9 ', ' ', ' 1 ', 'Site de la petite souris - Affichage  d''images repères pour la lecture', 'Images repères pour affichage en classe ou  utilisation individuelle', 'Auteur du site de la petite souris', ' affichage sons lecture phonème images CP Cycle 2 ', 'http://lps13.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (401, 1, '2006-07-28 14:54:58', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 12 ', ' 9 ', ' 4 ', 'Site de la petite souris - Des sous-mains évolutifs à proposer tout au long de l', 'Sous-mains ou référent individuels constitués de 2 pages A4 juxtaposées. Coloriables, personnalisables...à mettre en classe et aussi à prévoir à la maison... ou à transporter si l''outil est plastifié.\r\n1er semestre  \r\n29 sons et images-clés\r\n(oppositions appariées),\r\ngraphisme b-p-f, \r\njours de la semaine en script,\r\n20 mots-outils script, mots-couleurs,\r\nquantités de 1 à 10, file de 1 à 33,\r\ndroite, gauche\r\n \r\n2ème semestre\r\n16 maisons des sons,\r\nlettres cursives de l''alphabet,\r\n40 mot-outils en cursif,\r\njours de la semaine en cursif\r\nquantités de 11 à 20, file de 1 à 99', 'Auteur du site de la petite souris', ' lecture cycle 2 sous-main outils ', 'http://lps13.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (402, 1, '2006-07-28 15:01:21', '0000-00-00 00:00:00', 0, 0, ' ', ' 9 ', ' ', ' 1 ', 'Site de la petite souris - Illustration de l''histoire', 'Illustrations téléchargeables de l''histoire les musiciens de Brême', 'Auteur du site de la petite souris', ' histoire illustration contes ', 'http://lps13.free.fr/contenu/eleve/fichiers%20pdf/embr_breme.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (403, 1, '2006-07-28 15:49:47', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Site &quot;Ortholud&quot;  pour apprendre l''orthographe, la conjugaison, la gram', 'Tester vos connaissances en français. Jeux en ligne de grammaire de conjugaison et d''orthographe, phrases à trous, quiz et jeux avec correction en ligne. Auto-correction', 'Auteur d''Ortholud', ' jeux grammaire conjugaison quiz auto-correction grammaire ', 'http://www.ortholud.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (404, 1, '2006-07-28 15:58:38', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Site &quot;Ortholud&quot;  pour des exercices en ligne en conjugaison', 'Une façon de réviser la conjugaison des verbes du CE1 au CM2. Exercices gradués par thème d''étude, par temps, par groupe. Possibilité de s''auto-corriger et indication des scores de réussite l''exercice terminé.', 'Ortholud', ' exercices conjugaison CE1 CE2 CM1 CM2 groupes temps verbes ', 'http://www.ortholud.com/conjugaison.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (405, 1, '2006-07-28 17:28:22', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Site &quot;Ortholud&quot;  pour des exercices de grammaire en ligne', 'Exercices en ligne en grammaire couvant l''ensemble des notions du programme.\r\nRetrouver l''ordre des phrases. Phrases pour CP - CE1:\r\nRetrouver le verbe dans ses phrases\r\nRelier les mots pour construire un phrase (exercice pour CP - CE1 - FLE)\r\nDans chaque phrase, trouve le mot qui est en trop: Exercice\r\nLa pontuation\r\nLa forme négative\r\nEcris les phrases sous forme négative: Exercice 1\r\nLes phrases découpées\r\nGenre des adjectifs qualificatifs (règles)    \r\nLes fins de mots en au et eau\r\nChoisissez entre au, aux, eau ou eaux \r\nLe pluriel des noms communs\r\nLes mots avec terminaison en au et eau (cm1,cm2,fle)\r\nChoisissez entre aux, eaux ou ots (cm1,cm2,fle)\r\nLe pluriel des mots se terminant en ail (cm1,cm2,fle)\r\nL''adjectif démonstratif\r\nExercice avec: &quot;Ce ou Cet&quot;\r\nExercice avec: &quot;Celle, Celles, Celui, Ceux, Ce ou Cela&quot; \r\nL''adjectif possessif, placer le bon adjectif possessif\r\nLes adverbes, choisissez entre près, très ou trop\r\nLes homophones: ne pas confondre Son ou Sont Se ou Ce Ses et ces:\r\nEt ou Est:On et Ont Quel, quels, quelles, quelle, qu''elle, qu''elles', 'Auteur du site de Ortholud', ' grammaire exercices interactifs étude de la langue corrigés homonymes nature gratuit ', 'http://www.ortholud.com/grammaire.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (406, 1, '2006-07-28 19:56:57', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Exercices en ligne de grammaire avec &quot;le chat bleu&quot;', 'Site interactif pour les élèves. Exercices gradués par niveaux pour la grammaire.\r\nReconstituer la phrase. Reconnaître le type de phrases.', 'Auteur du chat bleu', ' phrase  déclarative interrogative exclamative impérative grammaire ', 'http://www.chatbleu.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (407, 1, '2006-07-28 20:04:14', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Exercices en ligne d''orthographe  avec &quot;le chat bleu&quot;', 'Exercices interactifs sur les homophones.\r\n     ce ou se  	 	\r\n	c''est ou s''est 	\r\n	et ou est 	\r\n	et ou est ou es 	\r\n	à ou a 	\r\n	à ou a ou as 	\r\n	ou ou où 		\r\n	son ou sont 	\r\n	tout ou tous\r\nRemplacer les points d''interrogation avec l''homonyme qui convient.', 'Auteur du chat bleu', ' grammmaire orthographe ORL observation réfléchie de la langue ', 'http://www.chatbleu.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (408, 1, '2006-07-29 15:10:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Logiciel Dicoverb  permettant la conjugaison de verbes à tous les temps', 'Logiciel de conjugaison V3.9.9 (Septembre 2003) (GRATUIT)\r\nIl permet de vérfier l''orthographe de tous les temps des verbes.Il est capable de sortir des tableaux de conjugaison, et de mettre en évidence la voix active, la voix passive, les formes interrogatives, négatives. C''est un outil idéal quand tu écris et que tu veux vérfier l''orthograhe d''un verbe conjugué. Idéal pour la correction d''un texte.', 'Hervé Echelard', ' conjugaison ORL observation réfléchie de la langue temps forme négative interrogative relecture ', 'http://perso.orange.fr/herve.echelard/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (409, 1, '2006-07-29 15:36:28', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 19 ', ' 4 ', 'AniPaint, un logiciel pour dessiner et faire du montage mutimédia', 'Logiciel simple et complet permettant de dessiner et de faire du montage multimédia. Logiciel payant', 'AniPaint', ' dessiner dessin logiciel multimédia ', 'http://www.anipaint.com/ap3/main.php?aptheme=2&amp;language=fr&amp;column=1', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (410, 1, '2006-07-29 15:37:27', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 19 ', ' 4 ', 'Art Pad, site de dessin en ligne idéal pour des activités collaboratives', 'Ce site de dessin en ligne permet de créer des illustrations à l’aide d’outils graphiques configurables et de les envoyer par email à la personne de son choix. \r\nPoints forts de ce logiciel; &quot;l’utilisateur qui réceptionne le mail y trouve un lien sur lequel il doit cliquer. Il est alors conduit sur Internet et là, oh miracle, le dessin se reconstitue devant lui traits après traits, tel que conçu par son créateur de départ. On peut choisir de créer à son tour une nouvelle oeuvre, mais il est également possible de complèter l’oeuvre reçue par nos propres traits, puis de la renvoyer à son expéditeur qui verra à son tour celle-ci se recréer sous ses yeux ! &quot; \r\n\r\nIl serait intéressant d’imaginer une activité pédagogique collaborative en utilisant cet outil.', 'Art Pad', ' dessin arts visuels ', 'http://artpad.art.com/artpad/painter/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (411, 1, '2006-07-29 16:03:16', '0000-00-00 00:00:00', 0, 0, ' ', ' 8 ', ' ', ' 4 ', 'FreeMind un logiciel de conception de cartes heuristiques pour &quot;apprendre à', 'FreeMind est un logiciel de conception de cartes heuristiques, appelées aussi &quot;remue-méninge&quot;. FreeMind est un outil de mise en forme des idées en «réseau de concepts». La navigation est particulièrement pratique car on peut faire déployer chaque concept avec toutes ses ramifications en un seul clic ou suivre les liens suggérés directement. On peut même s’en servir comme base de données, outil de planification, de suivi ou de gestion de projet, outil de scénarisation, d’agenda.', 'Joerg Mueller', ' compétences transversal organisation carte heuristique outil scénarisation méthodologie:méthodes ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=297', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (412, 1, '2006-08-01 22:49:02', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Frise et dossiers pour comprendre  l''histoire', 'Une frise qui défile. Tu voyages au fil du temps. Tu t''arrêtes sur un dossier et un commentaire oral qui te convient.\r\nTu apprendras plein de choses pour chaque période de l''histoire. A utiliser en fin de cycle 3 éventuellement à adapter en adaptant ou en choisissant les documents pour des élèves de cycle 3.', 'Amis d''Hérodote', ' histoire frise dossier ', 'http://www.herodote.net/frises/chrono.php?periode=0', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (413, 1, '2006-07-29 20:49:34', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 10 ', ' 4 ', '&quot;Flash Slide Show Maker&quot;, un logiciel facile pour créer des diaporamas', 'Flash Slide Show Maker permet la réalisation facile de diaporamas en flash. Les élèves pourront facilement créer leurs propres diaporamas, qui s''inséreront facilement dans un projet plus vaste (site internet, cd-rom...).Créez, à partir d''images, un diaporama en flash, en quelques clics de souris, avec insertion d''effets de transition, réglage (global) des durées (affichage de l''image et de la transition), insertion de musique (mp3 uniquement)... le tout en quelques clics ! Prise en main très facile.', 'ANVSOFT', ' logiciel propriétaire gratuit diaporama ', 'http://flash.dvd-photo-slideshow.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (414, 1, '2006-07-29 21:14:36', '0000-00-00 00:00:00', 0, 0, ' ', ' 8 ', ' ', ' 1 ', '&quot;Shrink pic&quot; un logiciel utilitaire de compression automatisée des pho', 'Shrink pic est utilitaire de compression automatisée des photos, lié à l''envoi de mails (via outlock ou thunderbird).\r\nshrink pic, qui redimensionne vos photos avant tout envoi sur le web.\r\nShrink Pic permet l’envoi de dizaine de photos en pièce jointe – rapidement et sans effort !Il vous suffit d’installer Shrink Pic sur votre ordinateur. Il détecte automatiquement l’envoi de grandes photos et les compresse automatiquement en arrière-plan.Il n’y a aucun réglage, ni instructions, rien, nadaIl vous suffit d’envoyer vos photos et Shrink Pic les compresse.', 'schrink', ' photos logiciel gratuit propriétaire utilitaire ', 'http://www.onthegosoft.com/fr/shrink_pic.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (415, 1, '2006-07-29 21:36:18', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 19 ', ' 4 ', 'Art rage 2 un logicel gratuit pour les arts', 'Logiciel de peinture 2d très réaliste et simple d''utilisation...Artrage imite les outils traditionnels et basiques du dessin\r\nIl y a 2 versions,une gratuite et l''autre à 20,67€ en téléchargement... \r\n- calques et beaucoup d'' outils supplémentaires depuis sa dernière version. Utiisable facilement dès les petites classes', 'ArtRage 2.0 © Ambient Design Ltd, 2003 - 2006.', ' Arts visuels dessin graphisme peinture freeware ', 'http://www.ambientdesign.com/artragedown.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (416, 1, '2006-07-29 21:58:43', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 10 ', ' 4 ', 'Photofiltre, logiciel gratuit, simple et puissant pour la création et la retouch', 'Logiciel gratuit, simple et puissant pour la création et la retouche d''images. Très léger, rapide, intuitif, PhotoFiltre propose une approche intéressante en fin de Cycke 3. Des informations, liens, tutoriels sont en ligne à l''adresse du site.PhotoFiltre Studio est disponible en version shareware.\r\nMis à jour régulièrement et gratuit , son principal défaut, en dehors de n''être disponible que pour windows, est de ne pas pouvoir utiliser les calques, mais cette lacune est comblée dans sa version payante : Photostudio', 'Auteur de photofiltre', ' photos retouche image arts visuels tutoriel shareware gratuit ', 'http://www.photofiltre.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (417, 1, '2006-07-29 22:09:28', '0000-00-00 00:00:00', 0, 0, ' ', ' 7 ', ' ', ' 1 ', 'Picasa  un logiciel pour classer les images sur l''ordinateur', 'Cet outil est très simple, ultrarapide pour aller retrouver les images disséminées dans votre ordinateur. Picasa crée des albums et vos donne la possibilté de les exporter sous forme de pages web. Picasa est entièrement gratuit.\r\nCe logiciel innove en en matière d’organisation de photos. Il ne raisonne pas en termes de dossiers et de fichiers mais en ceux d’espace et de temps. Il détecte automatiquement les appareils photos numériques connectés via un port USB et trie les photos importées en respectant l’ordre chronologique. Ceci facilite leur repérage et leur affichage et évite de devoir perdre du temps à les rechercher. Déplacer et renommer les photos, créer des albums virtuels sont autant d’opérations possibles sans avoir à farfouiller dans les répertoires\r\n http://www.zdnet.fr/telecharger/windows/fiche/0,39021313,11010531s,00.htm', 'Picasa Google', ' images classement utilitaire ', 'http://www.zdnet.fr/telecharger/windows/fiche/0,39021313,11010531s,00.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (418, 1, '2006-07-29 22:13:28', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 19 ', ' 4 ', 'Artweaver, un logiciel gratuit pour les arts', 'Entièrement gratuit, ce logiciel ressemble à Gimp en beaucoup plus simple. Une jolie palette d''outils et...la possibilité de travailler les calques ! Il a l''immense avantage par rapport à photofiltre de supporter les calques et d''avoir une palette d''outils et de brosses bien plus riche. En langue anglaise à l''origine, il a été en partie traduit en français (fichier disponible sur la page de téléchargement d''artweaver. http://www.artweaver.de/index.php?en_version', 'Auteur de Artweare', ' arts visuels freeware gratuit ', 'http://www.artweaver.de/index.php?en_version', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (419, 1, '2006-07-29 22:19:19', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 19 ', ' 1 ', 'Logiciel libre the GIMP pour les arts visuels', 'Logiciel libre que l''on compare souvent à photoshop pour les nombreuses possibilités qu''il offre, il est un peu difficile à prendre en main et les élèves (surtout en collège) sont vite perdus au milieu des nombreuses fenêtres ouvertes. Néanmoins un fois que l''on a compris le principe et que l''on sait que tous les menus restent à disposition par un simple clic droit, les possibilités sont fort nombreuses. Il existe fort heureusement quelques tutoriels. Voir sur l''espace premier degré du pôle de compétences des logiciels libres.', 'Spencer Kimball, Peter Mattis, et l’équipe de développement de The Gimp', ' arts visuels logiciel libre gratuit ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=128', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (420, 1, '2006-07-29 23:12:06', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' ', 'logiciel drawing for chidren', '', 'Mark Overmars', '  ', '', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (421, 1, '2006-07-30 10:08:10', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 20 ', ' 1 ', 'Oeuvres références pour l’écoute musicale dans les nouveaux programmes de l’écol', 'Dans ce document, Pascal Buch a repris la liste des oeuvres référencées des nouveaux programmes 2002. Sur les oeuvres et les auteurs, vous trouverez des liens sur les extraits musicaux, la vie et la biographie des compositeurs.\r\n Pierre et le Loup Prokofiev,Le Carnaval des Animaux; Saint-Saëns, Guide de l’orchestre pour une jeune personne 	Britten, L’apprenti sorcier Paul dukas, biographie ,La Sorcière du placard à balais,Marcel Landowski d’après Pierre Gripari, Fifi, Albert et la voix; Betsy Jolas, Barnabé et les bruits de la vie,	Louis Dandrel,Loulou et Pierrot-la-Lune et Les Drôles de sons ,Philippe Mion, La collection L’Oreille en colimaçon éditée par Radio-France', 'Education nationale  programmes 2002', ' musique écoute compositeurs oeuvres biographie Prokokiev Saint-Saëns Dukas ', 'http://www.gap.ien.05.ac-aix-marseille.fr/rre/article.php3?id_article=950&amp;var_recherche=dict%E9e', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (422, 1, '2006-07-30 10:21:11', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 19 ', ' 1 ', 'Oeuvres références pour les arts visuels  dans les programmes 2002 de l’école pr', 'Liste des oeuvres de rérérence pour les arts visuels dans les programmes 2002.  Des liens sur les artistes et sur les oeuvres.Oeuvres classées par grandes périodes.(préhistoire, antiquité, moyen-age,temps modernes,19 siècle et 20 siècle)\r\nle château de Chambord -Andrea Palladio , la Rotonda ,Claude-Nicolas Ledoux , les Salines royales d’Arc-et-Senans ,le Chrysler Building  de William Van Alen,la Cité radieuse  du Corbusier, Brasilia , Oscar Niemeye,le Centre Georges Pompidou , Renzo Piano  et Richard Rogers, le Musée Guggenheim , Frank O. Ghery,: Charlie Chaplin ,  La ruée vers l’or  , Les temps modernes,Jean Cocteau , La Belle et la bête ,Howard Hawks , Rio Bravo ,Fred Zinneman , Le train sifflera trois fois,Stanley Donen  et Gene Kelly , Chantons sous la pluie ,Fritz Lang , Les Contrebandiers de Moonfleet,Paul Grimault , Le Roi et l’oiseau ,Abbas Kiarostami , Où est la maison de mon ami ? , Une gravure  de Albrecht Dürer  (XVème-XVIème siècle).\r\n\r\n-  Un dessin à la sanguine de Michel-Ange (XVème-XVIème siècle).\r\n\r\n-  Une planche de l’Encyclopédie de Diderot et d’Alembert.\r\n\r\n-  Un dessin aux trois crayons de Antoine Watteau (XVIIIème siècle).\r\n\r\n-  Une aquarelle de William Turner (XVIIIe-XIXe siècle).\r\n\r\n-  Un dessin à la mine de plomb de Jean Auguste Dominique Ingres (XIXème siècle).\r\n\r\n-  Un dessin au fusain de Georges Seurat (XIXème siècle).\r\n\r\n-  Un dessin à la brosse de Hans Arp (XXème siècle).La lumière : Johannes Vermeer  (XVIIème siècle), La Dentellière  (Musée du Louvre, Paris) ou La Laitière  (Rijkmuseum, Amsterdam) ; ou Georges de La Tour (XVIIème siècle), La Nativité  (Musée des Beaux-Arts, Rennes) ou Job et sa femme  (Musée départemental des Vosges, Epinal).\r\n\r\n-  La couleur : une tapisserie de Sonia Delaunay (XXème siècle) ou un monochrome d’Yves Klein(XXème siècle).\r\n\r\n-  La perspective : Fra Angelico , L’Annonciation (Couvent San Marco, Florence, Italie, XVème siècle).\r\n\r\n-  La composition : Diego Velázquez , Les Ménines (XVIIe siècle, Musée du Prado, Madrid, Espagne).\r\n\r\nLes procédés\r\n\r\n-  Le collage : une œuvre de Kurt Schwitters (XXème siècle).\r\n\r\n-  Le montage et le photomontage : une œuvre de Max Ernst (XXème siècle).\r\n\r\n-  L’égouttage « dripping » : une œuvre de Jackson Pollock (XXème siècle).\r\n\r\nLes genres\r\n\r\n-  L’autoportrait : Rembrandt (XVIIème siècle) ou Vincent Van Gogh (XIXème siècle).\r\n\r\n-  La nature morte  : Paul Cézanne (XIX-XXème siècle).\r\n\r\n-  Le paysage : Camille Corot (XIXème siècle) ou Claude Monet (XIXème-XXème siècle).adar : Portrait de Sarah Bernhardt  ou de Baudelaire  (XIXème siècle).\r\n\r\n-  André Kertesz  : La Fourchette (1928).\r\n\r\n-  Henri Cartier-Bresson  : Ecluse de Bougival (1955).\r\n\r\n-  Man Ray  : un rayogramme (XXème siècle).\r\n\r\n-  Robert Capa  : Mort d’un soldat républicain près de Cerro Muriano (Espagne septembre 1936).\r\n\r\n-  Une photographie couleur , par exemple de William Wegman (XXème siècle).\r\n\r\nSculpture\r\n\r\n-  Une sculpture de l’antiquité : La Venus de Milo \r\n\r\n-  Un chapiteau roman ou un tympan (par exemple, celui de la basilique de Vézelay ou de Moissac ).\r\n\r\n-  Un marbre de Michel Ange : David (1504, Gallerie dell’Académia, Florence, Italie).\r\n\r\n-  Un bronze d’Auguste Rodin  : Les Bourgeois de Calais (1884, Musée Rodin, Paris).\r\n\r\n-  Un plâtre d’Alberto Giacometti  : Objet désagréable (1931, MNAM, Paris).\r\n\r\n-  Un mobile d’Alexander Calder  : Mobile sur deux plans (1955, MNAM, Paris).\r\n\r\n-  Un bois peint de Louise Nevelson (XXème siècle)\r\n\r\n-  Une installation vidéo de Nam June Paik  : Moon is the oldest TV \r\n\r\n-  Une sculpture animée de Jean Tinguely  : L’Enfer , un petit détail \r\n\r\nVitrail\r\n\r\n-  Un vitrail médiéval : Charlemagne , cathédrale de Chartres (Eure-et-Loir, XIIIème siècle).\r\n\r\n-  Un vitrail moderne : Henri Matisse , chapelle de Vence (Alpes-Maritimes, 1948-1951).\r\n\r\n-  Un vitrail contemporain : Pierre Soulages , abbatiale de Conques (Aveyron, 1987-1994)', 'Programmes 2002 Education nationale', ' architecture cinéma dessin peinture composition photographie sculpture vitrail préhistoire antiquité ', 'http://www.gap.ien.05.ac-aix-marseille.fr/rre/article.php3?id_article=765', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (423, 1, '2006-07-30 14:02:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'logiciel avec des exercices interactifs en ligne en ORL', 'Exercices d''orthographe en ligne, permettant l''entraînement autour des notions suivantes:\r\naccord sujet verbe, accord adjectif nom\r\nprésent de l''indicatif des verbes être et avoir\r\nprendre au présent de l''indicatif\r\nprésent de l''indicatif en « e, es ou ent »\r\nprésent de l''indicatif en « is, is ou it »\r\n présent en « es » à l''imparfait en « ais »\r\nprésent de l''indicatif des verbes être et avoir\r\nprendre au présent de l''indicatif\r\nprésent de l''indicatif en « e, es ou ent »\r\nprésent de l''indicatif en « is, is ou it »\r\nprésent en « es » à l''imparfait en « ais »\r\nprésent en « e » (3e pers. du sing.) à l''imparfait en « ait »\r\nprésent en « ons » à l''imparfait en « ions »\r\nprésent en « ez » à l''imparfait en « iez »\r\nprésent des verbes en venir et tenir (singulier)\r\nfutur en irai et ierai\r\nles sons é et è\r\nverbes en eler et eter \r\nprésent des verbes venir et tenir\r\nfutur en irai et ierai\r\nles sons é et è\r\nverbes en eler et eter \r\nparticipe passé en é ou infinitif en er Imparfait : i ou ii le conditionnel présent impératif présent à la forme négative', 'realsjacques', ' orthographe grammaire ORL accords conjugaison verbes logiciel gratuit ', 'http://pages.infinit.net/jaser2/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (424, 1, '2006-07-30 14:29:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'logiciel avec des exercices interactifs en orthographe ORL  en ligne -Orthograph', 'Des execices directs en ligne pour maîtriser l''orthographe d''usage de mots simples , en écrivant correctement les noms des objets ou des animaux représentés par un dessin, tu feras monter le mercure du thermomètre. Correction et score de réussite via un thermomètre &quot;qui monte&quot; dès que l''on a réussi.', 'realsjacques', ' ORL orthographe exercices corrigé correction usage lexical ', 'http://pages.infinit.net/jaser2/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (425, 1, '2006-07-30 14:51:16', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Site de Mamiplume pour échanger des histoires', 'Il s''agit d''une bourse amicale d''échange d''histoires illustrées pour enfants. Tu peux y accéder gratuitement à tous les livres et les IMPRIMER. Chacun peut faire héberger ses histoires illustrées à condition de respecter une charte.', 'association Mamiplume et ses auteurs', ' histoires récits écriture littérature B2i lecture ', 'http://mamiplume.free.fr/jeu%20de%20cadres.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (426, 1, '2006-07-30 15:09:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Histoires interactives et contes interactifs en flash', 'Contes interactifs pour enfants, avec jeux, sons...', 'continette.', ' histoires scénarios littérature langage oral récit structure logiciel ', 'http://continette.free.fr', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (427, 1, '2006-07-30 15:16:12', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Contes irlandais en images animés', 'Association images et textes sonores. Chaque page est illustrée par une animation flash et accompagnée d''un texte écrit. &quot;Les  iconographies sont très réussies et donnent à voir de beaux personnages de l''imagerie celte&quot; (Fenêtres sur cours)&quot;. &quot;Contes Irlandais&quot; a obtenu le Net d''Or régional 2003 des Pays de la Loire (catégorie&quot;site insolite&quot;)', 'James Fitz Patrick', ' contes animation logiciel littérature gratuit histoires ', 'http://www.contes-irlandais.com', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (428, 1, '2006-07-30 15:23:53', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 10 ', ' 4 ', 'Le site  Clicksouris ... des histoires interactives et des ateliers d''écriture', 'Des histoires interactives et des jeux dont tu es le héros, des choix, des énigmes, des ateliers d''écriture, des fins à inventer...Un bestiaire poétique pour lire, écrire et jouer avec les plus belles poésies sur les animaux.\r\n Le Bibliozoo : des livres choisis dans le meilleur de la littérature jeunesse. Tu peux organiser  un atelier &quot;Clicksouris&quot;. \r\nClicksouris c''est aussi découvrir la lecture interactive et l''écriture hypertextuelle en imaginant une fiction interactive \r\npubliée sur un site Internet d''histoires pour enfants.', 'clicksouris', ' histoires récits llittérature interactive bestiaire écrire lire atelier interactif ', 'http://www.clicksouris.com', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (429, 1, '2006-07-30 15:56:51', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 6 ', ' 1 ', 'Modules de natation pour non-nageurs', 'une progression et des fiches de travail autour des principaux items de l’apprentissage de la natation (Entrer dans l’eau - S’immerger - S’équilibrer / Flotter - Se propulser / Respirer ) elle met son travail à disposition des enseignants intéressés', 'Anne Gantelet Inspection de Châlons', ' natation immersion flottaison EPS piscine ', 'http://ien.chalons2.free.fr/article.php3?id_article=225', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (430, 1, '2006-07-30 16:02:12', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 16 ', ' 1 ', 'Programmations en sciences', 'Base de données dt CRDP de Montpellier permettant de traiter les points suuvants du programme. La matière   La matière et les objets-Etats et changements d''état de l''eau Mélanges et solutions. L''air et son caractère pesant Plans horizontal et vertical - Université et diversité du monde vivant -Stades et condition de développement Divers modes de reproduction des êtres vivants-Les mouvements corporels Les grandes étapes de l''évolution   \r\nEducation à l''environnement-Approche écologique, rôle et place des êtres vivants-L''eau-Les déchets Le rôle de l''Homme -Energie Sources d''énergie utilisables, consommation et économie - Ciel et Terre- La lumière et les ombres Les points cardinaux et la boussole Mouvement apparent du soleil Rotation de la Terre sur elle-même et ses conséquences Le système solaire et l''Univers Mesure des durées et unités  -Le corps humain et l''éducation à la santé  Les mouvements corporels La reproduction des humains Première approche de la fonction de nutrition Hygiène Cinq sens -Monde construit par l''Homme', 'CRDP de Montpellier', ' sciences programmation matière environnement vivant énergie ciel Terre corps santé technologie ', 'http://www.crdp-montpellier.fr/cd66/map66/pages/programmation/sommaire.htm', 'fr.png', 'N', 0, 'O');
