INSERT INTO `resource_online` VALUES (432, 1, '2006-07-30 16:21:52', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 16 ', ' 3 ', 'Le cahier d''expérimentation au cycle 3', 'Démarche pour intégrer le cahier d''expérimentation.\r\n http://ien.chalons2.free.fr/IMG/pdf/Bipsciences.pdf', 'ien Châlon 2', ' cahier expériences cycle3 démarche sciences ', 'http://ien.chalons2.free.fr/article.php3?id_article=70', 'fr.png', 'N', 0, 'O');
