INSERT INTO `resource_online` VALUES (434, 1, '2006-07-30 16:31:06', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 16 ', ' 1 ', 'Aides à la programmation de cycle en sciences', 'Une rubrique du site &quot;enseigner les sciences&quot; en Ariège', 'site &quot;enseigner les sciences&quot; en Ariège', ' sciences programmations ', 'http://pedagogie.ac-toulouse.fr/ariege-education/sciences09/php/rubrique.php3?id_rubrique=67', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (435, 1, '2006-07-30 16:36:23', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 16 ', ' 1 ', 'Des progressions complètes  à télécharger en sciences', 'Le site &quot;sciences en ligne&quot; vous propose une rubrique dédiée aux progressions en sciences.\r\nLes progressions (ensemble de situations d’investigation + les conseils pédagogiques pour bien les mener) sont désormais disponibles au téléchargement et, pour certaines concernant le Cycle 1, sous la forme d’un fichier pédagogique et d’une mallette de matériel expérimental.Chaque fichier à télécharger :   est au format PDF, \r\n  comporte les situations de départ sur le thème choisi,   des conseils relatifs à chaque situation de la progression et éventuellement sur le matériel   des extraits de travaux d’élèves (cahiers d’expériences) pour chaque situation.\r\nLle téléchargement des progressions est payant (moins de 2 euros par progression si vous êtes en France métropolitaine). Pour télécharger les progressions complètes, cliquez sur les liens :\r\n  Air Cycle 2 \r\n  Air Cycle 3 \r\n  Eau et matière Cycle 2 \r\n  Eau et matière Cycle 3 \r\n  Ombres et lumières Grande-Section (cycle 2) \r\n  Lumière Cycle 1 \r\n  Les aimants Cycle 1', 'sciences en ligne', ' progressions sciences matériel thèmes payant ', 'http://www.educasciences.org/sciences_en_ligne/article.php3?id_article=185', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (436, 1, '2006-07-30 16:47:52', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 16 ', ' 4 ', 'Un projet d''écriture &quot;Fiction ou journal&quot; à partir de sciences', 'Educasciences vous propose un site pour un projet d''écriture avec un arrière plan scientifique. Une occasion de réinvestir les notions vues en sciences et de changer de registre', 'educasciences', ' projets écriture sciences ', 'http://www.educasciences.org/sciences_en_ligne/article.php3?id_article=260', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (437, 1, '2006-07-30 17:15:34', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 16 ', ' 3 ', 'Site &quot;des sciences en ligne&quot; pour l''école', 'Le site “Sciences en ligne” est dédié aux enseignants et leurs élèves. Ce projet est proposé par l’ASSOCIATION Educ@Sciences .', 'Association Educ@Sciences Claudette Balpe et Stéphane Respaud', ' sciences projets Educ@Sciences ', 'http://www.educasciences.org/sciences_en_ligne/rubrique.php3?id_rubrique=136', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (438, 1, '2006-07-30 17:24:45', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 8 ', ' 3 ', 'Site &quot;Bien lire&quot; à l''école', 'Le site d''accompagnement du plan de prévention de l''illettrisme propose aux adultes intervenant auprès des enfants et des jeunes des outils, des exemples d’actions menées sur le terrain, des documents d’analyse et des communications de chercheurs', 'Education nationale  Site institutionnel', ' lire recherche outils lecture chercheurs ', 'http://www.bienlire.education.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (439, 1, '2006-07-30 17:27:31', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 8 ', ' 3 ', 'SIte institutionnel EDUSCOL de la direction des écoles et oeuvres de littérature', 'Le site pédagogique du Ministère de l''Éducation nationale présente les listes de références des œuvres de littérature pour les trois cycles de l''école primaire.', 'Ministère EN', ' littérature oeuvres liste références ', 'http://eduscol.education.fr/D0102/biblio.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (440, 1, '2006-07-30 17:30:48', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 8 ', ' 3 ', 'Les sélections en littérature de jeunesse du CNDP', 'La rubrique &quot;Actualités pour la classe&quot; propose des sélections commentées de livres de littérature de jeunesse. Les sélections Albums et romans sont proposées par La Joie par les livres, les sélections Bandes dessinées par Didier Quella-Guyot (CRDP de Poitou-Charentes).', 'CNDP', ' littérature:Jeunesse CNDP albums romans documentaires ', 'http://www.cndp.fr/actualites/eleve/livres/L20060607.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (441, 1, '2006-07-30 17:41:51', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 8 ', ' 3 ', 'La présence de la littérature à l''école', 'informations générales et nationales sur la littérature\r\nCet espace se propose d''être une source d''informations sur l''actualité de la littérature à l''école avec : \r\n- des dossiers pédagogiques\r\n- des articles de réflexion par des grands spécialistes\r\n- des pistes pédagogiques réalisées par des enseignants, et des documents complémentaires (photos, vidéos).\r\nUne rubrique permet de se renseigner sur l''opération Année du livre à l''école.', 'CNDP', 'littérature informations s''informer vidéo livre', 'http://www.artsculture.education.fr/presence_litterature/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (442, 1, '2006-07-30 17:43:21', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 8 ', ' 3 ', 'Espace littérature sur le site Arts et Culture', 'L''espace littérature du site Arts et Culture propose des informations pratiques pour mettre en place des projets pédagogiques en liaison avec la littérature de jeunesse (textes officiels, carnets d''adresses, listes de livres conseillés par niveau...)Pôles de ressources\r\n\r\n &quot;Ecritures contemporaines francophones et théâtre&quot; - Limoges\r\n\r\n &quot;Littérature à dominante Littérature de jeunesse&quot; - Créteil\r\n\r\n &quot;Littérature&quot; - Lyon\r\n\r\n &quot;Littérature-poésie&quot; - Clermont Ferrand\r\n\r\n &quot;Littérature de jeunesse&quot; - Grenoble', 'CNDP', ' littérature jeunesse ', 'http://www.artsculture.education.fr/litterature/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (443, 1, '2006-07-30 17:54:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 8 ', ' 3 ', 'Répertoire des 1001 livres pour l''école pour exploiter la littérature de jeuness', 'La Bibliothèque des Mille et Un livres est un répertoire conçu comme un guide pour choisir des ouvrages de littérature de jeunesse s''adressant aux enfants de 2 à 11 ans : album, poésie, bande dessinée, conte, nouvelle, roman. Les ouvrages ont été sélectionnés selon les critères suivants : \r\n-documents rendant compte de la diversité des productions des éditeurs pour la jeunesse \r\n-documents fournissant une offre culturelle ou de lecture adaptée au lectorat \r\n-documents prenant en compte la variété des genres, la multiplicité des thèmes, l''équilibre des fonds en fonction du niveau des élèves et de l''utilisation pédagogique envisagée. \r\nLa bibliothèque contient mille et une fiches bibliographiques décrivant les ouvrages sélectionnés. Chaque fiche comporte le titre, la couverture, un résumé, des entrées pédagogiques par âge, thèmes, types d''ouvrages et mots clés. Présentation thématique des ouvrages\r\n  	  	Basée sur une classification pédagogique comportant sept thèmes principaux.\r\nPermet aussi d''imprimer la liste des Mille et Un livres classés par thèmes ou par noms.\r\n	Recherche par mot clé\r\n  	  	Basée sur la collection de mots clés indexant les Mille et Un livres\r\n	Recherche multicritère\r\n  	  	Pour une recherche plein texte dans le titre, la présentation, les mots clés ou les créateurs (auteurs, photographes, illustrateurs) des ouvrages, complétée par un critère d''âge ou le choix d''un thème.', 'CNDP MEN', ' répertoire album poésie BD conte  nouvelle  roman littérature thèmes base classification recherche ', 'http://www.cndp.fr/1001livres/script/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (444, 1, '2006-07-30 18:03:15', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 8 ', ' 3 ', 'Observatoire national de lecture', 'L''espace Livres de jeunesse et apprentissages sélectionne des titres pour chacun des trois cycles à partir de critères explicites définissant les apprentissages visés. Il donne des exemples, des explicitations et une mise en œuvre de la démarche affichée. Un espace est dédié pour les méthodes de lecture au CP.', 'ONL Ministère de l''Éducation nationale,de l''Enseignement supérieur et de la Recherche', ' lecture littérature méthodes ', 'http://onl.inrp.fr/ONL/garde?accueil.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (445, 1, '2006-07-30 18:10:01', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 8 ', ' 3 4 ', 'Le site L@BD', 'Le site L@BD est une base de données sur la bande dessinée (albums, ouvrages documentaires, revues pédagogiques, magazines spécialisés, fanzines, sites, vidéos... ) et sur l''actualité des parutions. Il offre des possibilités de recherche multicritères.', 'CNDP CRDP de Poitiers', ' base liste ', 'http://www.labd.cndp.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (446, 1, '2006-07-30 18:15:06', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' ', 'Site Attire-lire CDDP de la Gironde', 'Le CDDP de Gironde propose des livrets d''accompagnement pédagogique pour l''exploitation en classe de valises-lecture.\r\n   \r\nBébés chouettes -  M. Waddel, P. Benson - Kaléidoscope - PS MS  \r\nEt le petit dit... - J. Maubille - L''école des loisirs, Pastel\r\n - PS \r\n \r\nLe petit loir qui voulait rencontrer Saint Nicolas - E. Schmid - Nord-Sud\r\n - PS MS \r\n \r\nLe plus bel oeuf du monde - H. Heine - Gallimard\r\n - PS MS \r\n \r\nLoup y es-tu ? -  S. Auzary-Luton - Kaléidoscope\r\n - MS GS \r\n \r\nRentrons à la maison petit ours - M. Waddel - L''école  des loisirs, Pastel\r\n - PS MS  \r\n \r\nRouge Matou - E. Battut - Milan\r\n - MS \r\n \r\n  \r\n    \r\nPour le cycle II      \r\nNiveau \r\n \r\n    \r\nBasile Boude - W. Steig - Kaléidoscope\r\n - CP CE \r\n \r\nBon appétit Monsieur Lapin - C. Boujon - L''école des loisirs,  Lutin poche\r\n - GS CP \r\nC''est moi le plus fort - M. Ramos - L''école des loisirs, Pastel\r\n - CP \r\n \r\nCric-Crac - Rascal &amp; Girel - L''école des loisirs\r\n - CP CE1 \r\n \r\nElmer - D. McKee - L''école des loisirs, Lutin poche\r\n - GS CP \r\n \r\nIl y a un crocodile sous mon lit - I. &amp; D. Schubert - Grasset \r\n - CP CE1 \r\n \r\nL’arbre aux corbeaux - Stibane - L''école des loisirs, Pastel \r\n -GS CP CE1 \r\n \r\nL''arbre généreux - S. Silverstein - L''école des loisirs\r\n -GS CP CE1 \r\n \r\nL''énorme crocodile - R. Dahl – Gallimard, Folio benjamin \r\n - CE1 CE2\r\n \r\nLa chanson d’Olive - R. Lewis - L''école des loisirs\r\n - CP CE1 \r\n \r\nLa chasse à l''ours  - M. Rosen - Kaléidoscope\r\n - GS CP \r\n \r\nLe buveur d''encre - E. Sanvoisin - M. Satje -  \r\n - CE1 CE2\r\n \r\nLe chat orange - A. Mets - L''école des loisirs\r\n - GS CP \r\n \r\nLe Géant de Zéralda - T. Ungerer - L''école des loisirs\r\n - GS CP \r\n \r\nLe loup est revenu - G. de Pennart -  Kaléidoscope\r\n - CP \r\n \r\nLe petitparadis - A. Mets - L''école des loisirs\r\n - CP CE1 \r\n \r\nLes trois brigands - T. Ungerer - L''école des loisirs\r\n - GS CP \r\n \r\nMais je suis un ours - F. Thaslin - L''école des loisirs \r\n - CE1 CE2\r\n \r\nPélagie la sorcière - V. Thomas - Milan \r\n - GS CP \r\n \r\nPetit Lapin Rouge - Rascal - L''école des loisirs, Pastel. \r\n - GS CP \r\n \r\nPorculus - A. Lobel - L''école des loisirs, Lutin poche \r\n - CP CE1 \r\n \r\nPoèmes de la lune... - Jean Joubert - L''école des loisirs  - Cycle 2 \r\nPuni Cagibi - A. Serres - L''école des loisirs, Lutin poche. \r\n - CP \r\n \r\nRenardeau - I. Korschunow - L''école des loisirs, Mouche de poche\r\n - CP CE1 \r\n \r\nValise thématique &quot;cochons&quot; \r\n - CP \r\n \r\nViens jouer avec moi, Petite Souris! - R. Kraus - L''école des loisirs\r\n - GS CP\r\n \r\n \r\n  \r\n \r\n Pour le cycle III   Niveau \r\n    \r\nDans les cheminées de Paris - M.C.Helgerson- Flammarion, Castor poche  - CM2 \r\n \r\nFabrice et les passeurs de l''ombre - J. Cazalbou - Flammarion, Castor poche  - CM \r\n \r\nFantastique Maître Renard - R. Dahl – Gallimard\r\n - CE2 CM1 \r\n \r\nJe n''aurai plus jamais de chien - J. Resh Thomas - L''école des loisirs\r\n - CE2 CM1 \r\n \r\nL''arbre généreux - S. Silverstein - L''école des loisirs\r\n - Cycle 3\r\n \r\nL''inondation - P. Oldfield - Flammarion\r\n - CE2 \r\n \r\nL''oeil du loup - D. Pennac - Pocket Junior\r\n - CM2 \r\n \r\nLa bête du Gévaudan - J. Féron Romano - Hachette, Le livre de poche jeunesse \r\n - CM 6° \r\n \r\nLa nuit du Boufadou - G. Pussey - Ecole des loisirs\r\n - CE2 \r\n \r\nLa petite marchande d''allumettes - Andersen - Nathan  - Cycle 3 \r\nLa vallée des mammouths - M. Peyramaure - Pocket Junior\r\n - CM2 6° 5° \r\nLa princesse de neige  - S Giral, P Nottet - L''école des loisirs - CM2 6° \r\nLe cabanon de l''oncle Jo - B. Smadja - L''école des loisirs - CM \r\nLe guerrier et le sage - D. Winiesky - L''école des loisirs\r\n - Cycle 3\r\n \r\nLe hollandais sans peine -  M.-A. Murail - L''école des loisirs\r\n - CE2 \r\n \r\nLe jobard - M. Piquemal - Milan \r\n - CM\r\n \r\nLe roi de la forêt des brumes - M. Morpurgo - Gallimard \r\n - CM 6° \r\n \r\nLes doigts rouges -  M. Villard - Pocket Junior, Mini souris noire \r\n - CE2\r\n \r\nLes souris de Sansonnet - D. King Smith - Gallimard, Lecture junior\r\n - CE2 \r\n \r\nMomo, petit prince des bleuets - Y. Hassan - Pocket Junior\r\n - CE2  CM \r\n \r\nMon bel oranger - J. Mauro de Vasconcelos - Hachette, Histoires de vie \r\n - CM2 6° 5° \r\n \r\nMusée Blues - S. Morgenstern, J. Claverie - Gallimard, Folio cadet \r\n - CM \r\n \r\nVerte - M. Desplechin - L''école des loisirs\r\n - Cycle 3\r\n \r\nUne Histoire à 4 voix - A. Browne - L''école des loisirs - Cycle 3', '', '  ', 'http://crdp.ac-bordeaux.fr/cddp33/attirelire/somatirelire.asp', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (447, 1, '2006-07-30 18:19:22', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 8 ', ' 3 ', 'Le site &quot;Ricochet&quot; pour la littérature Jeunesse', 'Le site très complet du Centre international d''études en littérature de jeunesse : à la fois portail et ressource, il réunit des actualités, des bibliographies, des articles sur la littérature de jeunesse, les auteurs, les illustrateurs, les éditeurs...', 'Ricochet', ' littérature jeunesse  auteurs  les illustrateurs éditeurs ', 'http://www.ricochet-jeunes.org/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (448, 1, '2006-07-30 18:23:56', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 8 ', ' 3 ', 'La maison des illustrateurs', 'La Maison des illustrateurs est un lieu de ressources, d''information et de promotion de l''illustration pour la jeunesse. Les illustrateurs sont reçus et accompagnés dans leurs démarches, soit administratives, soit artistiques auprès des éditeurs et des organisateurs d''événements culturels.Annuaire des illustrateurs. Possibilité de trouver des références et des adresses pour contacter les illustrateurs.', 'Association &quot;maison des illustrateurs&quot;', ' livre illustrateurs ', 'http://www.lamaisondesillustrateurs.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (449, 1, '2006-07-30 18:28:47', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 8 ', ' 3 ', 'Livralire un site  pour la connaissance pratique sur la littérature-Jeunesse', 'Un site pour la connaissance pratique sur la littérature-Jeunesse. Association &quot;LivraLire&quot;', 'Association &quot;LivraLire&quot;', ' littérature ', 'http://www.livralire.org/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (450, 1, '2006-07-30 18:34:18', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 8 ', ' 3 ', 'Site &quot;leplaisir.net&quot; pour les livres enfance', 'Site de &quot; référence &quot; animé par Jean-Paul Gourevitch, spécialiste de l''histoire et du patrimoine du livre d''enfance.\r\n Carnet d''adresses des gens du monde de la littérature enfantine : auteurs, illustrateurs, éditeurs, spécialistes et formateurs, organisateurs de salons, de prix et d''expositions. Panorama historique de la production littéraire pour la jeunesse : répertoire des titres de presse de 1850 à nos jours et textes de référence de la L-J.\r\n Bibliographies professionnelles : ouvrages et numéros spéciaux de revues sur auteurs contemporains ou classiques, sur des thématiques. Une banque de données, de références, et de liens, concernant le thème de la littérature Jeunesse', 'Gourevitch', ' Gourevitch littérature  auteurs  illustrateurs  éditeurs bibliographie livres ', 'http://www.leplaisir.net/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (451, 1, '2006-07-30 18:45:49', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 8 ', ' 3 ', 'Association &quot;Lire et faire lire&quot;', 'L’association Lire et faire lire créée fin 1999 a pour objet statutaire &quot;d’encourager et de développer toutes initiatives citoyennes de nature à promouvoir et développer le goût de la lecture&quot;.', 'Association &quot;Lire et faire lire&quot;', ' lire lecture association ', 'http://www.lireetfairelire.org/LFL/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (452, 1, '2006-07-30 20:13:33', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 2 3 11 22 ', ' 1 ', 'Le Chinois dans tous ses états', 'Méthode pour apprendre le Chinois.Cette méthode originale d’apprentissage du chinois s’inscrit dans les différents programmes d’enseignement des langues vivantes, école, collège, lycée et enseignement supérieur.\r\nElle convient également pour l’apprentissage hors du milieu éducatif.\r\nTous les domaines de compétence sont couverts : comprendre et parler le putonghua, savoir lire et écrire.', 'CRDP de Nantes', ' LV lanque chinois méthode ', 'http://www.crdp-nantes.fr/service/vente/chinois/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (453, 1, '2006-07-30 20:35:21', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 2 ', 'Éducation aux risques - Les usages de l''Internet au sein de l''établissement scol', 'Des repères et élements pratiques (textes officiels, dispositifs de protection et filtrage, d''éducation et de formation).\r\nDes liens spécifiques vers les structures et ressources de l''Académie de Nantes.\r\nDes références au vocabulaire de l''informatique et de l''internet en usage (glossaire)', 'CRDP de Nantes', ' juridique protection filtrage textes officiels ', 'http://www.crdp-nantes.fr/ressources/document/education_risques/index.htm', 'fr.png', 'N', 0, 'O');
