INSERT INTO `resource_online` VALUES (457, 1, '2006-07-30 20:55:01', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Jeux de lecture interactifs  sur l''histoire de la petire poule rouge pour le cyc', 'Les élèves peuvent choisir de lire  l''histoire et de faire un exercice.\r\nExercice 1: Ecrire des mots et une phrase \r\nExercice 2: Questions sur le texte \r\nLes exercices sont auto-correctifs', 'Pujol Y', ' lecture:texte compréhension correction corrigé logiciel gratuit ', 'http://pujol.club.fr/exercices/qcm/index.htm', 'fr.png', 'N', 0, 'O');
