INSERT INTO `resource_online` VALUES (460, 1, '2006-07-30 21:13:03', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel pour la lecture de mots et de textes pour le CP ou le CE1', 'Exercices interactifs en ligne pour la lecture de mots ou de textes.Corrections et scores', 'Yvan Pujol', ' logiciel lecture CP CE1 cycle2 correction score ', 'http://pujol.club.fr/exercices/ratus_exercices/entreelecture.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (461, 1, '2006-07-30 21:17:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Jeux de mathématiques  en ligne de Y Pujol', 'Jeux mathématiques\r\nCompter avec les doigts de la main\r\nCompter avec des dominos \r\nA. Dominos pour dénombrer\r\nB. Dominos cubains pour dénombrer \r\nC. Comparer  des quantités \r\nComparaison (&lt;,&gt; ou=) des nombres entiers naturels \r\nDécodage de nombres en base 10 \r\nLecture de nombres en lettres \r\nLes tables de Pythagore \r\nLabyrinthes(à  imprimer) \r\n Les tours pour jouer avec les déplacements codés \r\nRévision des tables', 'Yvan Pujol', ' exercices calcul numération opérations tables ', 'http://pujol.club.fr/exercices/sommaire.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (462, 1, '2006-07-30 21:22:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel pour réviser les tables de pythagore et les tables de multiplication', 'Logiciel avec des exercices interactifs en ligne.Evaluation\r\nRésultats affichés avec score de réussite', 'Yvan Pujol', ' exercices table multiplication pythagore gratuits addition score évaluation ', 'http://pujol.club.fr/exercices/tables/entree.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (463, 1, '2006-07-30 21:30:37', '0000-00-00 00:00:00', 0, 0, ' ', ' 8 ', ' ', ' 4 ', 'Gex-zo un logiciel libre  &quot;Générateur d''exercices auto-correctifs &quot;', 'Gex-zo est un Générateur d''exercices auto-correctifs à Zones de saisie gratuit, sous licence Open Source GPL. \r\nIl est utilisable en ligne mais aussi hors connexion et est optimisé pour Internet Explorer 5. \r\nIl permet de générer rapidement des &quot;exercices à trous&quot; auto-corrigés utilisables sur un poste isolé, un réseau intranet ou sur un site Web', 'Yvan Pujol', ' logiciel exercices exerciseurs libre gratuit ', 'http://pujol.club.fr/generateurs/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (464, 1, '2006-07-30 22:07:38', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' ', ' 8 ', ' 1 ', 'Sélection documentaire sur le thème de l''abécédaire', 'Document du CRDP de Nantes. Bibliographie sur des ouvrages parlant d''Abécédaire', 'CRDP Pays de la Loire', ' abécédaire  bibliographie lettre code ', 'http://www.crdp-nantes.fr/ressources/selection/abecedaire.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (465, 1, '2006-07-30 22:09:30', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 4 ', ' 8 ', ' 1 ', 'Dossier thématique pour réaliser un abécédaire photographique', 'Dossier thématique qui permet de réaliser un abécédaire photographique de lettres ou de chiffres en prenant appui sur l''environnement quotidien des élèves : pistes de travaux, approche du droit à l''image, témoignages pédagogiques, webographie.En repères, quelques pistes pour aborder la création d’abécédaires ou d’imagiers avec les élèves avec un rappel des droits de l''image. Des témoignages d''activités pédagogiques (alphabetville en photos) menées dans le cadre de classes à PAC et d''un concours scolaire. Une sitographie pour travailler sur le quartier et réaliser des abécédaires à l''école', 'CRDP Pays de la Loire', ' abécédaires lettre photo photographie ', 'Dossier thématique pour réaliser un abécédaire photographique de lettres ou de chiffres', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (466, 1, '2006-07-30 22:14:40', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 1 ', ' 8 ', ' 1 ', 'imagiers et abécédaires', 'Pour aborder la création d’abécédaires ou d’imagiers avec les élèves au cycle 1.', 'CRDP Pays de la Loire', ' imagiers abécédaires ', 'http://www.crdp-nantes.fr/ressources/dossier/ABCDAIR/Imagiers_reperes.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (467, 1, '2006-07-30 22:18:03', '0000-00-00 00:00:00', 0, 0, ' ', ' 7 ', ' ', ' 2 ', 'Photographie et droits de l''image', 'Site juridique d''Educnet', 'Ministère EN', ' photographie  droit image ', 'http://www.educnet.education.fr/juri/photo.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (468, 1, '2006-07-30 22:30:59', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 1 ', 'Méthodologie du reportage vidéo', 'Guide méthodologique pour intégrer la vidéo en classe au cycle 2 ou cycle 3.\r\nComment préparer et filmer une interview.', 'CRDP de Loire Atlantique', ' vidéo démarche guide film ', 'http://www.crdp-nantes.fr/ressources/document/reportage_video.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (469, 1, '2006-07-30 22:33:18', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 3 ', 'Sélection documentaire sur le thème du cinéma', 'Sélection documentaire sur le thème du cinéma', 'CRDP Pays de la Loire', ' dcuments bibliographie cinéma ', 'http://www.crdp-nantes.fr/ressources/selection/cinema_techniques.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (470, 1, '2006-07-30 22:34:28', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 9 ', ' 3 ', 'Etudier l''antiquité à travers le roman historique', 'Choisir un roman historique au cycle 3 pour traiter le thème de l''antiquité.', 'CRDP Pays de la Loire', ' antiquité histoire roman ', 'http://www.crdp-nantes.fr/ressources/selection/antiquite_roman_historique.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (471, 1, '2006-07-30 22:39:50', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 3 ', 'Rechercher des informations sur Internet', 'Présentation   Comment s''organiser ?  Des informations sur les  annuaires, les moteurs, les méta-moteurs .Des conseils pratiques.Pour des besoins spécifiques ou pour élargir son champ d''investigation, il est intéressant de consulter certains répertoires organisés. Une sélection commentée de ressources accessibles pour Internet [BNF] \r\nTous les outils en une page&quot; de Jean-Pierre Lardy [ADBS]', 'CRDP Pays de la Loire', ' moteur recherche internet annuaires ', 'http://www.crdp-nantes.fr/ressources/document/outils_internet/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (472, 1, '2006-07-30 22:50:55', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 18 ', ' 3 ', 'La laïcité à l''école', 'Textes généraux, ressources, bibliographie', 'CRDP Pays de la Loire', ' bibliographie ressources textes laïcité ', 'http://www.crdp-nantes.fr/Une/laicite/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (473, 1, '2006-07-30 23:01:06', '0000-00-00 00:00:00', 0, 0, ' ', ' 12 ', ' ', ' 3 ', 'Inviter un auteur en classe', 'Des conseils pour préparer (avant), accompagner (pendant) et prolonger (après)\r\nune rencontre avec un écrivain, poète, romancier en classe\r\n(correspondance, atelier d''écriture ...)', 'CRDP Pays de la Loire', ' auteur rencontre livre lecture ', 'http://www.crdp-nantes.fr/ressources/dossier/inviter_auteur/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (474, 1, '2006-07-30 23:03:31', '0000-00-00 00:00:00', 0, 0, ' ', ' 1 ', ' ', ' 1 ', 'Des activités autour d''une exposition', 'Des pistes d''activités autour du montage d''exposition. Poésies\r\nProposer des activités mêlant poésie, analyse d''images, mise en forme du texte et jeux de mots à partir d''une exposition . Fiches d’activités pour les élèves de maternelle et école élémentaire .', 'CRDP Pays de la Loire  l''Inspection Académique de Loire Atlantique et la Maison de la Poésie.', ' exposition poésies ', 'http://www.crdp-nantes.fr/ressources/document/poesie_saffiche/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (475, 1, '2006-07-30 23:26:24', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Takatrouver : un annuaire de recherche pour enfants pour Internet', 'Takatrouver : Un annuaire de recherche pour enfants', 'association Takatrouver', ' moteur annuaire recherche internet ', ' http://www.takatrouver.net', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (476, 1, '2006-07-31 12:07:07', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 8 ', ' 10 ', ' 4 ', 'Atellier de calligraphie d''Emmanuel SPAETH', 'Mise en place de 13 ateliers de calligraphie avec des techniques illustrées.Matériel et techniques.\r\nDes exemples de créations et des galeries.Banque d''alphabets &amp; ductus originaux téléchargeables \r\nDes liens et une bibliographie sur la calligraphie. Des ductus d''alphabets calligraphiés à télécharger. Possibilité d''écrire au calame en direct sur l''écran avec logiciel de calligraphie direct en ligne. Utiilisation d''un traducteur en hiéroglyphes, écrire au pinveau en direct.Découverte active des principes de typographies.Un forum en ligne sur la calligraphie. Ce site est une merveille ...', 'Emmanuel SPAETH', ' écriture calligraphie typographie alphabets lettre arts visuels techniques hiéroglyphes ', 'http://www.atelier-calligraphie.com/calligraphie1.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (477, 1, '2006-08-01 08:07:29', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel Baby Majhong pour des jeux de lecture au cycle 2', 'Le but de ce jeu est de constituer des paires :\r\n Images / Lettre initiale du mot\r\n- Lettre majuscule / Lettre minuscule\r\n- Image / Image (mots ayant la même initiale)\r\nPour cycle 2 et notamment le CP\r\nLogiciel gratuit PC', 'Laurent CHRETIEN', ' lecture jeux lettres CP gratuit freeware mémoire ', 'http://freeperso.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (478, 1, '2006-08-01 08:11:05', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel de Memory de Lybil BER pour le cycle 2', 'Retrouver les paires de lettres dissimulées sous les briques\r\nLogiciel gratuit PC pour le cycle 2 et notamment le CP et GS', 'Lybil BER', ' logiciel lecture lettres gratuit freeware GS CP ', ' http://lybil.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (479, 1, '2006-08-01 08:15:16', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel Tip Tape pour reconnaître les lettres et les chiffres au Cycle 1', 'Des lettres et chiffres mobiles s''affichent à l''écran, il faut les effacer en appuyant sur les touches correspondantes du clavier.Logiciel gratuit PC pour la GS, cycle 1, cycle 2, la maternelle', 'Anne SCOLÉ', ' logiciel lecture CP GS cycle2 cycle1 MS maternelle ', 'http://perso.wanadoo.fr/adajls/softedu.html 	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (480, 1, '2006-08-01 08:20:03', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel &quot;Virtuose du clavier&quot; pour apprendre à utiliser le clavier au', 'Virtuose du clavier permet d''apprendre l''utilisation du clavier. Un mot est tiré au hasard parmi une liste de mots pré-enregistrés. Il est possible de modifier cette liste ou d''en créer d''autres. L''enfant doit recopier le mot qui s''affiche.\r\nDans le mode &quot;pas à pas&quot; la touche du clavier est indiquée . On peut modifier ce paramètre : - indication rapide de la touche ou -pas d''indication.Logiciel ouvert gratuit PC pour le cycle 1 ou cycle 2', 'Lybil BER', ' logiciel lecture écriture lettres maternelle cycle1 cycle2 gratuit freeware GS CP ', 'http://lybil.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (481, 1, '2006-08-01 08:24:35', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel Clavier pour s''entraîner à chercher les touches du clavier au cycle 1 o', 'Un jeu pour s''entraîner à chercher les touches du clavier, (temps règlable).9 exercices différents : Alphabet minuscules-majuscules-les deux, Mots jusqu''à cinq lettres-plus de cinq lettres, Nombres de 1 à 4 chiffres, adaptables au niveau de chacun.\r\nLogiciel ouvert gratuit PC pour le cycle 2 et cycle 1,CP,GS', 'Jean-Luc GRENARD', ' lecture lettre alphabet logiciel gratuit freeware ', 'http://jlgrenar.free.fr/  	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (482, 1, '2006-08-01 08:28:21', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel mariages pour  &quot;marier&quot; la majuscule avec sa minuscule au cyc', 'Logiciel pour  &quot;marier&quot; la majuscule avec sa minuscule. Déplacement de la minuscule bouton gauche, placement de la minuscule bouton droit.Logiciel gratuit pour la maternelle;MS,GS et le CP', 'Didier BIGEARD', ' logiciel lecture code alphabet lettre majuscule minuscule gratuit freeware cycle1 CP ', 'http://perso.wanadoo.fr/dbigeard/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (483, 1, '2006-08-01 08:31:36', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel Mémory Lettres pour apprendre le nom des lettres en maternelle ou au CP', 'Pour apprendre le nom des lettres. Plusieurs niveaux. Affichage des lettres en minuscules, majuscules, cursives. Les consignes, les lettres sont sonorisées.Le paramétrage s''effectue par l''appui de la touche F2.\r\nLogiciel gratuit PC pour la maternelle GS, MS ou le CP', 'Daniel Bouziat', ' lecture lettre code logiciel CP GS MS CP majuscule minuscule cursive ', 'http://itineduc.free.fr/telecharg.htm 	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (484, 1, '2006-08-01 08:36:07', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel ABCDaire pour le cycle 1 ou cycle 2', 'Reconstituer les phrases dont les mots ont été mélangés. Les phrases ou des titres d''albums sont saisies dans un fichier texte (format .txt).Logiciel ouvert gratuit, pour la maternelle ou le cycle 2.', 'Lybil BER', ' lecture jeux lettres CP GS maternelle gratuit freeware ouvert ', 'http://lybil.free.fr/  	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (485, 1, '2006-08-01 08:42:17', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel de lecture &quot;Paires&quot; pour le  CP ou la GS', 'Le logiciel Paires permet de créer des associations, de mettre par deux des étiquettes. Un éditeur est fourni dans le logiciel et vous permet d''adapter le corpus de mot. L''écriture est plus grosse que dans le logiciel &quot;paires&quot;, et un menu permet de changer la graphie (cursive ou scripte) : on peut mettre 2 mots identiques, script à gauche et cursive à droite...Il y a des variations possibles pour réaliser les paires.Jusqu''à 9 paires simultanées, les élèves peuvent également faire les exercices eux-mêmes pour leurs camarades.', 'Daniel GROS', ' lecture jeux lettres CP GS maternelle gratuit freeware ouvert ', 'http://http://dan.gros.free.fr/logiciels/Logiciels.html 	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (486, 1, '2006-08-01 08:48:50', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel &quot;CacheMot &quot; pour la  maternelle GS ou le CP', 'Écrire un mot avec pour modèle sa silhouette. Les listes de mots sont saisies dans un fichier texte (format .txt). Chaque liste doit obligatoirement contenir un minimum de dix mots.\r\nLogiciel ouvert gratuit pour la MS,GS et le CP', 'Jean-Luc GRENARD', ' lecture écriture mot lettres MS GS CP logiciel ouvert gratuit freeware ', 'http://jlgrenar.free.fr/  	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (487, 1, '2006-08-01 08:52:41', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel du jeu du pendu pour le cycle 2 et le cycle 3', 'Logiciel &quot;ouvert&quot;. Les listes de mots sont saisies dans un fichier texte (format .txt) . L''extension .txt de chaque fichier doit être changée en .pdu. Logiciel ouvert gratuit pour le cycle 2 voire cycle 3  permettant de travailler l''orthographe sur des mots étudiés en classe.', 'Jean-Claude MEIER', ' lecture orthographe lexicale logiciel gratuit freeware cycle2 cycle 3 ', ' http://jc.meier.free.fr', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (488, 1, '2006-08-01 09:11:43', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel &quot; mémory&quot; pour jouer au mémory ou apprendre une liste de mots', 'Jeu de mémoire. Permet de jouer seul au mémory avec des images, des mots ou des nombres.\r\nPossibilité d''ajouter vos listes d''images ou de mots personnelles. Adaptation possible en fonction des mots et des répertoires de la classe.', 'Jean-Luc GRENARD', ' logiciel lecture CP CE1 cycle2 correction ouvert mémoire CP GS CE1 CE2 gratuit freeware ', 'http://jlgrenar.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (489, 1, '2006-08-01 09:15:47', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel MémoryX pour le cycle2 et cycle 3', 'Jeu de Mémo textuel. Les listes de mots sont saisies dans un éditeur de textes et doivent obligatoirement contenir dix paires de mots. Le mot de passe pour accéder à l''éditeur est : &quot;Maître&quot; . Possibilité d''adapter le jeu en fonction de la progression de la classe.', 'Yvan PUJOL', ' logiciel lecture CP CE1 cycle2 cycle3 orthographe ouvert mémoire CP GS CE1 CE2 gratuit freeware ', 'http://perso.club-internet.fr/pujol/sommaire.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (490, 1, '2006-08-01 09:20:37', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel &quot;Ecrivons&quot; un traitement de textes simplifié pour le cycle 1', 'Écrivons est un traitement de texte simplifié pour les enfants de cycle 1 et cycle 2 . Il ne propose que 3 polices, correspondants aux trois graphies utilisées à l''école maternelle. Par défaut, l''enfant écrit en majuscules d''imprimerie et retrouve donc les lettres du clavier de l''ordinateur. Il peut également choisir de voir et imprimer son texte en scripte ou en cursive. Les autres fonctions disponibles sont l''agrandissement et la réduction de la taille du texte.\r\nLogiciel gratuit PC', 'Pierre NOGUER', ' traitement de textes écriture maternelle C2 CP GS gratuit freeware ', 'http://pnoguer.free.fr/ 	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (491, 1, '2006-08-01 09:26:09', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel SupraLec pour augmenter la vitesse de lecture au cycle 2 et au cycle 3', 'Un logiciel pour augmenter la vitesse de lecture et mesurer les progrès réalisés. Il est ouvert et adaptable aux textes de la classe. N''importe quel type de fichier texte (format .txt) peut être utilisé. De nombreuses possibilités de paramétrage sont offertes : vitesse de défilement, taille des caractères, couleurs du fond et du texte, choix de la fonte ...Il est livré avec plusieurs textes mais il est possible de saisir ses propres textes.\r\nLogiciel ouvert en libre essai limité dans le temps - licence 17,50 euros sur site 35 euros', 'Jean-Claude MEIER', ' lecture exercices vitesse cycle2 cycle3 CP CE1 CE2 CM1 CM2 payant:mesures progrès ', 'http://jc.meier.free.fr', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (492, 1, '2006-08-01 09:31:37', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel de lecture &quot;lectra&quot; pour générer des exercices au cycle 2 et', 'L''enseignant propose des textes avec de simples fichiers texte (format .txt). Lectra génère les exercices d''entraînement suivants : closure, reconstitution, mots outils, mots éclairs, phrase mélangée, phrase incomplète, phrases sans espaces, lecture rapide, mots identiques, l''intrus, ordre alphabétique, memory mots, texte mélangé, texte à corriger. Enregistrement des résultats pour chaque élève. Le programmme existe en trois versions PC : Lectra 16 bits (Windows 3.1) - Lectra 32 bits (Windows 9x, XP), Lectra + synthèse vocale (Windows 9x, XP) et une version MAC (sans synthèse). Logiciel en libre essai - License établissement 22,87 euros et 38,11 euros pour Lectra + synthèse vocale. Les versions en libre essai (Lectra 16, Lectra 32) ne sont pas limitées dans le temps.', 'BRUN-VILLANI', ' lecture exercices closure reconstitution logiciel gratuit payant ouvert résultats progrès score ', 'http://www.lectramini.com/lectra.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (493, 1, '2006-08-01 09:43:39', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel de lecture &quot;Associations&quot;  d''AbulÉdu pour le cycle 1 et le cy', 'Le logiciel Associations propose de nombreuses activités d''entraînement à la lecture : J''entends / Je n''entends pas, J''entends (Début - Milieu - Fin) J''entends (quelle syllabe?)-Discrimination visuelle : lettres phonèmes ou mot-Reconnaître une image, plusieurs étiquettes /plusieurs images, une étiquette Remettre les syllabes en ordre, les lettres en ordre\r\nEcrire Dictée visuelle Dictée. Logiciel ouvert libre gratuit sous licence GNU/GPL PC Linux/Windows/Apple. \r\nPour en savoir plus. Voir l''espace premier degré du pôle de compétences des logiciels libres.http://www.logiciels-libres-premierdegre-sceren.fr/\r\nLe logiciel Association permet d''enregistrer les résultats nominatifs obtenus pour les différents exercices.', 'AbulEdu et son équipe', ' logiciel lecture CP CE1 cycle2 dictée libre gratuit progrès résultats ', 'http://www.abuledu.org/leterrier/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (494, 1, '2006-08-01 11:21:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 19 ', ' 4 ', 'Logiciel Gcompris', 'GCompris est un logiciel éducatif qui propose des activités variées aux enfants de 2 à 10 ans.\r\nVous trouverez des activités dans les domaines suivants :\r\ndécouverte de l''ordinateur : clavier, souris, les mouvements de la souris\r\nmathématiques : révision des tables, dénombrement, les tables à double entrée, symétrie\r\nsciences : l''écluse, le cycle de l''eau, le sous-marin\r\ngéographie : placer les pays sur une carte\r\njeux : des casses têtes, les échecs, le memory\r\nlecture : exercice d''entraînement à la lecture\r\nautres : lecture de l''heure, peintures célébres sous forme de puzzle, dessin vectoriel\r\nLogiciel ouvert libre sous la licence GPL . Gcompris fonctionne sur GNU/Linux, MacOSX, Windows et d’autres versions d’Unix. (40 mo) licence 20 euros', 'Bruno Coudoin', ' mathématiques sciences géographie jeux lecture peintres arts libre ', 'http://gcompris.free.fr/rubrique.php3?id_rubrique=1', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (495, 1, '2006-08-01 11:29:18', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel 1000 MOTS pour apprendre à lire', 'Le logiciel propose  un corpus de 1200 mots liés à autant d''images et autant de sons. A ces mots sont associés 14 types d''exercices, eux-mêmes gradués en plusieurs niveaux de difficulté (3 au maximum) ; ainsi chaque élève a-t-il la possibilité d''avancer à son rythme et selon ses possibilités tout en suivant la progression de la classe. Les exercices sont regroupés en trois grandes familles :les exercices d''écoute, les exercices de reconnaissances basés sur la recherche d''indices, les exercices de combinatoire.Le logiciel permet de plus une gestion de groupes de niveaux utilisant éventuellement des méthodes différentes. La progression est automatique. Un suivi sous forme de bilan individuel détaillé est également à la disposition du maître. L''enseignant peut  modifier la progression proposée pour l''adapter à ses élèves ou à la méthode utilisée. Licence 16 euros licence établissement 26 euros', 'Campaner Jean-Marc', ' logiciel ouvert payant lecture langues bilan progrès langue lecture ELCO ', 'http://perso.wanadoo.fr/jm.campaner/  	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (496, 1, '2006-08-01 11:32:40', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel de lecture &quot;Textes et questions&quot; du cycle 2 au cycle 3', 'Lecture et compréhension de textes sous forme de questionnaires à choix multiple. Il est possible de saisir ses propres textes et questionnaires (format .txt) lire le fichier Manuel.txt. Impression des résultats.\r\nLogiciel ouvert qui peut être adapté aux mots de la classe.', 'Yvan PUJOL', ' logiciel gratuit ouvert lecture compréhension questionnaires freeware ', 'http://perso.club-internet.fr/pujol/sommaire.html  	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (497, 1, '2006-08-01 11:36:05', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Puz-Mat, un générateur de puzzles pour la maternelle et le cycle 2', 'A partir de n''importe quelle image ou photo scannée, réalisez facilement un puzzle de 4 à 36 pièces, avec ou sans modèle, avec ou sans rotation des pièces, jouez à l''écran, ou bien imprimez-le à la taille de votre choix pour en faire un puzzle carton. Ce logiciel n''est pas directement lié à la lecture, mais il permet de créer facilement des &quot;puzzles de mots&quot;, phrases, textes ... à partir d''un fichier image.Le logiciel est adaptable aux projets de la classe.', 'Christian VINENT', ' logiciel gratuit ouvert lecture puzzle freeware générateur exerciseur ', 'http://primtice.free.fr/puzmat/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (498, 1, '2006-08-01 11:39:42', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 2 3 11 22 ', ' 4 ', 'Des imagiers en ligne', 'Une trentaine d''imagiers sonorisés (alphabet, couleurs, aliments, jours de la semaine, mois, saisons, sport, corps, visage, animaux, transports etc...) à utiliser directement en ligne.', 'Site &quot;imagier&quot;', ' langage vocabulaire imagiers thèmes FLE ELCO lexique logiciel gratuit ', 'http://www.imagiers.net  	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (499, 1, '2006-08-01 11:43:53', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 2 3 11 22 ', ' 4 ', 'Le logiciel Lexirama du cycle 2 au cycle 3', 'Le logiciel permet d''associer un mot à une image et l''écrire correctement. Lexirama gère une base lexicale de 480 mots extensible par l''enseignant(e) selon les thèmes ou les sons abordés en classe. Une fois la liste des élèves remplie, Lexirama peut évaluer inviduellement les élèves et dresser pour chacun la liste des mots erronés. Résultats imprimables. Le logiciel peut être adapté aux situations de classe. Peut servir en classe de langue ou pour l''ELCO. Utilisable dès la maternelle.', 'Dominique BILLE', ' langue vocabulaire lexique logiciel gratuit maternelle GS CP CE1 cycle2 cycle1 ', ' http://perso.wanadoo.fr/logoplus/logiciels_educatifs.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (500, 1, '2006-08-01 11:52:40', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel &quot;Recopie&quot; pour apprendre à copier', 'Logiciel d''apprentissage de l''écriture. Le but du jeu est de recopier une phrase. Des options permettent d''augmenter la difficulté de l''exercice en masquant une voyelle, le mot en cours de frappe ou la phrase originale. Si l''enfant ne reconnaît pas une lettre, il peut afficher un cartouche lui présentant cette lettre en majuscule et minuscule ainsi qu''en écriture cursive. L''enseignant peut personnaliser la liste des phrases. Le logiciel est ouvert et adaptable.La liste des phrases est entièrement personnalisable. Logiciel. Visualisation du score et des résultats.gratuit. Evaluation', 'Ludovic THEBAULT', ' écriture copie orthographe gratuit scores résultats évaluation ', 'http://ludovic.thebault.free.fr/edu.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (501, 1, '2006-08-01 13:03:11', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel  &quot;clavier&quot; pour des des exercices de copie ou de dictée au cy', 'L''activité proposée consiste à réécrire des mots qui sont donnés. Deux exercices au choix. L''un où l''on clique sur les touches, l''autre où l''on tape ces dernières. Le logiciel est &quot;ouvert&quot; et adaptable en fonction des mots étudiés en classe. Dans le  deuxième exercice, on peut changer les mots grâce au fichier &quot;data&quot; du répertoire d''installation.\r\nLogiciel ouvert gratuit', 'Didier BIGEARD', ' logiciel:ouvert orthographe copie ORL freeware CP CE1 CE2 CM1 CM2 gratuit ', 'http://perso.wanadoo.fr/dbigeard/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (502, 1, '2006-08-01 13:07:03', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel &quot;''aquadictées&quot; pour le cycle 2 et le cycle 3', 'Aquadictées permet à l''élève de recopier des mots ou goupes de mots proposés par l''enseignant. Ces mots ou goupes de mots remontent progressivement l''écran, à une vitesse plus ou moins rapide selon le nombre de lettres et disparaissent. Il est possible de revoir les mots. Logiciel entièrement paramétrable par l''enseignant\r\nLogiciel ouvert gratuit', 'Georges JACQUEMETTAZ', ' logiciel ouvert copie orthographe CP CE1 CE2 CM1 CM2 cycle2 cycle3 ', 'http://monthey.ecolevs.ch/Jacquemettaz/Aquadictees/aquadict.htm   	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (503, 1, '2006-08-01 13:09:58', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel &quot;les Orthonautes&quot; pour le cycle 2 et le cycle 3', 'Ce logiciciel propose aux élèves de recopier tous les mots du vocabulaire de base, classés par noms, verbes, adjectifs, autres mots, phrases. Les mots/goupes de mots, disparaissent progressivement à l''écran. Il est possible de revoir les mots. Ces mots sont issus de la répartition suisse romande du vocabulaire à étudier à l''école primaire. Logiciel entièrement paramètrable. Adaptable au niveau et au contexte de la classe \r\n\r\n\r\n \r\n\r\nLogiciell ouvert gratuit', 'Georges JACQUEMETTAZ', ' logiciel ouvert gratuit freeware copie orthographe ', 'http://monthey.ecolevs.ch/Jacquemettaz/Ortho/ortho.htm  	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (504, 1, '2006-08-01 13:13:17', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel &quot;Discrimots&quot; pour des jeux de lecture au cycle 2', 'Discrimots est un logiciel proposant des jeux de lecture, de discrimination visuelle. Entrainement pour l''utilisation avec la souris.Dans chaque ligne de mots, l&quot;élève doit retrouver celui qui est identique au premier mot. Le logiciel est fourni avec 6 fichiers externes format .txt modifiables à souhait.Ils correspondent aux 6 séries de mots du logiciel.Logiciel ouvert à la progression de la classe.\r\n\r\nLogiciel gratuit', 'mnouvier@club-internet.fr', ' logiciel gratuit exercices lecture ouvert cycle2 CE1 CP ', 'http://www.chez.com/ecoliciel/discrimots.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (505, 1, '2006-08-01 13:16:22', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel de lecture &quot;Jumimot&quot; pour le cycle 2', 'Jeu de lecture, discrimination visuelle\r\nUtilisation avec la souris.\r\nDans chaque tableau , il faut retrouver les paires de mots identiques .\r\nTrois niveau de difficulté et huit tableaux de mots. Utilisable du CP au CE1.', 'mnouvier@club-internet.fr', ' lecture jeux lettres CP GS maternelle gratuit freeware ouvert ', 'http://www.chez.com/ecoliciel/discrimots.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (506, 1, '2006-08-01 13:21:11', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel &quot;Ordinombres&quot; pour le cycle 2', 'Logiciel de numération pour le cycle 2.', 'mnouvier@club-internet.fr', ' mathématiques CE1 numération gratuit freeware logiciel ', 'http://www.chez.com/ecoliciel/discrimots.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (507, 1, '2006-08-01 13:25:04', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel &quot;Ordinombres&quot; pour le cycle 2', 'Logiciel de numération qui permet de travailler sur les comparaisons de nombres de façon ludique.six niveaux de recherche:nombre mystère entre:0 et 10, 0 et 20, 0 et 50,0 et 100, 0 et 250, 0 et 500. Trois niveaux de difficulté:\r\nfacile, moyen et difficile. L''enfant choisit au départ un niveau de difficulté. Pour gagner, il devra trouver le nombre mystère en ne dépassant pas le nombre d''essais proposé.Dans ce cas, il gagne une coupe. A la fin d''une partie complète, le score est affiché. Utilisable à partir du CP', 'mnouvier@club-internet.fr', ' calcul numération cycle2:CP CE1 logiciel gratuit freeware jeux scores réussite ', 'http://www.chez.com/ecoliciel/discrimots.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (508, 1, '2006-08-01 13:29:19', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel  &quot;Operpose&quot; pour la technique de l''additions avec ou sans ret', 'Operpose est un logiciel permettant de s''entraîner sur la technique de l''addition avec ou sans retenue.Pour le CP;CE1', 'mnouvier@club-internet.fr', ' logiciel gratuit exercices calcul opération addition cycle 2 CP CE1 ', 'http://www.chez.com/ecoliciel/discrimots.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (509, 1, '2006-08-01 13:36:09', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 16 ', ' 4 ', 'Logiciel Questions et Réponses V.3a pour le cycle 2 et le cycle 3', 'Questionnaires et exercices élaborés. Création aisée de nouveaux exercices ou questionnaires avec un module de création. Questionnaires ou exercices pour toutes matières.Idéal pour proposer des fiches de lecture ou toutes sortes d''exercices variés. Les questionnaires peuvent être indépendants ou liés à un texte consultable à l''écran ( texte littéraire, règle de grammaire, énoncé,etc...). On peut ajouter un commentaire à chaque question.\r\nLe logiciel contient plusieurs questionnaires dans diverses matières.Utilisable en cycles 2 au au cycle 3.Une gestion très souple des groupes et des résultats.Dix groupes de trente élèves au maximum, peuvent être gérés.\r\nL''enseignant peut consulter  les résultats de chaque groupe ou ceux d''un élève en particulier et  les imprimer. Les dates, résultats et noms de questionnaires sont clairement indiqués. On peut donc contrôler simplement le parcours de chaque élève.', 'mnouvier@club-internet.fr', ' lecture compréhension lecture ouvert gratuit résultats réussite score cycle2 cycle3 ', 'http://www.chez.com/ecoliciel/discrimots.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (510, 1, '2006-08-01 13:55:15', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel  Calcul mental et comparaisons de nombre pour le cycle 2 et le cycle 3', 'Activités proposées : calcul mental avec les quatre opérations de base. Différents niveaux de difficulté pour chaque type d''opération.Comparaison de nombres et de sommes. Gestion optimisée des groupes d''élèves et des résultats.\r\nUtilisable en cycles 2 et 3 et plus... Pour l''enseignant:Une gestion des groupes et des résultats très simple, protégée par mot de passe créé par l''enseignant. Dix groupes, avec un maximum de trente élèves par groupe, peuvent être gérés. L''enseignant peut consulter les résultats individuels ou ceux d''un groupe et  les imprimer. Il peut aussi les archiver dans des fichiers externes ou les supprimer si besoin.  Pour l''élève:sélection de l''opération et du niveau de difficulté. L''élève peut voir et imprimer les résultats de sa séquence de travail.\r\naddition ( 5 niveaux) ajouter 10, 20, ..ajouter 100, 200...	soustraction (3 niveaux); retrancher 10, 20, ..\r\nretrancher 100, 200... 	multiplication ( 2 niveaux); tables; multiplier par 10, 100, 1000', 'mnouvier@club-internet.fr', ' calcul mental comparaison numération logiciel gratuit freeware résultats scores cycle2 cycle3 ', 'http://www.chez.com/ecoliciel/discrimots.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (511, 1, '2006-08-01 14:04:00', '0000-00-00 00:00:00', 0, 0, ' ', ' 12 ', ' ', ' 1 ', 'Mots dissimulés pour le cycle 3', 'Mots dissimulés à imprimer.\r\nMémorisation des noms de pays. Jeu d''attention', 'mnouvier@club-internet.fr', ' texte mots dissimulés exercice ', 'http://www.chez.com/ecoliciel/discrimots.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (512, 1, '2006-08-01 14:09:57', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 16 ', ' 1 ', 'Une simulation d''écluse', 'Animation interactive en ligne permettant de découvrir le fonctionnement d''une écluse', 'Pragmasoft', ' sciences technologie fonctionnement écluse ', 'http://www.pragmasoft.be/carnets/geo/ecluse/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (513, 1, '2006-08-01 14:18:24', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 1 ', 'Logiciel Balamonde pour voyager en hélicopère', '2 100 destinations à localiser en hélicoptère sur la carte du monde ! \r\n(pays, capitales, villes, mers, fleuves, montagnes, lieux historiques, ...)Version complète (10 EUR)', 'Pragma-Soft', ' logiciel payant goégraphie ', 'http://www.pragmasoft.be/balamonde/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (514, 1, '2006-08-01 14:23:38', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 4 ', 'Logiciel avec des jeux de géographie', 'Avec ce logiciel, vous pouvez maintenant jouer avec les fleuves, rivières, lacs, montagnes et sommets de France, la carte du ciel, les pays ou états du monde entier (Europe, Amérique du Sud, Amérique du Nord, Afrique, Asie, ou Océanie), leur capitale et leur drapeau, ainsi qu''avec les départements français, leurs préfectures et leurs sous préfectures', 'Olivier Leflon', ' logiciel gratuit géographie fleuves rivières montagnes sommet pays capitale drapeaux département ', 'http://olivier.leflon.free.fr/jeux/jeux.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (515, 1, '2006-08-01 14:30:47', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 7 ', ' 1 ', 'Logiciel Tuxpyping pour apprendre à utiliser le clavier', 'Le logiciel TuxType permet d’apprendre aux enfants (pas uniquement ;-) à taper au clavier. L''interface s''apparente aux jeux vidéos. Des lettres ou des mots tombent du haut de l''écran, il faut taper ce qui s''affiche pour nourrir le petit pingouin en bas de l''écran. Le logiciel est sonorisé. Il propose plusieurs niveaux de difficulté et un mode d''entrainement (Pratique) qui indique en même temps que la lettre à taper, le doigt à utiliser. Plusieurs langues sont proposées. Au lancement du logiciel, paramétrer le logiciel en français via le menu Options. Logiciel libre gratuit sous licence GNU/GPL PC Linux/Windows MAC', 'Jesse Andrews', ' logiciel libre gratuit lettres ', 'http://tuxtype.sourceforge.net 	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (516, 1, '2006-08-01 14:39:04', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 20 ', ' 1 ', 'Logiciel Arc-En-logicel REPERE', 'REPERE permet de présenter des exercices où l''élève devra indiquer la ou les bonnes réponses à l''aide de la souris.\r\nIl pourra ainsi désigner un mot dans une phrase, un élément de la phrase, une phrase dans un texte ou choisir une réponse parmi celles qui sont proposées.REPERE dispose d''un éditeur puissant et simple d''emploi qui va permettre à chaque enseignant de créer ses exercices et de les modifier.Il est possible de personnaliser chaque exercice en ajoutant par exemple un commentaire associé à une erreur possible. Choix des couleurs, des polices de caractère, tailles modifiables.Logiciel ouvert gratuit PC windows.', 'Arc-En-logicel', ' logiciel gratuit ouvert exercice exerciseur freeware résultats commentaire ', 'http://www.monhebergement.fr/aelc/repere.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (517, 1, '2006-08-01 14:44:22', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel Arc-En-logicel Mélange 2000', 'Mélange 2000 est un outil qui permet de produire des puzzles de Texte. Les exercices qu''il peut créer concernent autant la construction de mots avec un travail sur la correspondance entre son et signe, que la construction de phrases et de textes ; le travail porte alors sur la recherche du sens, du déroulement logique, de la présence d''indices tels que majuscules, ponctuation ou connecteurs. L''enfant est amené à mettre en place une stratégie où la mémorisation occupe une place importante. L''apport du son peut constituer une aide précieuse pour les élèves éprouvant des difficultés. D''utilisateur, l''enfant peut devenir créateur et produire ses propres exercices qu''il pourra échanger ensuite.\r\n\r\nLogiciel ouvert en libre ess', 'Arc-En-logicel', ' lecture écriture exercice exerciseur gratuit freeware phrase texte ', 'http://www.monhebergement.fr/aelc/melange2000.htm  	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (518, 1, '2006-08-01 14:46:47', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel Moi, je sais lire éditeur Club PoM pour le cycle 2', '&quot;Moi, je sais lire&quot; offre une mine d''activités ludiques pour reconnaître les lettres, apprendre les sons, jouer à les assembler, former des mots, et jongler avec les phrases.\r\nCe logiciel, offre de nombreux paramètres (gestion des niveaux, sélection des thèmes à étudier, création d''exercices, suivi des activités, bilans personnalisés,...), il se révèle un outil d''enseignement sur mesure et peut être utilisé avec n''importe quelle méthode de lecture existante. Cdrom compatible Mac et PC Label RIP (Reconnu d''Interêt Pédagogique) 35 euros en version monoposte.', 'ClubPom', ' logiciel exerices lecture cycle2 CP CE1 ', 'http://www.clubpom.fr/nlire.htm 	', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (519, 1, '2006-08-01 15:03:21', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 2 3 11 22 ', ' 4 ', 'Imagiers de Pédagosite', 'Imagiers avec une entrée par thème. Utilisable dans les classes de maternelle, dans les classes de primo-arrivant pour apprendre le lexique des mots. L''imagier n''est pas sonore.On voit apparaître les images et le mot de ce qui est représenté.', 'pédagogite', ' vocabulaire imagiers catégorisation lexique logiciel FLE primo-arrivant ', 'http://pedagosite.net/archives/pedago_imagier.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (520, 1, '2006-08-01 15:06:21', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 8 ', ' 4 ', 'Le petit-déjeuner d''Ali une histoire animée pour la maternelle', 'Pour lire l''histoire d''Ali c''est très simple, dans chaque page qui apparaît à l''écran, clique sur un des objets animés et celui-ci te mènera à la page suivante de l''histoire. Attention, tous les objets ne marchent pas forcement, il faut trouver le bon !', '', ' histoire lecture cycle1 MS GS logiciel gratuit ', 'http://vbelmokhtar.free.fr/ali/page0.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (521, 1, '2006-08-01 15:12:12', '0000-00-00 00:00:00', 0, 0, ' ', ' 2 ', ' ', ' 2 ', 'Site sur la sécurité routière', 'Tests APER ASSR | Fiches pédagogiques | Actions | Articles | Liens\r\nRessources pour les élèves sur la sécurité routière.', 'Ia59', ' sécurité route APER ', 'http://netia59a.ac-lille.fr/~securite-route/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (522, 1, '2006-08-01 15:17:01', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 18 ', ' 1 ', 'Attestations scolaires de sécurité routière de niveaux 1', 'Espace de préparation individuelle des élèves de collège aux épreuves des attestations scolaires de sécurité routière de niveaux 1 et 2 propose plusieurs moyens d''évaluation des connaissances : des fiches thématiques, des questions liées à des vidéos en mode d''entraînement ou de test.Ressources pour les élèves.', 'DESCO;education nationale', ' sécurité route APER ', 'http://eduscol.education.fr/D0187/accueil.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (523, 1, '2006-08-01 15:18:35', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 2 3 ', ' 6 ', ' 1 ', 'La natation à l''école au cycle 2', 'S''appuyant sur les contenus des programmes de 2002 pour la natation à l’école élémentaire, ce cédérom vous propose :\r\n    * 4 situations de référence,\r\n    * plusieurs situations de jeux pour répondre aux besoins d’apprentissage des élèves selon 3 thèmes : immersion, équilibre et propulsion. Dans chaque thème, 3 niveaux d’acquisition sont repérés : du niveau 1 (orange) pour les élèves les moins débrouillés au niveau 3 (vert) pour ceux qui sont le plus à l’aise, en passant par un niveau intermédiaire (jaune),\r\n    * des éducatifs pour renforcer les acquis de chacun, avec des indicateurs visant à orienter l’observation et les conseils prodigués par l’enseignant le cas échéant,\r\n    * 4 tests d’évaluation de fin de module pour des niveaux de classe différents\r\nLe CD-Rom est téléchargeable', 'ia59', ' EPS natation cycle2 CD-ROM programmes 2002 ', 'http://netia59a.ac-lille.fr/~siteia/ressources_peda/eps/cd_natation.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (524, 1, '2006-08-01 15:23:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 3 ', ' 6 ', ' 1 ', 'Les points essentiels des  programmes en EPS pour les 3 cycles', 'Arrêté du 25-1-2002 . JO du 10-2-2002 (BO HS n°1 du 14 février 2002)\r\n\r\n    * Les Objectifs     * La Programmation  *  L''École Maternelle  *  Le cycle 2: Cycle des apprentissages fondamentaux \r\n\r\n   * Le cycle 3: Cycle des approfondissement', 'IA59', ' EPS cycle2 cycle3 programmes 2002 maternelle ', 'http://netia59a.ac-lille.fr/~eps/programme.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (525, 1, '2006-08-01 15:33:25', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 20 ', ' 4 ', 'Site Tralalère pour des activités musicales en ligne', 'Des activités musicales pour les enfants par niveau d''age pour l''école maternelle et aussi pour l''école élémentaire.\r\nSite interactif,sonore.Activités directes en ligne. Le site sera accessible sur abonnement après Septembre 2006', 'site Tralalère', ' musique chants répertoire ', 'http://www.tralalere.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (526, 1, '2006-08-01 15:35:24', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Les jeux de Lulu pour le cycle 2 et le cycle 3', 'Jeux de logique;repérage.Exercices et jeux variés; logique; Le bon rythme;Le bon ordre;Autour du\r\nnombre;numération;jeux de lettre;jeux de mémory;Abécédaire;observations;jeux pour malvoyants.\r\nLogiciels directement accessibles en ligne.', 'Régine des jeux de Lulu', ' calcul lecture cycle2 cycle3 logique repérage logiciel gratuit freeware ', 'http://perso.orange.fr/jeux.lulu/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (527, 1, '2006-08-01 15:53:06', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 5 ', ' 13 ', ' 4 ', 'Le cercle enchanté pour aborder le nombre au cycle 1', 'Le Cercle enchanté s''adresse à l''enfant de quatre et de cinq ans. Au fil des mois, par le biais de jeux tu apprendras à compter.Grâce  à  Mélisse et de Serpolet, tu auras à résoudre une série de trente problèmes simples sur le nombre. Exploitable en MS;GS;évetuellement au CP avec des élèves en difficulté.', 'cercle enchanté', ' calcul numération nombre cycle1 MS GS ', 'http://station05.qc.ca/css/cercle/accueil.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (528, 1, '2006-08-01 15:59:33', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Les jeux éducatifs en ligne de l''Escale', 'Série de jeux de lecture, de mathématiques, quelques suggestions de bricolage pour le temps des Fêtes.  	Français 1. L''alphabet des animaux 	Identifier et comparer les lettres de l''alphabet. 	\r\n2. Pratique d''écriture! Apprendre la façon d''écrire les lettres de l''alphabet. Mathématiques\r\nCalculer des nombres, représentés par des canards, dont la somme est égale ou inférieure à 5. 	Rechercher, sur des animaux, des formes ressemblant au cercle, au triangle et au carré. 	Comparer des images pour y trouver des ressemblances et des différences. Différencier les notions d''intérieur et d''extérieur.  Chants de Noël 	Connaître les chants de Noël les plus populaires /\r\nSciences de la nature Associer des animaux à leur habitat.', 'L''escale', ' jeux biologie lecture alphabet chants bricolage ', 'http://www.lescale.net/4a5ans.htm#francais', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (529, 1, '2006-08-01 16:05:37', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 5 ', ' 9 ', ' 4 ', 'Jeux et activités en ligne sur le site de Toupty pour le cycle 1 et cycle 2', 'Site dédié aux enfants avec des activités diverses et variées en ligne. Puzzles en ligne classés par difficultés. Jeux pour découvrir l''intrus. Jeux d''observation. Grilles de sodoku pour enfants. Jeux sur les chiffres et les nombres.Jeux de mémory. Jeux pour classer les images dans l''ordre chronologique. Jeux de mémoire et de  logique. Mots croisés en ligne pour enfants. Jeux de  lettres. Jeux puissance4, jeu du morpion et taquins de 9, 12 et 16 pièces en ligne. Jeux divers. Activités à imprimer. coloriages à imprimer. Llabyrinthes à imprimer. Labyrinthes. Paroles de chansons', 'Toupty', ' jeux logiciel cycle1 cycle2 exercice labyrinthe morpion puissance4 mots:croisés Sodokou ', 'http://www.toupty.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (530, 1, '2006-08-01 16:10:45', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'Une histoire de chat à découvrir en passant la souris dessus ...et rêver', 'Une belle histoire de chat &quot;à voir&quot; et à découvrir en passant la souris sur certains points de l''écran. Un régal pour les yeux. Un moment pour rêver. Pour tous les élèves qui aiment les oeuvres d''art à l''écran', 'broenink-art', ' histoire arts visuels chat souris ', 'http://www.broenink-art.nl/maukie2.swf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (531, 1, '2006-08-01 16:14:09', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 5 ', ' 9 ', ' 4 ', 'Les jeux en ligne de Picandou pour le cycle 1', 'Jeux pour les élèves du cycle 1 ( maternelle ) et GS :puzzles, tableaux à double entrée,mémory;associations', 'Picandou', ' logiciel puzzles  tableaux  mémory associations  GS MS ', 'http://materalbum.free.fr/puzzles.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (532, 1, '2006-08-01 16:25:35', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 2 3 11 22 ', ' 4 ', 'Le site de la belette pour découvrir une histoire et des illustrateurs', 'Site pour découvrir une histoire animée et sonore. Une version en espagnol est disponible', 'association &quot;la Belette&quot;', ' histoire espagnol langue vivante ', 'http://www.belette.biz/fr.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (533, 1, '2006-08-01 16:28:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'Découvrir une illustratrice de livre', 'Emilie Vansomsem, illustratrice, se présente sur son site adapté aux élèves de l''école élémentaire qui aiment les albums et les arts visuels', 'Emilie Vansomsem', ' littérature illustration histoire images ', 'http://www.emilievanvolsem.info/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (534, 1, '2006-08-01 16:35:29', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'Les jeux de Margouillat du vélo rouge.. des albums animés', 'Des histoires animées à l''écran pour des élèves de l''école maternelle. de 3 à 7 ans ...\r\nou pour tous les âges, si on aime &quot;les belles choses à l''écran&quot;. jeux de couleur;une poule qui perd la boule;Jojo l''ascenceur', 'vélo rouge', ' histoire animation littérature cycle1 maternelle ', 'http://petitvelorouge.free.fr/principal.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (535, 1, '2006-08-01 16:42:05', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 5 ', ' 21 ', ' 4 ', 'Le mémory des sons du petit vélo rouge pour les élèves de maternelle ( Cycle 1 e', 'Associe deux boîtes qui font le même bruit.', 'petitvelorouge.free.', ' musique écoute mémory discrimination auditive cycle1:cycle2 maternelle ', 'http://petitvelorouge.free.fr/Flash/memoSon.swf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (536, 1, '2006-08-01 16:48:17', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 5 ', ' 21 ', ' 4 ', 'L''arbre magique de Tibao .. une histoire à voir et à écouter pour le cycle 1 et', 'Histoire animée à l''écran. Pour le cycle 1 et le cycle 2\r\nElèves de GS;CP;CE1', 'Grégory Korzeniowski 2006', ' histoire animation littérature cycle1 maternelle ', 'http://www.tibao.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (537, 1, '2006-08-01 16:54:11', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'Wismo et le cirque Babum .. une histoire animée du cycle 1 au cycle 3', 'Une histoire animée en ligne pour les élèves du cycle 1 et cycle 2. Peut être utilisée pour des travaux en Arts... Une petite merveille à l''écran à découvrir.Exploitation en graphisme, en langage oral. Les élèves du cycle 3 pourront lire le texte associé à l''histoire. Les personnages sont tellement beaux... il y a là un petit chef d''oeuvre à exploiter. ( Découverte indivisuelle ou en vidéo-projecteur...iy TBI', 'www.wismo.ch', ' histoire langage cycle1 cycle2 récit cycle3 graphismes décors ', 'http://www.wismo.ch/pages/cirque.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (538, 1, '2006-08-01 17:03:12', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 5 ', ' 8 ', ' 4 ', 'Histoire animée de &quot;YO-YO&quot; l''ascenseur', 'Yoyo n''est pas un ascenseur comme un autre. Pour découvrir son secret, il faut cliquer sur des les boutons.Texte à lire au fur et à mesure de la progression de l''histoire.', 'petitvelorouge.free.fr Olivier Douzou', ' histoire arts visuels ascenseur cycle1 cycle2 cycle3 ', 'http://petitvelorouge.free.fr/Flash/yoyo/Master.swf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (539, 1, '2006-08-01 17:09:33', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 5 ', ' 21 ', ' 4 ', 'Histoire animée des &quot;Couleurs de balthazar&quot; pour le cycle 1 et le cycl', 'Histoire animée des &quot;Couleurs de balthazar&quot;. Exploitable pour le jeu des mariage des couleurs.Texte abordable niveau CE1; début CE2', 'petitvelorouge.free.f', ' histoire animation littérature cycle1 maternelle récit arts visuels CP CE1 ', 'http://petitvelorouge.free.fr/Flash/balthazar.swf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (540, 1, '2006-08-01 17:14:33', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Histoires e-stories pour lire de belles histoires animées en ligne au cycle 2 et', 'Tu peux choisir 16 histoires à lire en ligne. Le texte n''est pas trop difficile si tu es au CE1 ou au CE2. Les histoires sont animées et sont très bien illustrées.', 'e-stories', ' récits histoires animation ', 'http://www.e-stoires.net/vignette/Vignette.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (541, 1, '2006-08-01 17:17:48', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Le bestiaire poétique pour le cycle 2, cycle 3', 'Un atelier de découverte, d''écriture et de jeux autour de la poésie.\r\nDécouvrir les grands poètes et inventer des poèmes.Clique sur les lettres pour découvrir les animaux et leur poème, ainsi que les poésies créées par les enfants de ton âge.', 'Clicksouris', ' écriture lecture poésie bestiaire cycle2 cycle3 GS CP CE1 CE2 CM1 CM2 ', 'http://www.clicksouris.com/poesie/bestiaire.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (542, 1, '2006-08-01 17:23:38', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Des machines à histoires de Clicksouris pour le cycle 3', 'Dans les histoires interactives de Clicksouris, tu es tour à tour, lecteur, acteur et auteur de l''histoire.\r\nDes histoires à lire, à écrire, à jouer... une autre façon de découvrir la lecture !\r\nTu verras, il y a de l''interactivité, des choix multiples, des parcours de lecture...\r\nMettre des histoires sur Internet, c''est aussi pour toi,  la possibilité de participer activement au récit, de devenir, tour à tour, lecteur, acteur et auteur de ce récit, et d''avoir la satisfaction valorisante de voir tes créations publiées.', 'clicksouris', ' lecture écriture récit fiction histoire ', 'http://www.clicksouris.com/machine.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (543, 1, '2006-08-01 17:27:52', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 5 ', ' 18 ', ' 4 ', 'Petit Paul et les dangers à la maison', 'Tu peux rentrer dans la maison de Paul. Tu verras une chouette qui sera de bons conseils pour éviter le danger.\r\nA la fin, si tu as su évitet tous les dangers, la chouette te remettre un diplôme. Utilisable au cycle 2', 'gouvernement', ' dangers sécurité multimédia citoyenneté éducation civique cycle2 ', 'http://www.minefi.gouv.fr/jeunes/jeu/jeu5_7.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (544, 1, '2006-08-01 17:38:16', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'Jeux magiques de Thérèse Eveillau du cycle 1 au cycle 3', 'De nombreux jeux directement en ligne. Cycle 1- Cycle 2-Cycle 3\r\nGS;CP;CE1;CE2;CM1;CM2', 'Thérèse Eveillau', ' mathématiques numération:calcul figures soustraction addition problèmes figure repérage logique ', 'http://perso.orange.fr/therese.eveilleau/pages/jeux_mat/indexF.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (545, 1, '2006-08-01 17:39:32', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 2 3 11 22 ', ' 4 ', 'Dictionnaire multilangue des enfants', 'Tu veux chercher comment s''écrit un mot dans une langue. Tu as un glossaire avec les lettres dans l''ordre alphabétique.\r\nTu cherches ton mot dans la liste proposée et puis quand tu l''as trouvé, tu cliques .. et puis tu vois ton mot traduit dans toutes les langues. Si tu veux l''écouter, tu cliques sur le haut-parleur et tu entends comment se prononce ton mot dans une langue', 'logosdictionary.org', ' traduction dictionnaire langue ', 'http://www.logosdictionary.org/pls/dictionary/new_dictionary.kdic.elenco_dizio_project3?lang=FR&amp;l=50', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (546, 1, '2006-08-01 17:51:24', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 16 ', ' 4 ', 'Encyclopédie de s animaux pour le cycle 2 et le cycle 3', 'Sur ce site, tu peux découvrir des animaux, leurs modes de vie. Tu peux même entendre leurs bruits. Les  textes sont écrits par les camarades de ton âge. Toi aussi, tu peux contriber et enenvoyer un texte sur un animal. Tu as également un espace &quot;forum&quot;.', 'académie de Nancy-Metz- CRM Faulquemont (Moselle)', ' encyclopédie recherche animaux écriture lecture ', 'http://www3.ac-nancy-metz.fr/crmfaulquemont/maternelle/ema2_imagier.php3', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (547, 1, '2006-08-01 18:00:07', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 16 ', ' 4 ', 'Fiches d''identité de certains animaux', 'Tu cherches des renseignements  sur les mammifères, oiseaux, amphibiens... tu peux aller sur ce site qui te présentent des animaux avec une fiche d''identité.', 'darwin.cyberscol.qc.ca', ' animaux lecture document  mammifères  oiseaux  amphibiens biologie ', 'http://darwin.cyberscol.qc.ca/Expo/Zoo/Fiches/Accueil.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (548, 1, '2006-08-01 18:06:53', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'La petite ferme avec des jeux en ligne pour le Cycle 2', 'Tu vas découvrir des lieux avec des animaux et puis tu devras participer à des jeux.CP;CE1; éventuellement CE2\r\nTu devras associer les phrases aux dessins proposés. Il y a deux niveaux de difficulté. L''ordinateur t''indiquera si c''est juste ou faux.', 'Commission scolaire de Scherbrook au Canada', ' animaux lecture document résultats ', 'http://station05.qc.ca/csrs/ferme/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (549, 1, '2006-08-01 18:20:25', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'L''art en s''amusant au cycle 3', 'Avec ce site, tu vas pouvoir faire des parcours pour découvrir les arts... Des natures mortes, des paysages, des scènes de genre et puis tu pourras découvrir l''histoire de la peinture car tu disposes d''une machine à remonter le temps.\r\nTu peux aussi découvrir des oeuvres d''enfants. Adapté au cycle 3 (CE2;CM1;CM2)', 'Fondation Blemberg', ' arts visuels payages peintres scènes histoire peinture natures mortes cycle3 ', 'http://www.bemberg-educatif.org/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (550, 1, '2006-08-01 18:33:33', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le musée en s''amusant', 'Entre dans ce drôle de musée et joue avec la lumière, les couleurs, les formes et les histoires des tableaux. Tu découvriras également des techniques que tu pourras reprendre et faire chez toi à la maison ou l''école.', 'France 5', ' musée arts visuels lumière couleurs formes histoires ', 'http://www.france5.fr/zouzous/museeamusant/accueil.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (551, 1, '2006-08-01 18:37:59', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'Le musée des arts asiatiques des enfants', 'Un parcours asiatiques où tu peux découvrir des dragons, des oeuvres asiatiques. Un éléphant apparaîtra parfois avec des énigmes à trouver. Si tu lis bien, si tu observes, bien tu y arriveras facilement', 'Musée des arts asiatiques Guimet', ' lecture arts Asie ', 'http://www.museeguimet.fr/minisite/enfants.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (552, 1, '2006-08-01 18:49:11', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 12 ', ' 10 ', ' 4 ', 'L''art expliqué aux enfants de 6 à 12 ans', 'Tu vas découvrir des artistes comme Miro, Matisse;Warlhol;Dubuffet;Hundertwasser.\r\nTu peux également percer les secrets de l''art au Mexique, l''art indigène. Et puis ... tu peux t''atteler à un bricolage qui fera sensation si tu le réussis bien. Si tu es fatigué de bricoler, ou lire, tu peux faire des jeux, des quizzs. Tu as un carnet de jeux pour les vacances. Tu peux être toi-même artiste et créateur et envoyer ton oeuvre pour qu''elle soit mise en ligne. Et puis n''hésite pas à essayer les &quot;trucs&quot; et les techniques décrits en ligne.', 'webmomes', ' Miro  Matisse Warlhol Dubuffet Hundertwasser arts visuels techniques bricolage jeux ', 'http://www.webmomes.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (553, 1, '2006-08-01 18:58:37', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 20 ', ' 4 ', 'Les couleurs de l''orchestre du cycle 1 au cycle 3', 'Alors là ... tu voyages au choeur d''un orchestre comme si tu y étais. Tu découvres chaque série d''instruments de musique. Tu les entends jouer, tu les vois. Et puis .. sais-tu reconnaître les différentes voix d''une chorale ou d''un choeur? Tu as beaucoup d''informations sur l''époque de Verdi, Verdi,  et l''histoire d''Otello. Voici aussi une histoire de l''opéra qui te permettra de mieux connaître ce genre musical, ainsi que ses œuvres et ses compositeurs célèbres.', 'capcanal', ' musique enncyclopédie instruments écoute voix cycle1 cycle2 cycle3 Verdi opéra genre musical ', 'http://www.capcanal.com/couleurs/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (554, 1, '2006-08-01 19:03:18', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 20 ', ' 4 ', 'Encyclopédie musicale pour le cycle 3', 'Tu trouves là la définition de mots en musique. Cette encyclopédie peut être utilisée par les élèves du cycle 3', 'capcanal', ' musique définition vocabulaire ', 'http://www.capcanal.com/couleurs/pages/encyclo/encyclo.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (555, 1, '2006-08-01 19:04:37', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 20 ', ' 4 ', 'Disposition des instruments dans un orchestre', 'Un document synthétique te permattant de voir en &quot;un schéma&quot; comment sont disposés les instruments dans un orchestre.', 'capcanal', ' instruments orchestre ', 'http://www.capcanal.com/couleurs/telecharger/disposition.pdf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (556, 1, '2006-08-01 19:08:55', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 9 ', ' 10 ', ' 4 ', 'Schéma simplifié de la classification des instruments', 'Un document qui te sert à te repérer avec tous les types d''instruments.Utilisable au cycle 3', 'capcana', ' musique instruments classification schéma cycle3 ', 'http://www.capcanal.com/couleurs/pages/encyclo/classification.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (557, 1, '2006-08-01 19:13:00', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Activités et jeux avec l''orchestre au cycle 3', 'Des activités et des jeux qui vont te permettre de mieux repérer et reconnaître les instruments de musique\r\n	Diapason de l''orchestre 	\r\n\r\n	Instruments à percussion 	\r\n\r\n	Instruments à cordes 	\r\n	Classification des 4 choeurs 	\r\n\r\n	Classification de toutes les voix 	\r\n	Hauteur : grave / aigu 	\r\n\r\n	Classification des instruments à vent 	\r\n	Colorie des instruments 	\r\n	La maison des cordes 	\r\n	Le chant 	\r\n	Peux-tu reconnaître les notes ? 	Dictée musicale (Do-mi-sol)\r\n	Le chant : la voix de ténor 	QCM\r\n	Les choristes et le travail de la voix\r\n	\r\n	Memory musical 	\r\n	Disposition des instruments 	\r\n	Teste-toi sur le VIOLONCELLE 	\r\n	Teste-toi sur le COR 	\r\n	Les cuivres 	\r\n	Les trois types de hautbois 	\r\n	Teste-toi sur le COR ANGLAIS 	\r\n	Teste-toi sur le TROMBONE 	\r\n	La durée des notes 	\r\n	Teste-toi sur la TROMPETTE 	\r\n	Les parties de la trompette 	\r\n	Les parties de la flûte 	\r\n	Teste toi sur le BASSON 	\r\n	Les parties de la clarinette \r\n	Pendu des instruments à vent 	\r\n	Teste-toi sur la FLUTE 	\r\n	Les cordes du violon 	\r\n	Teste-toi sur le VIOLON 	\r\n	Reconnaissance des bois 	\r\n	Les parties de l''alto 	\r\n	Teste-toi sur l''ALTO', 'capcanal.', ' musique orchestre jeux instruments écoute ', 'http://www.capcanal.com/couleurs/pages/encyclo/encyclo.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (558, 1, '2006-08-01 19:16:50', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 20 ', ' 4 ', 'Le monde de Koda pour un voyage musical du cycle 1 au  cycle 3', 'Sur la carte du monde, tu peux lire dans les pays des 4 contients, l''histoire des instruments. Tu vas découvrir les classes d''instrument et puis on t''emmènera dans un labo-interactif.Bon voyage musical. Tu ne seras pas déçu du voyage.', 'France 5', ' musique instruments jeux cycle1 cycle2 cycle3 ', 'http://education.france5.fr/musique/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (559, 1, '2006-08-01 19:30:48', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 10 ', ' 2 3 ', 'Site du CLEMI pour utiliser les médias à l''école', 'Accompagnement pédagogique des projets &quot;Presse&quot;. Outils et activités pour la classe. Projet de la semaine de la presse.', 'CLEMI', ' média ', 'http://www.clemi.org/spe1.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (560, 1, '2006-08-01 19:32:50', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 10 ', ' 3 ', 'Ouest-France-Ecoles  pour l''éducation aux média', 'Un site pour l''éducation aux média.Le site ouestfrance-ecole.com a été créé parce qu’il répond à des besoins légitimes à la fois de l’éducation nationale et d’Ouest-France.Le site a été créé d’abord pour aider à l’instruction et à l’éducation des élèves. Mais les enseignants, du premier et du second degré, sont les prescripteurs naturels. Le contenu et la forme du site ont été conçus pour répondre aux attentes des enseignants qui souhaitent :\r\n- être mieux armés pour aborder la presse à l’école : &quot; Le journal en 10 questions &quot; et &quot; Découvrir Ouest-France &quot;\r\n- avoir des propositions d’exercices en classe, avec des outils et des corpus d’articles ou de visuels : &quot; Travailler sur le journal &quot;\r\n- disposer d’articles et de visuels pour enrichir ou illustrer un cours : &quot; Comprendre l’actualité &quot;\r\n- échanger avec Ouest-France : of-ecole@ouest-france.fr \r\n- échanger avec d''autres enseignants, proposer aux élèves de valoriser leurs travaux en participant à des forums', 'Ouest-France-Ecoles', ' médias journaux écrire lire presse ', 'http://www.ouestfrance-ecole.com/commun/scripts/blocsmetiers/com_frame.asp?lien=/accueil.asp&amp;param=', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (561, 1, '2006-08-01 19:53:45', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 18 ', ' 4 ', 'Les clés de l''actualité Junior pour le cycle 3', 'Avec ce site, tu pourras découvrir l''actualité clairement expliquée aux élèves de  8/12 ans(CE2,CM1,CM2)', 'Les clés de l''actualité Junior', ' journal actualités presse cycle3 ', 'http://www.lesclesjunior.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (562, 1, '2006-08-01 19:57:18', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 1 ', 'Les 1001 nuits pour le cycle 3', 'Histoire avec le texte et lue par un conteur. Tu verras les belles illustrations et calligraphies.', '2001nuits.net', ' histoire récit cycle3 calligraphie ', 'http://www.2001nuits.net/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (563, 1, '2006-08-01 20:01:05', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'La citadelle du Ty Pouet ... petite  poésie pour sourire', 'Une façon &quot;rigolote&quot; d''user du moteur de recherche', 'la-citadelle-du-typouet', ' poésies humour moteur recherche ', 'http://www.la-citadelle-du-typouet.info/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (564, 1, '2006-08-01 20:08:00', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Les enquêtes de l''inspecteur Lafouine pour le cycle 3', 'Tu peux lire des textes où tu pourras mener des enquêtes policières. Il y aura des énigmes mais tu sauras trouver les indices...C''est interactif. Qu''il faille procéder par élimination, ou par déduction. Qu''il faille exposer les mensonges ou déchiffrer des codes, l''inspecteur Lafouine n''a pas son pareil pour venir à bout des énigmes les plus retorses.Tu peux devenir écrivain et écrire une enquête  policière.', 'Le personnage de Lafouine est une création de Christian Souchard', ' énigmes intrigies policier enquête lecture cycle3 écriture littérature ', 'http://ecole.toussaint.free.fr/lafouine/lafouine.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (565, 1, '2006-08-01 20:14:38', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'A la découverte de Jean de La Fontaine au cycle 3', 'Site sur les fables de La Fontaine.\r\nTu peux envoyer tes  pastiches de fables, tes dessins de fables, tes portraits de La Fontaine.\r\nTu verras les belles illustrations sur ce site. Elles sont faites par Willy Aractingi qui vient de mourir en Espagne pendant ses vacances en Espagne. On peut rendre hommage à ces belles oeuvres.', 'Willy Aractingi pour l''illustration - Administrateur du site', ' fables littérature La Fontaine répertoire illustration cucle 3 CE2 CM1 CM2 ', 'http://www.lafontaine.net/nouveau-site/index.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (566, 1, '2006-08-01 20:26:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Site sur Jules Verne pour les élèves du cycle 3', 'Site sur la vie et l''oeuvre de Jules Verne. Tu peux également voir des vidéos en ligne', 'education.france5. La vidéo : CNDP', ' Jules Verne littérature vidéo cycle 3 ', 'http://education.france5.fr/julesverne/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (567, 1, '2006-08-01 20:28:47', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Poèmes à voix haute de Victor Hugo au cycle 3', 'Tu pourras lire et écouter des poèmes ou poésies de Victor Hugo.', 'bnf', ' Victor Hugo poésies écoute cycle3 littérature ', 'http://expositions.bnf.fr/hugo/grosplans/loupe/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (568, 1, '2006-08-01 20:30:54', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le site de Saint-Exupéry pour les élèves de cycle 3', 'Un site sur la vie et l''oeuvre de Saint-Exupéry. CE2-CM1-CM2', 'Société Civile pour l''Oeuvre et la Mémoire d''Antoine de Saint Exupéry', ' littérature:Jeunesse Saint Exupéry cycle3 ', 'http://www.saint-exupery.org/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (569, 1, '2006-08-01 20:33:29', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Les contes de Charles Perrault', 'Contes de Perrault\r\nBarbe bleue\r\nCendrillon\r\nLa belle au bois dormant\r\nLe chat botté\r\nLe petit chaperon rouge\r\nLe petit poucet\r\nLes fées\r\nLes souhaits ridicules\r\nPeau d''âne\r\nRiquet à la houppe', 'www.alyon.asso.fr', ' contes lecture Perrault ', 'http://www.alyon.asso.fr/litterature/livres/XVIII/esprit_salon/perrault/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (570, 1, '2006-08-01 20:37:28', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 9 ', ' 4 ', 'Histoire de France ... des repères; des frises pour le cycle 3', 'L''histoire de France de l''antiquité à nos jours\r\nSouverains et chefs d''Etat Français\r\nLe calendrier Républicain\r\nConstitution française depuis 1789\r\nElection présidentielle depuis 1965\r\nMaréchaux de France depuis 1804\r\nHistoire des guerres françaises', 'Association Alyon', ' histoire france frise ', 'http://www.alyon.org/generale/histoire/france/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (571, 1, '2006-08-01 20:41:35', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'L''Europe de contes pour le  cycle 2 et cycle 3', 'Ce site est une petite merveille. Il te fera découvrir des contes d''Europe ( Islande;Ecosse,Finlande,Bretagne;Italie).\r\nTu rentreras dans les histoires comme si tu étais au cinéma. Les personnes vont parler devant toi, comme si tu étais devant la télé, ou au cinéma, ou dans une bande dessinée. Tu verras apparaître pour chaque histoire, un narrateur ( un animal) qui &quot;observe&quot; et raconte à la troisième personne.Tu aimeras les narrations et les mises en scène des histoires interactives. Tu auras un dictionnaire à ta disposition et une carte poiur t''aider à tout moment.', '.europeoftales.ne', ' littérature narration mise en scène personnage narrateur contes bulles cycle 2 ', 'http://www.europeoftales.net/site/fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (572, 1, '2006-08-01 20:55:41', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Que lire ce mois-ci ?', 'France 5 et Ricochet te proposent des sélections tous les  mois.\r\nSélection de 2 à 9 ans et de 9 ans à 15 ans. Tu as également des sélections thématiques', 'France 5 et Ricochet', ' lecture compréhension sélection ', 'http://education.france5.fr/livres_jeunesse/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (573, 1, '2006-08-01 21:01:14', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Le dictionnaire multi-fonctions de TV5', 'Dictionnaire de définitons  	Dictionnaire de synonymes  	Dictionnaire de conjugaisons  	Dictionnaires Français-Anglais  	Dictionnaire Anglais-Français. Tu saisis ta requête et tu valides pour obtenir la définition ou cliquez sur l''onglet de ton  choix.', 'TV5', ' dictionnaires synonymes conugaison anglais ', 'http://dictionnaire.tv5.org/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (574, 1, '2006-08-01 21:02:31', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 9 ', ' 4 ', 'Un site sur la préhistoire pour le cycle 3', 'Un site où tu pourras chercher des informations sur la préhistoire.\r\nLexique  Chronologie Paléolithique  Néolithique Art, des références filmiques', 'Lucien SULTRA', ' préhistoire histoire ', 'http://www.laprehistoire.net/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (575, 1, '2006-08-01 21:49:21', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Le site de l''homme de Tautavel', 'Un site sur la préhistoire. Le musée de Préhistoire de Tautavel, avec plus de 1500 m² de galeries d’exposition,\r\nvous invite à découvrir l’Homme de Tautavel, ses outils, les animaux qu’il chassait,\r\nson habitat et son environnement. Jeu sur les origines de l''homme.', 'tautavel.culture.gouv.fr', ' préhisroire histoire ', 'http://www.tautavel.culture.gouv.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (576, 1, '2006-08-01 21:50:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Site de la grotte de Lascaux', 'Visite virtuelle de la grotte de Lascaux permettant de découvrir la vie des hommes préhistoriques, l''art.\r\nUtilisable pour le cycle 3.', 'Ministère de la culture', ' préhisroire histoire ', 'http://www.culture.gouv.fr/culture/arcnat/lascaux/fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (577, 1, '2006-08-01 21:53:00', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Les mégalithes du Morbihan', 'Inscrits dans le paysage, menhirs et agencements de pierres dressées font du Morbihan une région privilégiée en matière de recherche sur le mégalithisme. Les formes colossales de Carnac constituent la série la plus emblématique de cette architecture. Elles sont les marques des sociétés néolithiques des Ve-IIIe millénaires avant notre ère', 'culture.gouv.fr', ' mégalithes menhirs ', 'http://www.culture.gouv.fr/culture/arcnat/megalithes/fr/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (578, 1, '2006-08-01 21:59:47', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 10 ', ' 4 ', 'Un site sur les Gaulois à exploiter au cycle 3', 'Un site sur les Gaulois et les Grecs', 'culture.gouv.fr', ' histoire Gaulois Gaulle grèce Grecs ', 'http://www.culture.gouv.fr/culture/arcnat/entremont/fr/index2.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (579, 1, '2006-08-01 22:02:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Carte de la Gaule pour le cycle 3', 'Tu cliques sur la carte. Tu découvres le nom des peuple gaulois, le nom de la région, de la ville, le nom antique', 'lexilogos.com', ' Gaulle carte peuple gaulois histoire ', 'http://www.lexilogos.com/gaulois_peuples_carte.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (580, 1, '2006-08-01 22:06:16', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Accès aux différentes cartes de France', 'geoportail : carte ign 1/25 000 &amp; 1/100 000 de toute la France &amp; photo satellite [toutes les nouveautés]\r\ncarte Mappy : carte routière, plan de ville &amp; photos des principales villes de France vues du ciel\r\ncarte Michelin : carte routière de la France, plan de ville &amp; calcul d''itinéraire. \r\nLocalisation d''une commune;Carte des départements;Cartes anciennes-\r\nInformations partagées sur une commune par les contributeurs de GenCom', 'lexilogos.com', ' cartes France Mappy géoportail Michelin départements ', 'http://www.lexilogos.com/france_carte.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (581, 1, '2006-08-01 22:15:18', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 10 ', ' 4 ', 'Dictionnaires des habitants des villes de France', 'Tu cherches le  nom des habitants d''une ville. Tu cherches le nom de la ville dans la liste et tu cliques sur  [Ctrl]+F', 'lexilogos', ' villes France orthographe dictionnaire ', 'http://www.lexilogos.com/noms_habitants_villes.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (582, 1, '2006-08-01 22:22:02', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 10 ', ' 4 ', 'Lexilogos', 'Une collection de cartes, de dictionnaires', 'lexilogo', ' dictionnaires cartes recherche ', 'http://www.lexilogos.com/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (583, 1, '2006-08-01 22:27:29', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Bestiaires en images de la BNF', 'Le site des bestiaires de la BNF ( bibliothèque nationale de France).\r\nTu y découvres des dragons; des histoires merveilleuses;le roman de renard\r\nTu pourras avec ton maître et ta maîtresse travailler sur des bestiaires et des maxi-monstres.Les classes sont invitées à travailler sur un projet de création collective d''un album illustré à la manière des bestiaires médiévaux.', 'biblithèque nationale de France', ' bestiaire images arts enluminures moyen âge roman renard animaux ', 'http://expositions.bnf.fr/bestiaire/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (584, 1, '2006-08-01 22:36:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 10 ', ' 4 ', 'Histoire naturelle des oiseaux en 9 volumes', 'Des articles sur les oiseaux. Document historique de Buffon', 'Buffon (George-Louis Leclerc 1707-1788)', ' oiseaux recherche document ', 'http://www.oiseaux.net/buffon/buffon.tome1.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (585, 1, '2006-08-01 22:40:40', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'La guerre des Gaules pour le cycle 3', 'Site dédié à la guerre des Gaules. Un résumé, des commentaires. Texte et illustration sur  Alésia\r\nprésentation de l''armée romaine et l''armée gauloise', 'alesia.asso', ' histoire guerre Alésia Gaule:Gaules armées lecture documents recherche César ', 'http://www.alesia.asso.fr/pg/GDG/home-GDG.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (586, 1, '2006-08-01 22:52:33', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 10 ', ' 4 ', 'L''Histoire de A à Z - L''index des noms', 'L''Histoire de A à Z - Index des noms\r\nRecherche sur Herodote.net. Tape  une date ou le nom de personne ou de lieu de ton  choix.\r\nDécouvre aussi le sens des mots en rapport avec l''Histoire et les curiosités de l''orthographe des noms propres', 'herodote.net', ' histoire index noms personnages dates ', 'http://www.herodote.net/indx.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (587, 1, '2006-08-01 22:55:20', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'La guerre des Gaules pour le cycle 3 - un texte à lire', 'Un document sur la guerre des Gaules', 'herodote.net', ' Gaulle carte peuple gaulois histoire guerre lecture ', 'http://www.herodote.net/histoire011100.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (588, 1, '2006-08-01 22:59:16', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 10 ', ' 3 ', 'Cartes animées et atlas historiques pour mieux comprendre l''histoire.', 'Un site pour composer votre atlas historique multimédia. Le site l’Histoire à la carte, vous propose de constituer progressivement votre propre atlas historique multimédia, personnel ou familial, en téléchargeant* sur votre ordinateur tout ou partie des cartes animées que nous mettons régulièrement en ligne.\r\n* Nous pouvons également les graver sur un cédérom', '', ' histoire cartes atlas ', 'http://www.histoirealacarte.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (589, 1, '2006-08-01 23:04:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Une frise chronologique pour percer les secrets de chaque période de l''histoire', 'Une grande frise chronologique et interactivete  permet de faire défiler sur yon  écran 15 siècles d’histoire de France : personnages, évènements, monuments, régimes politiques….Visuellement cette frise est très colorée : chaque grande période est caractérisée par une couleur et le passage progressif d’une couleur à l’autre rappelle que les grandes évolutions se font dans la durée. Tu verras comme c''est bien de remonter ainsi l''histoire', 'histoirealacarte.com', ' frise histoire périodes personnages  évènements  monuments  régimes: politiques ', 'http://www.histoirealacarte.com/histoire_de_france/chronologie.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (590, 1, '2006-08-01 23:09:11', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Des dossiers en histoire pour le cycle 3', 'Au nombre de vingt-sept, tu trouves des dossiers avec des sujets très divers : un homme, un événement, une œuvre d''art, un témoignage... Chacun d''entre eux constitue une sorte de «petite leçon d''histoire» illustrant comment travaille l''historien aujourd''hui. De nombreux documents, textes ou images, sont consultables. Des textes d''historiens de diverses époques sont présentés pour montrer l''évolution de la perception des événements passés et de leur importance.Chaque dossier est introduit par une courte animation audiovisuelle.', 'Images et Savoirs a coédité avec le Centre Régional de Documentation Pédagogique d’Aix Marseille le', ' histoire  baptême Clovis Empereur Normands guerres religion ', 'http://www.histoirealacarte.com/histoire_de_france/dossiers/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (591, 1, '2006-08-02 10:42:45', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 9 ', ' 4 ', 'Découvrir le chantier médiéval de Guédelon', 'Découvre le chantier médiéval de Guédelon ; la construction d''un château fort en plein coeur de la Puisaye, en Bourgogne.Utilisable pour les élèves du cycle 3', 'guedelon.com', ' histoire moyen âge château lecture cycle3 ', 'http://www.guedelon.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (592, 1, '2006-08-01 23:10:30', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Diaporamas de cartes pour l''histoire au cycle 3', 'Une animation cartographique soutenue par un commentaire sonore permet de suivre en continu les principales évolutions territoriales qui ont conduit de la Gaule romaine à la France d''aujourd''hui. Cet enchaînement est découpé en trois grandes périodes, caractérisées par trois couleurs différentes. Jusqu''à la fin du Xème siècle la notion d''espace prime. Du XIème siècle à la Révolution française on fait référence à la notion de royaume, ensuite à celle d''état-nation.', 'Images et Savoirs a coédité avec le Centre Régional de Documentation Pédagogique d’Aix Marseille', ' histoire Gaule romaine animation carte ', 'http://www.histoirealacarte.com/histoire_de_france/diaporama.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (593, 1, '2006-08-01 23:14:27', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Le temps des châteaux forts - un DVD  à exploiter au  cycle 3', 'Le DVD Le temps des châteaux forts offre un vrai moment de bonheur à tous les amateurs d''Histoire  qui voient revivre sous leurs yeux le Moyen Âge comme s''ils y étaient.  A travers les images, le rythme, la musique et les commentaires, les auteurs nous communiquent leur passion pour cette époque lointaine et réussissent la gageure de nous la montrer comme si nous y étions.\r\nL''Éducation nationale certifie l''intérêt pédagoqique de ce DVD (label RIP)', 'Herodote.net', ' histoire moyen âge DVD RIP atlas châteaux forts ', 'http://www.herodote.net/Boutique/dvd.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (594, 1, '2006-08-03 08:00:52', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'La vie quotidienne en l''an 1000... Un site sur le moyen-âge pour le cycle 3', 'Nous sommes en l''an 1008, peut-être 1010, de l''ère chrétienne. Une troupe se fraie difficilement un passage sur le territoire de l''actuel département de l''Isère. Les femmes sont accompagnées d''enfants ; les hommes, en armes, montent à cheval tandis que d''autres mènent à grand bruit cochons, chèvres et vaches. Dans les charrettes se trouve tout le matériel pour vivre. Une aventure qui les mènera jusqu''aux rives boisées du lac de Paladru.\r\nQui sont ces chevaliers-paysans ? Quelle était la nourriture ? la défense? quels étaient les divertissements ?\r\nTextes intéressants à dévouvrir au CE2,CM1,CM2.', 'Ministère de la culture', ' chevaliers paysans moyen âge histoire cycle3 rechercher lecture divertissements ', 'http://www.culture.gouv.fr/culture/arcnat/charavines/fr/chepay.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (595, 1, '2006-08-01 23:22:31', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Un site d''histoire sur Alésia', 'Tout savoir sur Alésia et la guerre entre Gaulois et Romains', 'Association &quot;Alésia&quot;', ' Alésia Gaulois Romains ', 'http://www.alesia.asso.fr/pg/Alesia/home-alesia.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (596, 1, '2006-08-02 10:20:18', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 10 ', ' 4 ', 'La cité romaine le site de France 5 pour tout savoir sur la vie des Romains', 'Tu trouveras des informations sur la cité romaine;les habitants, les bâtiments. France 5 va t''aider à constuire une cité romaine.', 'France5; Education.france5.fr', ' histoire Romains cité habitants bâtiments vie ', 'http://education.france5.fr/romains/accueil.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (597, 1, '2006-08-02 10:26:30', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Musée national du Moyen Age pour le cycle 3', 'Le musée national du Moyen-âge va te permettre de remonter le temps jusqu''au moyen-âge et de te faire une visite virtuelle. Trois questions pour découvrir le musée ...Quand et où ? Comment vas-tu situer les oeuvres dans le temps et dans l''espace ? Qui est qui ? Qui pourras-tu rencontrer au détour d''un couloir ? Qu''est-ce que c''est ? Quels objets se cachent dans les oeuvres ? Quelques indications pour visiter le site en toute tranquillité et ne rien manquer :\r\nUn clic sur le lapin et tu retournes à la page précédente. Un clic sur l''oeil et tu découvres d''autres images. Enfin, si tu veux en savoir plus, passe ta souris sur les mots de couleurs différentes : ils te donneront des définitions, et des repères pour lire les images.', 'Ministère de la culture', ' musée histoire moyen âge images lecture cycle3 définitions ', 'http://www.culture.gouv.fr/cluny/intro.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (598, 1, '2006-08-03 08:12:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 10 ', ' 4 ', 'Explorer le moyen-âge sur France 5... pour le cycle 3', 'Site très complet où tu peux découvrir ce qu''est le moyen-âge, comment on vivait à cette époque là. Tu sauras tout sur l''art au moyen-âge, le monde des paysans, les banquets, les châteaux, les divertissements. La vidéo pourra te montrer les secrets du moyen-âge. France 5 a prévu un labo-interactif pour toi pour voir comment la France s''est construite. Tu  pourras le faire dès que tu es au CE2. Tu as également un jeu interactif pour découvrir la vie des seigneurs, des paysans, des chevaliers si tu es au CM ou au collège. Un quiz t''attend.Tu seras aidé car tu auras un glossaire à ta disposition à tout moment.', 'France 5  Le Louvre et Hachette Jeunesse', ' histoire moyen âge seigneurs paysans chevaliers vie vidéo cycle3 jeux glossaire ', 'http://education.france5.fr/Moyenage/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (599, 1, '2006-08-03 08:14:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 10 ', ' 4 ', 'A votre service, mon seigneur un site de TV5 pour le cycle 3', 'Un site pour partir à la découverte des châteaux-forts, de la vie des paysans, des seigneurs, des moines, des chevaliers, les personnages principaux du moyen-âge. Un site adapté pour les élèves du cycle 3 (CE2;CM1;CM2)', 'France 5  Le Louvre et Hachette Jeunesse', ' histoire moyen âge château lecture cycle3 paysans seigneurs moines chevaliers ', 'http://education.france5.fr/moyenage/illustrations/labo02.swf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (600, 1, '2006-08-03 08:25:47', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 9 ', ' 10 ', ' 4 ', 'Enluminures une base de données d''images pour illustrer la vie au moyen-âge', 'Tu travailles sur le moyen-âge, tu cherches des informations sur les enluminures. Tu as là la base Enluminures qui propose la consultation gratuite de plus de 80 000 images, sous forme de vignette et de plein écran, reproductions numériques des enluminures et éléments de décor de plus de 4 000 manuscrits médiévaux conservés dans une centaine de bibliothèques municipales françaises. Tu peux faire une visite culturelle sur la nativité, les rois de France, les sciences, les travaux des champs; le moyen-âge à table... une façon de découvrir des images de la vie quotidienne du moyen-âge. Utilisable à tous les niveaux pour la lecture des images.', 'Ministère de la culture', ' moyen âge histoire chevaliers lecture images rois champs paysans vie quotidienne ', 'http://www.enluminures.culture.fr/documentation/enlumine/fr/index3.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (601, 1, '2006-08-03 08:40:27', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Site sur l''art gothique au cycle 3', 'Un site bien illustré sur le temps des cathédrales et sur l''art gothique pour le cycle 3. (CE2,CM1,CM2)', 'Clio et Calliope', ' histoire cathédrales arts lecture cycle3 ', 'http://www.clioetcalliope.com/medieval/gothique/gothiquemenu.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (602, 1, '2006-08-03 08:44:59', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 10 ', ' 4 ', 'Base de données pour trouver des documents sur une époque en histoire', 'Avce ce site, tu peux rechercher et trouver un article sur Clio et Calliope. Tu as à ta disposition un moteur de recherche avec une recherche multi-critères. ( Histoire, archéologie, architecture, peinture, sculpture) et puis une entrée par époque (antiquité,médiéval,moderne,contemporaine).Utilisable dès le cycle 3 pour trouver des textes ou des photos illustrant une période en histoire.', 'Clio et Calliope', ' histoire moteur  architecture  peinture sculpture antiquité médiéval moderne contemporaine période ', 'http://clioetcalliope.free.fr/recherche.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (603, 1, '2006-08-03 08:55:31', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 19 ', ' 1 ', 'La peinture en un clic .. un CD pour comprendre les oeuvres d''arts', 'L''association Clio et Callioque a produit un outil multimédia et pédagogique. Véritable introduction à l''art de la peinture et à son évolution du XVe au XXe siècles, ce CD vous permet de découvrir à partir de vingt œuvres phares les techniques de la peinture (lignes de force, perspectives,..), de comprendre le message et la signification d''une œuvre placée dans son contexte historique. Ce CD vous permet également d''apprendre quels sont les différents genres de peinture : aquarelle, huile sur toile, tempera….', 'association Clio et Callioque', ' arts visuels peinture CD Rom oeuvres artistes techniques lecture ', 'http://www.clioetcalliope.com/cd.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (604, 1, '2006-08-03 08:57:23', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 9 ', ' 10 ', ' 4 ', 'Les cadhédrales de France pour le cycle 3', 'Une sélection d''images numériques réalisées à partir des fonds photographiques conservés aux Archives photographiques a donné lieu à cette exposition virtuelle. Tu as là des photos et des images numériques concernant les cathédrales en France.', 'Archives Photographiques - Médiathèque de l''architecture et du patrimoine (DAPA)', ' histoire cathédrales images moyen âge lecture recherche ', 'http://www.culture.gouv.fr/culture/inventai/itiinv/cathedrale/cat_2.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (605, 1, '2006-08-03 09:04:28', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Paris sous Philippe Auguste pour le cycle 3', 'Un site qui t''apporte des renseignements sur la muraille, le quotidien, la ville du roi. Tu as une bibliographie à ta disposition si tu veux en savoir plus.', 'mchristine@ecole-multimedia.com', ' Paris Philippe Auguste vie quotidienne lecture ville histoire moyen âge recherche ', 'http://www.philippe-auguste.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (606, 1, '2006-08-03 09:13:05', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Les grandes inventions et les grandes découvertes pour les élèves du cycle 3', 'Un siècle plein de surprises. Alors en route pour cette passionnante aventure ! pour découvrir les  grandes inventions, les grandes découvertes, un nouveau monde. Recherche documentaire pour les élèves du cycle 3 (CE2,CM1,CM2)', 'IUFM de Lyon', ' histoire inventions découvertes lecture ', 'http://web.lyon.iufm.fr/formation/lyon/product/decouv/dec1.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (607, 1, '2006-08-03 09:19:19', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Léonard de Vinci, un site pour le cycle 3', 'Un site pour découvrir la vie et l''oeuvre de Léonard de Vinci. Tu pourras lire et comprendre  ses oeuvres, des peintures, ses dessins.Tu peux décrypter les détails de tableaux.Tu as une biographie complète. Tu peux jouer sur le site avec des  quizz ou les jeux des détails. Utilisable dès le cycle 3(CE2,CM1;CM2)', 'artdevinci.free.fr', ' arts Léonard Vinci vie oeuvre biographie tableaux lecture ', 'http://artdevinci.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (608, 1, '2006-08-03 09:21:35', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Découvrir le château de la Loire de Cheverny au cycle 3', 'Un site où tu peux visiter le château de la Loire. Le domaine de Cheverny appartient à la même famille, les Hurault, depuis plus de six siècles. Le père du constructeur fut chancelier ( ministre de la justice ) des rois Henri III et Henri IV. Cheverny servit de modèle au célèbre dessinateur Hergé‚ pour créer Moulinsart.', 'chateau-cheverny.com', ' château Loire renaissance lecture ', 'http://www.chateau-cheverny.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (609, 1, '2006-08-03 09:34:37', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Journées de rois un site pour le cycle 3', 'Un site qui te donne des renseignements sur les levers, le travail; la religion, le repas, les divertissements;les promenades, la chasse, la vie en famille au château de Versailles et de Chambord', 'journeesderois.com avec le soutien de l''Union Européenne', ' histoire lecture rois travail religion repas divertissement château Versailles ', 'http://www.journeesderois.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (610, 1, '2006-08-03 09:43:18', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Site du château de Versailles', 'Pour découvrir le château de Versailles, comme si tu y étais.', 'chateauversailles.fr', ' histoire château Versailles ', 'http://www.chateauversailles.fr/fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (611, 1, '2006-08-03 09:51:29', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le site de Napoléon pour le cycle 3', 'France 5 et Le Louvre te  proposent de découvrir la vie et l''oeuvre de Napoléon. Tu as un commentaire et une explication de la vie de Napoléon. On t''explique la peinture du sacre de Napoléon 1er est une fresque historique monumentale (six mètres sur dix) servant la propagande de l''Empire. Tu as à ta disposition un labo-interactif où tu peux jouer en ligne  avec Napoléon Bonaparte du Consulat à l''Empire. Tu as un espace vidéo pour &quot; La mise en scène du Sacre&quot;, la composition de la toile. Accessible dès le cycle 3(CE1,CE2,CM1,CM2).', 'Le Louvre et France 5', ' histoire Napoléon sacre vidéo peinture cycle3 lecture ', 'http://education.france5.fr/napoleon/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (612, 1, '2006-08-03 09:59:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 10 ', ' 4 ', 'La seconde guerre mondiale et le débarquement', 'Tout au long de ce parcours interactif, tu peux découvrir les principaux faits du 06 Juin 1944 et le débarquement', 'France 5 Education', ' histoire guerre mondiale débarquement ', 'http://www.france5.fr/images/flash/2gm/fla1/debarquement1.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (613, 1, '2006-08-03 10:04:17', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 10 ', ' 4 ', 'La seconde guerre mondiale en 60 dates pour le cycle 3', 'Des modules interactifs, une sélection de liens et livres, des vidéos...sur la seconde guerre mondiale.\r\nPour les enseignants: des pistes pédagogiques autour des modules interactifs et des archives vidéos, une sélection de liens, livres...Tu as un labo-interactif.', 'France 5 Education', ' histoire seconde  guerre  mondiale vidéo ', 'http://www.france5.fr/2gm/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (614, 1, '2006-08-03 10:37:52', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'L''arbre d''Anne Frank, un monument interactif', 'Anne Frank regardait souvent un marronnier par la lucarne de sa maison. Elle a également parlé de cet arbre dans son journal. Âgé de plus de 150 ans, l’arbre est maintenant malade, mais il continue de vivre en ligne. Laisse ta feuille dans le marronnier virtuel, fais-la suivre à des ami(e)s et maintiens ainsi vivants les idéaux d’Anne Frank.', 'Anne Frank Stichting', ' Anne Frank journal juifs lecture écriture ', 'http://www.annefranktree.nl/index.aspx?lang=fr', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (615, 1, '2006-08-03 10:42:05', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 10 ', ' 4 ', 'La maison de Anne Frank un site interactif pour le cycle 3', 'Ce site interactif propose un grand nombre de documents sur l’histoire d’Anne Frank et la Seconde Guerre mondiale ainsi que des conseils pour préparer un exposé. C’est une base de données avec des textes, des photos et des extraits audio-visuels sur les thèmes d’Anne Frank et de la Seconde Guerre mondiale.Utilisable au cycle 3(CE2,CM1,CM2)', 'Association Anne Frank', ' histoire guerre seconde mondiale lecture écriture cycle3 ', 'http://www.annefrank.org/content.asp?pid=1&amp;lid=5', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (616, 1, '2006-08-03 10:55:58', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le grenier de Sarah pour comprendre la Shoah au cycle 3', 'Le grenier de la Shoah permet d''aborder la Shoah à travers les destins de cinq enfants Juifs.Tu apprendras à connaître ce temps où on exterminait les Juifs du temps des Nazis pendant la seconde guerre mondiale.', 'Musée, centre de documentation juive contemporaine;mémorial de la Shoah', ' histoire Shoah juifs guerre mondiale judaïsme nazis nazisme lecture ', 'http://www.grenierdesarah.org/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (617, 1, '2006-08-03 11:09:35', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le 06 Juin 1944,  le débarquement expliqué aux enfants', 'Site qui te permet de découvrir ce qui s''est passé le 06 Juin 1944, quand les armées alliées se sont lancées à l''assaut des plages normandes pendants la deuxième guerre mondiale. Tu as un parcours interactif avec des vidéos, des extraits de radio de l''époque. Utilisable dès le cycle 3.(CE2,CM1,CM2)', 'France 5 Education', ' histoire guerre seconde mondiale lecture débarquement ', 'http://www.france5.fr/images/flash/2gm/fla2/debarquement2.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (618, 1, '2006-08-03 11:10:44', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 1 ', '1944, la bataille de Normandie', 'des témoignages, des documents exploitables en classe sur la bataille de Normandie. A adapter en choisissant des documents', 'Conseil Régional de Basse Normandie et normandie44lamemoire.com', ' guerre mondiale Normandie 1944 ', 'http://www.normandie44lamemoire.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (619, 1, '2006-08-03 11:14:27', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 9 ', ' 10 ', ' 1 ', 'Le site de l''IGN pour les cartes de la France', 'Les fichiers images proposés en téléchargement sont libres de droits.\r\nCarte de la France;silhouette de la France;cartes administratives de la France;départements;régions', 'IGN', ' géographie départements régions cartes libre ', 'http://www.ign.fr/affiche_rubrique.asp?rbr_id=441&amp;lng_id=FR#8223', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (620, 1, '2006-08-03 11:18:47', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 1 ', 'Le site de l''IGN pour les cartes du monde', 'Cartes du monde à télécharger. Des vues du ciel.', 'IGN', ' géographie cartes monde libre continents pays ', 'http://www.ign.fr/rubrique.asp?lng_id=FR&amp;rbr_id=2416', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (621, 1, '2006-08-03 12:00:28', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 10 ', ' 4 ', 'Atlas géographique mondial', 'Vous trouverez sur ce site chaque pays classé d''abord par continent,puis par ordre alphabétique. En suivant les différents liens,situez-les à l''aide des cartes et découvrez leurs drapeaux,leurs hymnes, leurs armoiries et leurs fiches descriptives.Afrique, Amérique,Antarctique,Asie,Europe,Océanie', 'atlasgeo.net', ' atlas géographie pays continent cartes drapeaux hymnes Afrique  Amérique Antarctique Asie Europe ', 'http://www.atlasgeo.net/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (622, 1, '2006-08-03 12:21:17', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 10 ', ' 4 ', 'Moteur de recherche pour chercher une carte ou des objets géographiques', 'Un service de Atlas Géo. Un moteur de recherche pour chercher des cartes, des hymnes.\r\nNe marche qu''avec Internet Explorer', 'atlasgeo.net', ' géographie cartes monde libre ', 'http://www.atlasgeo.net/search.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (623, 1, '2006-08-03 14:35:29', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 18 ', ' 1 ', 'Les journaux télévisés en ligne', 'Une page qui vous permet d''accéder aux journaux télévisés de TF1, FRANCE 2, FRANCE 3', 'TF1;FRANCE2;FRANCE 3 page proposée par Sébastien Maisse', ' journaux télé presse ', 'http://www.cyberserveur.net/tv/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (624, 1, '2006-08-03 15:20:52', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'La France vue sur mer pour le cycle 3', '5 500 kilomètres de littoral 15 000 photos aériennes.Tu cliques sur une région ou tu utilises le moteur de recherche pour trouver une ville près d''une côte de France. Tu te rapproches en un clic et tu peux voir les différentes côtes et paysages de France. Utilisable au cycle 3(CE2,CM1,CM2). Francevuesurmer.com est le diaporama géographique et touristique de l’ensemble des côtes françaises. Il te permet de visiter en quelques clics le port de Saint Tropez et la plage de Deauville, les rochers de Trégastel et la citadelle de Bonifacio, le phare de Biarritz et les falaises d’Etretat.', 'francevuesurmer.com', ' géographie côtes mer paysage lecture cycle3 moteur recherche ', 'http://www.francevuesurmer.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (625, 1, '2006-08-03 15:23:18', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 10 ', ' 4 ', 'Le site pour apprendre l''Union européenne', 'Un site qui te permet de découvrir comment l''Europe a été construite. Tu as une vidéo, un quizz et de nombreuses informations pour découvrir les rouages de l''Europe.', 'France 5 Education', ' Europe citoyenneté géographie vidéo ', 'http://education.france5.fr/europe/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (626, 1, '2006-08-03 15:32:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 9 ', ' 3 ', 'Exemples de cartes topographiques', 'Un site pour illustrer le principe des cartes topographiques.', 'Support de  cours des  séances et  modules de  l''IUFM  de  Paris', ' géographie carte topographique ', 'http://www.tice-hg.net/stages/topo.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (627, 1, '2006-08-03 15:36:42', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 10 ', ' 1 ', 'Google Earth pour chercher et découvrir', 'Vous souhaitez en savoir plus sur un lieu précis ? Lancez-vous ! Google Earth met des informations géographiques sur le monde entier à votre portée en associant des images satellite, des plans, des cartes et la puissance de la recherche GoogleLles sites de Google permettant de voir la terre d''en haut proposent images satellites, aériennes et cartes à de multiples échelles des Etats-Unis mais aussi de plus en plus pour l''ensemble du globeSurvolez la planète ou explorez votre quartier. Saisissez une adresse et zoomez dessus. Recherchez des écoles, des parcs, des restaurants et des hôtels Calculez des itinéraires.Inclinez la vue et faites-la pivoter pour voir les bâtiments et le relief en 3D.Enregistrez et partagez vos recherches et vos endroits préférés. Vous pouvez même ajouter vos propres annotations.', 'Google', ' géographie images satellites plans cartes vues échelle ', 'http://earth.google.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (628, 1, '2006-08-03 15:37:56', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Le logiciel Mapsgoogle pour la géographie au cycle 3', 'Un logiciel pour rechercher un plan. Faites glisser la carte avec la souris ou double-cliquez sur un point pour effectuer un zoom.', 'Google', ' géographie carte plan ', 'http://maps.google.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (629, 1, '2006-08-03 16:00:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 10 ', ' 4 ', 'Un site avec des cartes pour se repérer', 'Tu choisis l''endroit sur la carte où tu veux te repérer. Tu as un curseur qui te donnera un effet zoom .. et tu peux avoir des plans.', 'windows', ' cartes échelles plans ', 'http://virtualearth.msn.com', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (630, 1, '2006-08-03 16:04:32', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 10 ', ' 3 ', 'Le logiciel PrintGoogle pour rechercher un livre ou une page de livre sur Intern', 'l suffit d''effectuer une recherche sur la page d''accueil du service Google Recherche de Livres. Chaque fois que les termes de votre recherche figurent dans un livre, un lien vers cet ouvrage s''affiche dans les résultats obtenus. Cliquez sur le titre d''un livre, pour qu''apparaisse la page dans laquelle se trouvent vos termes de recherche. Cette page est accompagnée d''autres informations concernant l''ouvrage, mais aussi de liens « Acheter ce livre » permettant d''accéder à des librairies en ligne. Si le livre est libre de droits, vous pourrez le consulter intégralement.', 'Google', ' moteur recherche Google livres ', 'http://books.google.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (631, 1, '2006-08-03 16:13:44', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 3 ', 'Chroniques Internet ... un site pour s''informer et se former en histoire et en g', 'Chroniques Internet ... est un site pour s''informer et se former en histoire et en géographie. Il est édité par une association de professeurs d''histoires et de géographie de Caen', 'aphgcaen', ' histoire géographie chroniques ', 'http://aphgcaen.free.fr/chronique.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (632, 1, '2006-08-03 16:17:20', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 9 ', ' 9 ', ' 1 ', 'Cartothèque .... des cartes libres de droit pour un usage scolaire', 'Fonds de cartes pour l''enseignement de l''histoire et de la géographie, libres de droits dans le cadre d''un usage scolaire.\r\nCes documents sont disponibles en cinq formats :\r\n# Un format bitmap (GIF) pouvant être récupéré et inséré dans tous les traitements de texte par simple copier/coller.\r\n# Quatre formats vectorisés pour une impression plus précise des tracés, un redimensionnement sans altération de la qualité de l''image, ou une modification à l''aide du logiciel adéquat : Monde,Afrique,Amérique,Europe,Asie,France', 'Professeurs de l''ac-aix-marseille', ' cartes géographie ', 'http://histgeo.ac-aix-marseille.fr/carto/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (633, 1, '2006-08-03 16:24:56', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 1 ', 'Le logiciel &quot;Make your map&quot; pour créer une carte sur Internet', 'Logiciel gratuit en ligne. Vous choisissez une carte. Vous choisissez vos données. Vous choisissez le mode de repésentation. Ce logiciel peut vous rendre service en géographie. Vous êtes aidé avec un tutoriel.http://mym.articque.com/files/mym_help.fr.pdf', 'makeyourmap.net', ' géographie carte logiciel gratuit ', 'http://www.makeyourmap.net/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (634, 1, '2006-08-03 16:37:01', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 1 ', 'Le logiciel &quot;Time Line maker&quot; pour concevoir des frises historiques', 'Time Line Maker est un logiciel qui permet concevoir des tableaux et surtout des axes chronologiques à partir de bases de données incluant des dates, des évènements et autres renseignements. L''interface est simple et rapide à utiliser. L''on peut créer 3 sortes de chronologies à partir d''une sélection d''évènements choisis dans l''ensemble d''un fichiers de données. - Possibilité de sélectionner les évènements à inclure dans la chronologie\r\n- Ajustement automatique de l''échelle et de la position des données sur l''axe chronologique\r\n- Aperçu avant impression\r\n- Accepte toute date de JC à 9999 y compris avec jour/mois/année (NOTA : impossibilité avant JC)\r\n\r\nDes élèments de : réflexion, conseils, évaluations et des pistes de travail avec les élèves Une AIDE en français : TUTORIAL illustré', '', ' frise logiciel freeware gratuit événements dates ', 'http://soshg.free.fr/loghist.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (635, 1, '2006-08-03 16:44:45', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 12 ', ' 9 ', ' 1 ', 'Tutoriel pour utiliser le logiciel &quot;Time line maker&quot; pour faire des fr', 'Aide pour utliser le logiciel &quot;Time line maker&quot;', '?', ' logiciel gratuit frise histoire tutoriel ', 'http://soshg.free.fr/timeline/timeline-hlp.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (636, 1, '2006-08-03 16:48:03', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Géobase une base de données de 1600 articles parus dans GEO (depuis N° 1)', 'Base de données de 1600 articles parus dans GEO (depuis N° 1)\r\nOn peut télécharger Géobase en décompressant le fichoer  geobase.zip (0,115 Mo)', 'Revue Géo', ' articles revue GEO géographie reportages ', 'ftp://ftp.ac-amiens.fr/pub/Educatifs_fr/Geographie/geobase.zip', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (637, 1, '2006-08-03 16:50:38', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 1 ', 'GEOSTAT 1.0 un logiciel pour fabriquer des cartes', 'GEOSTAT 1.0 Ce programme vous permet, à partir d''une carte vierge de la France départementale, de représenter géographiquement diverses répartitions :\r\n- Répartition de la population\r\n- Représentation en couleur de la superficie des départements français', 'O Guilleton', ' géographie population départements ', 'http://olg137.club.fr/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (638, 1, '2006-08-03 16:53:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 1 ', 'Base de données en chiffres des principaux états', 'Base données des principaux états (150) : fiche d''identité, tableaux comparatifs, drapeaux.\r\nSimple..Atout principal: gratuité. Télécharger à partir du site de Géonet : Le_monde.zip (0,206 Ko)\r\nou ftp://ftp.ac-amiens.fr/pub/Educatifs_fr/Geographie/lmec.zip', 'W Prévost', ' géographie états tableaux données drapeaux ', 'ftp://ftp.ac-amiens.fr/pub/Educatifs_fr/Geographie/lmec.zip', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (639, 1, '2006-08-03 16:57:13', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 10 ', ' 4 ', 'Logiciel sur l''Union Européenne', 'UNION EUROPEENNE 1.01. Présentation multimédia sur les quinze pays de la communauté européenne suivie d''exercices sur l''euro ainsi que de jeux. Logiciel créé à partir des travaux d''enfants. De nombreux thèmes abordés. A installer au préalable.', 'Jean-Michel BARDIN', ' Europe géographie freeware gratuit logiciel ', '', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (640, 1, '2006-08-03 17:01:31', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 4 ', 'Logiciel  de géographie CBGEO FRANCE  USA  WORLD', 'CBGEO est un logiciel de géographie. \r\nCes 3 logiciels permettent de tester ses connaissances sur le monde (états, départements... ) Deux modes de fonctionnement :apprentissage et interrogation. Ensemble très bien réalisé, graphisme agréable.\r\nTélécharger directement CBGEO chez l''auteur.', 'C Brun', ' géographie états France USA pays ', ' ftp://ftp.ac-amiens.fr/pub/Educatifs_fr/Geographie/cbgeo311.zip', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (641, 1, '2006-08-03 17:04:13', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 4 ', 'Logiciel COGEO  en ligne pour apprendre la position des villes d''une manière int', 'Cogeo est un professeur virtuel de géographie dont le but est d''apprendre aux internautes la position des villes de différents pays de manière INTERACTIVE et sympathique', 'Alexis RUYANT', ' logiciel géographie freeware gratuit villes ', '', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (642, 1, '2006-08-03 17:10:55', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 4 ', 'Logiciel sur les départements français', 'Le logiciel &quot;Les DEPARTEMENTS français 2.0&quot; permet de mémoriser les différents départements de la France\r\nPrincipe du jeu : recherche sur un fond de carte des départements, affectation des préfectures et des sous préfectures, des régions. Nouveautés  par exemple un mode apprentissage, qui vous présente la bonne réponse, et qui vous permet de visualiser une fiche de renseignements sur les départements (source INSEE 1990), le paramétrage du jeu pour choisir les étapes et leur ordre, ...', 'Pierre Leflon', ' géographie départements régions cartes préfecture ', 'http://olivier.leflon.free.fr/jeux/jeux.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (643, 1, '2006-08-03 17:18:01', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 3 ', 'Logiciel de géographie &quot;Versamap&quot;', 'VERSAMAP 2.07	Affiche le  monde (actualisé) selon 14 projections différentes. Diverses configurations possibles. Possibilité d''annoter les cartes réalisées et de les sauvegarder en bitmap ou vectoriel !.\r\nHome Page http://www.versamap.com/ Pour la formation des enseignants', 'versamap@versamap.com Charles Culberson', ' logiciel freeware cartes ', 'http://www.versamap.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (644, 1, '2006-08-03 17:21:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 1 ', 'Le logiciel &quot;Winglobe&quot; 1.2', 'Le globe en 3 D sur le bureau Windows avec zoom, rotation. Possibilité de voir jour et nuit, latitude et longitude, distances, Etats, relief et végétation... Voir WinGlobe Home Page http://www.djuga.net/winglobe.html\r\nTélécharger : wglobe12.zip (1,70 Mo) à ftp.zcu.cz  ou', 'Dirk Djuga', ' logiciel freeware gratuit globe jour nuit rotation Terre ', 'ftp.mpi-sb.mpg.de', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (645, 1, '2006-08-03 17:24:51', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 1 ', 'Le site Géoclip pour créer ses cartes en ligne', 'Geoclip.net/fr  - Geoclip est un excellent outil pour créer ses cartes en ligne (Flash 6 nécessaire), proposé par Eric Mauvière et Isabelle Coulomb.', 'Geoclip.net/fr', ' logiciel géographie gratuit cartes ', 'http://www.geoclip.net/fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (646, 1, '2006-08-03 17:29:05', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 9 ', ' 3 ', 'Site Insee du recensement de la population', 'Vous pouvez choisir un thème d''étude, choisir une zone géographique, choisir un produit en ligne', 'INSEE', ' géographie INSEE population ', 'http://www.recensement.insee.fr/RP99/rp99/page_accueil.paccueil', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (647, 1, '2006-08-03 17:45:11', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 8 ', ' 10 ', ' 4 ', 'Un moteur de recherche &quot;Mozmot&quot; pour les synonymes, les expressions, l', 'Un moteur qui rend bien des services pour chercher des synonymes ou des expressions à partir d''un mot.\r\nPar exemple pour le mot &quot;manger&quot;, Mozmot te donne comme synonyme : casser la croûte, bâfrer, croquer, bouffer, dévorer, dépenser, se nourrir, se sustenter, chipoter, nourriture, s''empiffrer, grignoter.\r\nSuggestions : manger au râtelier, manger avec un lance-pierres, manger comme quatre, bien manger...\r\nUn tableau de conjugaison est accessible pour les verbes.', 'mozmot Moteur de recherche © Abondance, Brioude-Internet et Raynette  en partenariat avec Google', ' moteur synonymes expressions dictionnaire conjugaison ', 'http://www.mozbot.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (648, 1, '2006-08-03 18:04:20', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 10 ', ' 4 ', 'Dictionnaire en ligne d''Alexandria', 'Dictionnaire', 'Alexandria', ' dictionnaire définitions traductions ', 'http://www.sensagent.com/fr/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (649, 1, '2006-08-03 18:12:52', '0000-00-00 00:00:00', 0, 0, ' ', ' 13 ', ' ', ' 1 ', 'Alexandria , un logiciel pour pouvoir traduire ou définir le sens d''un mot  sur', 'Alexandria à chaque fois que le besoin s''en fera sentir vous fournira: traductions vers votre langue, définitions et synonymes dans la langue d''origine...Vous rencontrez parfois des difficultés face à un terme technique ou rare?  Comptez sur Alexandria pour faire le possible pour vous renseigner en un instant. D''une manipulation rapide de votre souris, obtenez l''information que vous recherchez. A tout moment, vous pouvez rechercher la définition d''un mot en ligne \r\n\r\n\r\n\r\nRien de plus simple et immédiat que de double-cliquer! Si la page web que vous consultez a fait le nécessaire, Alexandria se mettra à votre service.', 'Contributeurs d''Alexandria  Memodata  Wordnet 2.0 Copyright 2003 by Princeton University. All rights', ' dictionnaire traducteur ', 'http://www.memodata.com/2004/fr/index.shtml', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (650, 1, '2006-08-03 18:30:26', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'Dictionnaire du français MEMODATA', 'Le dictionnaire de français de MEMODATA comporte plus 250,000 entrées avec de l''ordre de 450,000 traductions.  En version papier, il représenterait plus de quatre mille pages.  Des suppléments sont en outre apportés par de nombreuses terminologies propres à des domaines spécifiques (médecine, documentation etc.).\r\nIl y a également un outil pour la conjugaison intégré ainsi qu''un outil d''analyse grammaticale du mot.', 'Mémodata', ' dictionnaire grammaire vocabulaire ', 'http://www.sensagent.com/dictionnaires/fr-fr/dictionnaire/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (651, 1, '2006-08-03 18:33:45', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 1 ', 'Ajouter Alexandria à la barre de Firefox', 'Alexandria est un outil qui est intéressant pour les élèves de cycle 3 qui peuvent à tout moment, rechercher la définition d''un mot dans un texte du Web. On peut également faire des recherches sur les mots ( sens d''un mot, analyse grammaticale, conjugaison d''un verbe), mais aussi trouver un synonyme.', 'Alexandria', ' dictionnaire conjugaison vocabulaire grammaire ', 'http://www.sensagent.com/fr/alexandria/plugin/plugin-recherche-firefox.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (652, 1, '2006-08-03 18:58:31', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 9 ', ' 1 ', 'Mappy .. un site pour plans et itinéraires en  France et en Europe', 'Mappy.fr  - Plans et itinéraires pour la France et  l''Europe', 'Mappy', ' géographie plans ', 'http://www.mappy.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (653, 1, '2006-08-03 18:59:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 9 ', ' 1 ', 'Le site Maporama  pour des plans du monde entier', 'Des plans du monde entier pour trouver une adresse ou tracer un itinéraire.', 'Maporama', ' géograohie trajet itinéraires plans ', 'http://world.maporama.com/idl/maporama/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (654, 1, '2006-08-03 19:03:51', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 9 ', ' 1 ', 'Bottin cartographe', 'Carte et  Plan. Calcul d''itinéraire entre 2 villes. Trajet RATP à Paris et en ile de France', 'Planfax', ' géographie carte plan ', 'http://www.1bis.com/1bis/default.asp', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (655, 1, '2006-08-03 19:22:17', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 9 ', ' 9 ', ' 1 ', 'Site pour avoir des vues aériennes de votre ville et des documents historiques', 'La plus grande collection de photos et illustrations anciennes du Net ! Entrez votre ville et découvrez tous les documents des XVIIIe, XIXe et XXe siècles qui la concernent : photos, gravures, cartes, livres...\r\nVilles &amp; Villages\r\nHistoire des Villages-Mémoires des Régions-Cartes Anciennes-La France en 1850-La France en 1750-La France en 1600- Images de France-Cartes Postales 1900-Gravures Anciennes-La France Vue du Ciel', 'notrefamille.com', ' géographie vues aériennes photos cartes postales ', 'http://www.notrefamille.com/v2/services-villes-villages/default.asp', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (656, 1, '2006-08-03 19:34:17', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 1 ', 'Chronocartes pour créer automatiquement des cartes animées,', 'Chronocartes est un outil, librement téléchargeable, pour créer automatiquement des cartes animées, au format gif.\r\nOn peut ainsi réaliser une image animée à partir de séries statistiques récupérées sur Internet', 'Chronocartes', ' géographie cartes animation ', ' http://www.ac-reims.fr/datice/hist-geo/chronocartes/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (657, 1, '2006-08-03 20:02:49', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 4 ', 'Puzzle des départements français en ligne', 'Jeu animé visant à reconstituer le puzzle des départements français.Possiblité de varier le niveau en indiquant ou pas le contour des départements ou des régions', 'Stéphane Guillot.', ' cartes géographie départements régions ', 'http://splaf.free.fr/puzzle.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (658, 1, '2006-08-03 20:03:33', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Familles en ligne ...', 'Conseil aux jeunes et aux plus jeunes et aux parents sur l''utilisation d''internet.\r\nUne charte familiale', 'Ministère  délégué à la famille', ' internet usages charte ', 'http://www.pointdecontact.net/famille/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (659, 1, '2006-08-03 20:13:05', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le sénat Junior', 'Tu peux faire un voyage virtuel au sénat. Dessin animé sur la loi et toi.Portrait de sénateurs, interviews, journée d''un sénateur. Tu pourras découvrir le  palais du Luxembourg, une véritable ville .Il y a  un salon de coiffure, une lingerie, une salle de sport, une infirmerie, un labo-photo, un atelier d''ébénisterie, un poste souterrain de sécurité. Place ton curseur sur les pièces. Sénatin,le héros du site t''apprend ce qu''est la démocratie. Tu apprends comment fonctionne le sénat. Tu peux avoir accès à des dossiers comme &quot;l''environnement&quot;, la discrimination, le racisme ou la violence.', 'junior.senat', ' sénat loi démocratie palais Luxembourg lecture racisme discrimination environnement ', 'http://www.junior.senat.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (660, 1, '2006-08-03 20:18:44', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Les symboles de la répubique : le drapeau français pour le cycle 3', 'Emblème national de la Vème République, le drapeau tricolore est né de la réunion, sous la Révolution française, des couleurs du roi (blanc) et de la ville de Paris (bleu et rouge). Aujourd''hui, le drapeau tricolore flotte sur tous les bâtiments publics ; il est déployé dans la plupart des cérémonies officielles, qu''elles soient civiles ou militaires.Tout savoir sur le drapeau français.', 'site de l''Elysée', ' histoire citoyenneté république drapeau ', 'http://www.elysee.fr/elysee/francais/les_symboles_de_la_republique/le_drapeau_francais/le_drapeau_fr', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (661, 1, '2006-08-03 20:19:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Site de l''UNICEF, la voix des jeunes', 'Site pour les enfants traitant des points suivants ;\r\nDroits de l'' enfant     Connais-tu tes droits ?Education Pourquoi tous les enfants ne vont-ils pas à l''école ? Eau, assainissement et environnement Comment peux-tu aider à prévenir les maladies liées à l''eau ? Les médias Quelle est la couverture des jeunes dans les médias ?Objectifs du Millénaire Comment faire pour mettre fin à la pauvreté ? VIH et le SIDA, l''exploitation sexuelle des enfants à des fins commerciales. Comment mettre fin à  l''exploitation sexuelle des enfants ? La Situation des enfants dans le monde Comment atteindre  les enfants les plus vulnérables ? Forum de discussion', 'UNICEF', ' UNICEF droits enfants pauvreté eau environnement médias lecture SIDA forum ', 'http://www.unicef.org/voy/french/explore/explore.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (662, 1, '2006-08-03 20:23:46', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'La maison des droits de l''enfant', 'Tu as un travail de recherche à faire\r\n        * sur les droits du mineur dans des situations\r\n          - de maltraitance\r\n          - d''adoption\r\n          - de délinquance\r\n          - de changement de nationalité...\r\n        * sur les problèmes de société\r\n          - les sectes\r\n          - la toxicomanie\r\n          - la violence... \r\n\r\n  Tu vas trouver à la maison des droits de l''enfant\r\n une documentation spécialisée sous forme de livres, de revues, de dossiers thématiques', 'maison des droits de l''enfant', ' droits enfants maltraitance adoption délinquance violence toxicomanie sectes ', 'http://www.droits-et-enfants.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (663, 1, '2006-08-03 20:31:07', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Sécurité routière pour les enfants', 'Pour appprendre la sécurité tout en s''amusant. A toi de dire si la situation est dangereuse ou pas', 'Renault', ' sécurité route danger prévention ', 'http://www.securite-pour-tous.com/enfants/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (664, 1, '2006-08-03 20:34:02', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Sites des monuments français', 'Tu cliques sur un monument de la frise qui se déroule devant toi. Tu sauras tout sur le monument.\r\nIl y a un lien vers le site d''un ministère, vers un forum pour en savoir plus...', 'culture.fr centre des monuments nationaux', ' histoire monuments ', 'http://www.monum.fr/prehome/prehome.dml', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (665, 1, '2006-08-03 20:40:20', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Histoire de l''esclavage', 'Un site pour jouer et discuter sur le thème de l''esclavage.Découvre l''histoire de l''esclavage en Martinique en retrouvant les origines d''un jeune étudiant au travers d''un jeu ludo-éducatif...Joue au jeu ludo-éducatif ou discute\r\n   Si tu as envie de faire partagertess opinions sur l’esclavage, le forum est pour toi.', 'esclavage-martinique.com', ' esclavage histoire lecture ', 'http://www.esclavage-martinique.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (666, 1, '2006-08-03 20:49:47', '0000-00-00 00:00:00', 0, 0, ' ', ' 8 ', ' ', ' 1 ', 'QCM  un logiciel de création et d''utilisation de questionnaires à choix multiple', 'Réalisé en collaboration avec Jean-Claude Boudaud de l''association Prévention MAIF 17, il répond au cahier des charges suivant:\r\n\r\n    *      Utilisation simple, en autonomie, par des enfants lecteurs (cycles 2 &amp; 3), de questionnaires existants.\r\n    *       Chaque questionnaire est constitué de 30 questions au plus. Chaque question présente une illustration (photo, dessin...) et une série de propositions (5 au plus). La réponse peut être unique, ou bien multiple. En cas d''erreur, un commentaire explicatif peut être affiché.\r\n    *       Possibilité de création de questionnaires par des enfants de cycle 3, à partir de travaux de classe, reportages, visites, etc... grâce à un éditeur simple. La construction d''un questionnaire à partir d''un travail de recherche est une démarche très intéressante sur le plan pédagogique: remise en forme, mémorisation, recherche de réponses erronées, le tout sous une forme ludique car on invente un jeu pour les copains.', 'MAIF Jean-Claude Boudaud', ' logiciel gratuit QCM ', '', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (667, 1, '2006-08-03 20:51:58', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 18 ', ' 4 ', 'Les panneaux ... un logiciel pour apprendre le code de la route', 'Panneaux est un logiciel d''apprentissage du code de la route pour les élèves du cycle 3.\r\nIl comporte deux parties :\r\n   1. Reconnaissance et classement de panneaux par familles : interdiction, obligation, etc... avec un tirage aléatoire de la situation de départ.\r\n   2. Un atelier de construction. L''élève doit fabriquer le panneau demandé en choisissant le bon fond, puis la bonne illustration.', '', ' logiciel gratuit freeware danger code sécurité ', 'http://primtice.apinc.org/index.php?rub=Article&amp;a=25', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (668, 1, '2006-08-03 20:54:04', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel Tangram Meuh', 'Tan Gram Meuh est un logiciel d''initiation au célèbre puzzle permettant:\r\n\r\n    * la résolution de problèmes,\r\n    * la création de nouveaux problèmes,\r\n    * l''échange de problèmes par e-mai\r\nUtilisable au cycle 2 et au cycle 3', '', ' mathématiques cycle2 cycle3 tangram ', 'http://primtice.apinc.org/index.php?rub=Article&amp;a=31', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (669, 1, '2006-08-03 20:59:24', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 20 ', ' 1 ', 'Logiciel Coin écoute scolaire pour bien stocker le son', '- interface simple et facile à utiliser. les enregistrements sonores sont regroupés par thème ou par auteur. avance rapide, volume et balance sont accessibles d''un clic.\r\n-un espace info interactif (page web) est accessible durant la lecture. il peut être local uniquement, ou bien renvoyer sur internet (site dédié à l''auteur, par exemple). C''est en fait un mini-site, facilement réalisable avec n''importe quel logiciel de création de pages web. Ce peut être aussi une simple photo.', '', ' logiciel écoute coin ', 'http://primtice.apinc.org/index.php?rub=Article&amp;a=17', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (670, 1, '2006-08-03 21:02:41', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Labygen, un logiciel pour générer des labyrinthes', 'Labygen est un générateur de labyrinthes paramétrable : taille, difficulté, solution visible ou non.\r\nLes fiches obtenues peuvent être sauvegardées et/ou imprimées.\r\nSelon la complexité des labyrinthes générés, Labygen peut être utilisé de la maternelle au cycle 3.', 'Primtice', ' logiciel gratuite labyrinthe orientation ', 'http://primtice.apinc.org/index.php?rub=Article&amp;a=9', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (671, 1, '2006-08-03 21:06:29', '0000-00-00 00:00:00', 0, 0, ' ', ' 12 ', ' ', ' 1 ', 'ReDim pour redimensionner facilement les photos', 'ReDim est un petit utilitaire permettant d''effectuer plusieurs traitements sur une série de photos numériques en une seule opération :\r\n    * redimensionnement à largeur ou hauteur fixe.\r\n    * redimensionnement à dimension maximum.\r\n    * accentuation des contours (utile pour les fortes diminutions)\r\n    * réglage de la qualité JPEG', 'primtice', ' images traitement dimension ', 'http://primtice.apinc.org/index.php?rub=Article&amp;a=30', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (672, 1, '2006-08-03 21:08:49', '0000-00-00 00:00:00', 0, 0, ' ', ' 12 ', ' ', ' 1 ', 'Wave-Mp3  pour convertir des fichiers wave au format Mp3', 'Wave-Mp3 est un petit utilitaire permettant de convertir une série de fichiers wave au format Mp3.\r\nIl utilise l''encodeur GPL Lame (inclus dans Wave-Mp3)', 'primtice christian.vinent@ac-poitiers.fr', ' sons traitement logiciel utilitaire ', 'http://primtice.apinc.org/index.php?rub=Article&amp;a=32', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (673, 1, '2006-08-03 21:18:22', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 9 ', ' 10 ', ' 1 ', 'Logiciel Autodiapo', 'Autodiapo, petit logiciel très simple d''utilisation. il suffit de le copier dans le dossier regroupant les photos à projeter et de le lancer. Aucun paramétrage n''est nécessaire. Pas de scénario à écrire. Pilotable à la souris, il dispose uniquement des fonctions de base de ce type de programme :\r\n    * lecture des formats bmp, jpg, gif et gif animés. Les images sont automatiquement recadrées en plein écran. (800x600)\r\n    * musique d''ambiance au format wav ou mid. (volume on/off)\r\n    * passage des photos dans l''ordre alphanumérique.\r\n    * enchaînements choisis aléatoirement. (parmi 23)\r\n    * mode automatique (par défaut) ou manuel (pour commentaire).\r\n    * pilotable à la souris.\r\n    * Menu accessible en amenant le curseur-souris dans le coin &quot;haut-gauche&quot; de l''écran.', 'Christian Vinent', ' logiciel libre gratuit diapositives diaporama ', 'http://primtice.apinc.org/index.php?rub=37', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (674, 1, '2006-08-03 21:29:26', '0000-00-00 00:00:00', 0, 0, ' ', ' 8 ', ' ', ' 1 ', 'Logiciel Photoweb', 'Photoweb a été conçu pour répondre aux objectifs suivants:\r\n  - Palette des outils de base indispensables à la photo numérique.\r\n  - Simplicité d''utilisation pour une initiation des élèves et/ou de l''enseignant(e) au traitement des images numériques.\r\n  - Gratuité pour les écoles.\r\nLogiciel libre et gratuit.', 'christian.vinent@ac-poitiers.fr', ' logiciel libre gratuit traitement images ', 'http://primtice.apinc.org/index.php?rub=42', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (675, 1, '2006-08-03 21:50:09', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Contes de Noël en ligne', 'Les élèves des Chatentes vont lisent leurs contes de Noël.', 'Elèves de CM de l''école de Moragne (17)', ' contes Noël ', 'http://primtice.apinc.org/index.php?rub=Article&amp;a=19', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (676, 1, '2006-08-03 21:54:12', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 1 ', 'Histoire du vélo pour les élèves du cycle 3', 'Photographie et textes simples sur le thème des vélos et de son histoire.', 'christian.vinent@ac-poitiers.fr', ' histoire vélos bicyclette ', 'http://primtice.apinc.org/cyclades/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (677, 1, '2006-08-03 22:30:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 10 ', ' 1 ', 'La recherche documentaire au cycle 3', 'Ce document se compose d''une partie technique et pédagogique et d''une série de fiches de recherche utilisables en classe. Les fiches sont extraites des &quot;Défis Internet du Chapus&quot;.\r\nPour suivre les &quot;défis Internet&quot; et éventuellement vous inscrire, visitez la page suivante :\r\nhttp://web-ia.ac-poitiers.fr/web17/LeChapus/index_defis.php \r\nLivret recherche documentaire au cycle 3 téléchargeable sur Internet (1Mo)', 'ac-poitiers', ' internet B2i recherche documentaire cycle3 ', 'http://primtice.apinc.org/index.php?rub=Article&amp;a=33', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (678, 1, '2006-08-04 14:01:40', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 10 ', ' 4 ', 'Site &quot;Eco-emballages&quot; Juniors pour le cycle 3', 'Outils en ligne du petit citoyen.\r\nEspace interactif du site consacré pour comprendre et apprendre à être bons citoyens. Zoom sur 25 pays de l''union européenne.Guide, puzzles,cartes et fiches et techniques. Jeux et concours.La newsletter des petits citoyens.Informations pratiques et conseils.Magazine les petits citoyens pour les 7 au 11 ans pour apprendre la citoyenneté.Dictionnaire des petits citoyens.', 'Dominique Sibon et l''association les petits citoyens', ' citoyenneté magazine Europe dictionnaire lecture jeux concours jeux environnement ', 'http://www.lespetitscitoyens.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (679, 1, '2006-08-04 14:10:22', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 13 ', ' 4 ', 'Documents et jeux mathématiques pour les cycles 2 et 3', 'Documents de travail à télécharger  pour les jeux d''Ermel CE1 - pour les jeux d''Ermel CE2, pour les jeux d''Ermel CM.\r\nPrésentation sous la forme de fiches &quot;élève&quot; à décompresser.', 'académie de Grenoble', ' exercices calcul jeux Ermel ', 'http://www.ac-grenoble.fr/robespierre/ressourc/math/math.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (680, 1, '2006-08-04 15:32:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Le matou matheux, des exercices de mathématiques en ligne pour le cycle 3', 'Un site pout t''entrainer en mathématiques avec des logiciels en ligne. Des exerices interactifs d''entrainement en numération sur les les nombres entiers, la multiplication, les tables de multiplication, des problèmes, les graphiques, des constructions, la symétrie axiale, avec un quadrillage, la division, les nombres décimaux, les unités de mesure, la division, les fractions, les pourcentages, la proportionnalité, les graphiques, calcul mental;les unités de mesure, les figures géométriques, la symétrie axiale, les périmètres et les aires, les angles, des constructions.', 'matoumatheux', ' mathématiques calcul exercices logiciel gratuit ', 'http://matoumatheux.info/accueil.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (681, 1, '2006-08-04 14:15:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 13 ', ' 10 ', ' 1 ', 'Site &quot;Eco-emballages&quot; pour les enseignants', 'Espace interactif du site consacré pour comprendre et apprendre à être bons citoyens. Zoom sur 25 pays de l''union européenne. Pour chaque rubrique du site, l''enseignant peut accéder à des fiches pédagogiques avec des situations  ou des exercices à proposer aux élèves.', 'Dominique Sibon et l''association les petits citoyens', ' citoyenneté magazine Europe dictionnaire lecture jeux concours jeux environnement ', 'http://www.lespetitscitoyens.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (682, 1, '2006-08-04 15:41:29', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Le matou matheux, des exercices pour s''entraîner aux échecs', 'Des exercices interactifs en ligne pour apprendre à jouer aux échecs.\r\nDeux niveaux d''exercices.', 'Matoumatheux', ' exercices calcul jeux échecs ', 'http://matoumatheux.info/echecs/accueil.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (683, 1, '2006-08-04 15:42:58', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 13 ', ' 4 ', 'Le dictionnaire de mathématiques du Matoumatheux', 'Le dictionnaire pour comprendre la définition des mots en mathématiques ou calcul.', 'Matoumatheux Ruhlmann', ' mathématiques dictionnaire définitions ', 'http://matoumatheux.info/dictionnaire/accueil.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (684, 1, '2006-08-04 15:48:13', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Le Développement durable.. pourquoi une exposition pédagogique pour tous', 'Une exposition pédagogique de 22 affiches sur le Développement Durable\r\nmise gratuitement à disposition de toutes les écoles, collèges et lycées de France.\r\n50 000 établissements concernés. Un public de 10 millions d''enfants et de leurs parents.', 'Un projet de Yann Arthus-Bertrand / Association GoodPlanet.org', ' environnement développement durable exposition affiches ', 'http://www.ledeveloppementdurable.fr/main.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (685, 1, '2006-08-04 15:54:17', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 13 ', ' 4 ', 'Récréomath ... un site pour les mathématiques récréatives', 'Ce site est consacré aux  mathématiques récréatives. Il contient une banque de problèmes récréatifs et une banque d''outils mathématiques, une banque de jeux de société. Banque de problèmes récréatifs. Banque d''outils mathématiques. Vous allez pouvoir vous récréer et relever des défis en mathématiques. Vous goûterez aux mathématiques récréatives et pourrez améliorer votre performance en résolution de problèmes. Tu as un aide mémoire à ta disposition.\r\n\r\n 	\r\n\r\n	\r\n\r\nDétente', 'Charles-É. Jean,', ' jeux logique mathématiques mémoire problèmes outils ', 'http://www.recreomath.qc.ca/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (686, 1, '2006-08-04 15:58:13', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Réaliser une figure au cycle 3  avec le logiciel de géométrie instrument poche', 'Tu peux construire des figures géométriques avec les instruments à l''écran. Utilisable au cycle 3.', 'Equipe de math en poche', ' géométrie logiciel instrument dynamique libre ', 'http://instrumenpoche.sesamath.net/IMG/instruments.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (687, 1, '2006-08-04 16:04:38', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Des animations en ligne pour apprendre à  faire des constructions géométriques', 'Avec ce lien et les logiciels en ligne, tu pourras trouves des animations pour apprendre à tracer des perpendiculaires, des parallèles, un triangle, un carré, un rectangle, un losange, un parallèlogramme avec une équerre, une règle, ou avec une règle et un compas. Utilisable dès le cycle 3 (CE2,CM1,CM2)', 'pcolleu@free.fr', ' géométrie construction figures tracer équerre compas cycle3 ', 'http://pcolleu.free.fr/maths/flash/geometrie.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (688, 1, '2006-08-04 16:15:57', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 13 ', ' 4 ', 'Exercices de mathématiques autocorrectifs en ligne en calcul au cycle 3', 'Exercices pour chaque  chapitre du programme de mathématiques au cycle 3 ( CE2,CM1,CM2).\r\nDes exercices avec des corrections.', 'mathématiques;exercices;corrections;corrigés', ' mathématiques exercices corrections ', 'http://pcolleu.free.fr/maths/Maths-Index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (689, 1, '2006-08-04 16:25:11', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Exercices de grammaire et de conjugaison autocorrectifs  pour le  cycle 3', 'Ce site propose des exercices de grammaire et de conjugaison en ligne destinés à des enfants de CM1 et CM2. .A faire sur papier...ou à faire à l''écran...', 'Philippe Colleu', ' exercices grammaire conjugaison ORL ', 'http://pcolleu.free.fr/grammaire/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (690, 1, '2006-08-04 16:33:49', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Automaths ...S''entraîner en ligne  pour le calcul mental', 'Tu accèdes aux différents exercices permettant de travailler le calcul mental.\r\n Calcul mental : addition, soustraction, multiplication et division.\r\nTables de multiplication ( 2 à 9 )\r\nTables de multiplication à l''envers\r\nCalcul et ordre de grandeur\r\nLe compte est bon', 'Emilien Suquet', ' mathématiques calcul mental addition soustraction multiplication division ', 'http://www.automaths.com/?rub=192', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (691, 1, '2006-08-04 16:37:34', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 13 ', ' 4 ', 'Automaths ...S''entraîner en ligne  à construire des figures au cycle 3', 'Pour chacune des constructions suivantes, une explication en images t''es proposée.\r\n	-Milieu d''un segment\r\n	-Parallèle à une droite passant par un point\r\n  	-Perpendiculaire à une droite passant par un point\r\n Utilisable pour le cycle 3', 'Emilien Suquet', ' mathématiques géométrie construction ', 'http://www.automaths.com/?rub=191', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (692, 1, '2006-08-04 16:47:49', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 13 ', ' 4 ', 'Exercices en ligne du chat bleu', 'CP\r\nRepérages-Déplacements-Comparer-Les nombres jusqu''à 10-Additions-Les formes\r\n\r\nCE\r\nAdditions-Repérages-Lire l''heure\r\nExercices d''orthographe et de grammaire\r\nPour le CP,CE;CM1', 'Le chat bleu', ' calcul exercices grammaire orthographe ', 'http://www.chatbleu.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (693, 1, '2006-08-04 16:54:47', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 13 ', ' 4 ', 'Opérations en ligne du CP au CM2', 'Un site pour t''entraîner à faire des opérations. C''est interactif  et tu as ton score de réussite', 'hmido29@hotmail.com', ' mathématiques techniques opérations additions ', 'http://sites.rapidus.net/celmarti/maths.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (694, 1, '2006-08-04 16:59:09', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Les mathématiques ...&quot;forêt&quot; y voir ... pour le cycle 3', 'Durant sa visite au cœur de la forêt boréale, tu es accompagné(e) d’une petite fillette du prénom de Mathilde. À l’occasion, le personnage te renseigne sur des informations qui lui sont souvent transmises par sa tante ingénieure. Mathilde est soucieuse de la qualité de l’environnement de la forêt boréale. Dans les problèmes de mathématiques, Mathilde te donne les consignes  par l''intermédiaire de sa tante. Des textes sur &quot;La transformation et l’utilisation du bois&quot;.Tu découvres l''industrie des pâtes et papiers.Tu as des exerciseurs pour  t''entraîner.', 'Gouvernement du Québec', ' environnement forêt industrie papier exerciseurs ', 'http://www.madie.qc.ca/math/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (695, 1, '2006-08-04 17:07:26', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Le cycle de l''eau et les réservoirs', 'Un site pour découvrir un dossier sur le thème de l''eau.', 'CNRS et Sagasciences', ' eau environnement cycle ', 'http://www.cnrs.fr/cw/dossiers/doseau/decouv/cycle/menuCycle.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (696, 1, '2006-08-04 17:10:02', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le cycle de l''eau à travers le conte de conte-goutte au cycle 3', 'Conte-Goutte  t’invite à prendre place dans la fabuleuse histoire de sa vie. Si tu acceptes de te mettre dans sa peau, tu vivras des transformations toutes plus extraordinaires les unes que les autres. Tu as un lexique pour t''aider.Tu pourras faire quelques expériences qui te permettront de mieux comprendre les transformations que l’eau subit dans son cycle naturel. Site utilisable dès le cycle 3. ( CE2,CM1,CM2)', 'Josée Brulotte et Sébastien Matte', ' eau histoire lecture sciences cycle glace vapeur évaporation cycle 3 ', 'http://www.cscapitale.qc.ca/st-bernard/decouvre/cycle/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (697, 1, '2006-08-04 17:14:12', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 10 ', ' 4 ', 'Les différents états de l''eau ... un site de France5', 'Un site pour découvrir et comprendre les différents états de l''eau, les changements d''état de l''eau, le cycle de l''eau.\r\nUtilisable dès le cycle 3.', 'France 5 Education', ' eau sciences cycle3 états changements ', 'http://education.france5.fr/MINTE/MINTE10977/page_10977_71571.cfm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (698, 1, '2006-08-04 17:18:45', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 16 ', ' 4 ', 'A la découverte du cycle de l''eau avec MétéoFrance', 'Un personnage qui ressemble à Donald va tout te faire comprendre sur le cycle de l''eau.\r\nUn site avec de belles animations. Il y a des quizs qui t''attendent à chaque étape du cycle.\r\nUtilisable au CE2,CM1,CM2.', 'météoFrance', ' eau lecture sciences cycle glace vapeur évaporation cycle 3 jeux ', 'http://www.meteofrance.com/FR/pedagogie/jeunesse_et_jeux/cycleau/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (699, 1, '2006-08-04 17:24:51', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 10 ', ' 4 ', 'Site &quot;Arboris&quot; pour déterminer simplement les arbres de nos régions.', 'Arboris est un site permettant de déterminer simplement les arbres de nos régions. Vous y trouverez également des fiches photos présentant tronc, rameau, feuille, fleur, fruit et bourgeon. Vous y trouverez également des fiches photos présentant tronc, rameau, feuille, fleur, fruit et bourgeon.', 'académie de Versailles', ' arbres biologie végétal ', 'http://www.ac-versailles.fr/pedagogi/svt/docpeda/banques/arboris/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (700, 1, '2006-08-04 17:26:38', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Site  &quot;ma forêt m''a dit&quot; au cycle 3', 'Site pour explorer et exploiter la forêt. Dès le cycle 3.\r\nDes activités et des ressources.', 'FRAPNA Région', ' forêt:lecture ', 'http://www.frapna.org/site/region/siteforet/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (701, 1, '2006-08-04 17:32:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 10 ', ' 4 ', 'Bricocité le site de la cité des sciences pour bricoler,s''informer', 'un site pour les enfants. Tu pourras tout savoir sur les planeurs, les grenouilles rainettes, et puis la menthe ...', 'cité des sciences', ' sciences bricolage lecture ', 'http://www.cite-sciences.fr/francais/web_cite/experime/bricocite/frame_brico_fran.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (702, 1, '2006-08-04 17:36:42', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'La fenêtre de l''univers... pour s''initier en astronomie', 'La Fenêtre de l''Univers va te  permettre d''en apprendre plus sur le Système Solaire : son étoile, ses planètes, ses astéroïdes et ses comètes. Vous pourrez aussi découvrir chacune des 88 constellations, et apprendre à en répérer une partie dans le ciel d''été.Tu peux aussi poser les questions auxquelles vous ne trouvez pas de réponse dans la rubrique Questions / Réponses.Tu trouveras surtout une imposante Galerie de Photos qui compte exactement 456 photos des merveilles de l''Univers, regroupées en catégorie. On t''explique l''éclipse du soleil.Utilisable dès le cycle 3.', 'Olivier - Webmaster de La Fenêtre de l''Univers', ' sciences astronomie lecture cycle3 ', 'http://funivers.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (703, 1, '2006-08-04 17:42:33', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 10 ', ' 4 ', 'L''encyclopédia des sciences de Google', 'L''encyclopédi@ des Sciences associe une encyclopédie traditionnelle à un environnement en ligne permettant d''accéder à des informations complémentaires. Le site Internet associé au livre a été créé en collaboration avec Google, le moteur de recherche le plus utilisé au monde. Il guide le lecteur vers les sites Internet les plus intéressants, les plus adaptés aux enfants et les plus étonnants.\r\nMatière et matériaux	Forces et énergie	Électricité et magnétisme	Espace	Terre	Plantes	Animaux	Corps humain', 'google, ERPI Jeunesse', ' sciences encyclopédie lecture ', 'http://encyclopedia.erpi.com/science/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (704, 1, '2006-08-04 17:47:24', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Défi pour la Terre un site pour le cycle 3', 'Avec Polo, relève le défi pour la Terre. Des informations. \r\nRelever le Défi pour la Terre, c’est s’engager  à réduire son impact sur l’environnement à travers des gestes simples et concrets au quotidien. Tu choisiras des défis à relever pour sauver la planète.', 'Adème;Fondation Nicolas Hulot', ' terre environnement sciences écologie lecture ', 'http://www.defipourlaterre.org/juniors/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (705, 1, '2006-08-04 18:07:58', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Découvrir l''univers brûlant des volcans avec France5', 'Un site pour découvrir les volcans. Tu seras invité(e) à passer dans le laboratoire interactif.Utilisable dès le cycle 3\r\n( CE2,CM1,CM2)', 'France 5 Education et Larousse', ' volcans sciences lecture cycle3 ', 'http://education.france5.fr/volcans/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (706, 1, '2006-08-04 18:14:09', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Vulcanoes ... un site pour découvrir les volcans', 'Un site où tu rentres avec les thématiques suivantes : un volcan comment cela marche ? Les grandes éruptions, les volcans du monde, la vie autour d''un volcan, les grands vulcanologues comme Haroun Tazieff;Katia et Maurice Krafft.', 'Jerôme Simans &amp; Benoit Arweiler', ' Tazieff Krafft volcans sciences lecture éruptions vulcanologues ', 'http://volcanoes.free.fr/indexl.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (707, 1, '2006-08-04 18:17:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Le site de Pascal Blondé ...un passionné des volcans', 'Pascal Blondé est un passionnée de volcans et il a fait un site sur ce thème.\r\nTu pourras voir tous ses reportages, ses photos et ses commentaires.', 'Pascal Blondé', ' volcans sciences lecture ', 'http://pascal-blonde.info/pblonde.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (708, 1, '2006-08-04 18:30:40', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Electropolis ... un site du musée Electropolis  pour aborder l''électricité', 'Ce site te propose des montages très simples à effectuer à la maison ou en classe. Ils sont pour chacun d''entre eux accompagnés d''un texte explicatif.Les phénomènes électrostatiques, la pile électrique, la danse des pantins, un électro-aimant;une lampe électrique.', 'Musée Electropolis', ' sciences électricité musée lecture cycle3 ', 'http://www.electropolis.tm.fr/frgenie.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (709, 1, '2006-08-04 18:34:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Jeux et expériences interactifs de la cité des sciences', 'Expérimentez avec des interactifs ludiques, jouez, explorez, bricolez... Faites le en ligne et si vous avez aimé, venez à la Cité pour en savoir plus, pour découvrir d''autres manipulations dans chacune de ses expositions.', 'cité des sciences', ' jeux physique biologie arts informatique bricolage environnement logique expériences ', 'http://www.cite-sciences.fr/francais/experiences_ligne.htm#', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (710, 1, '2006-08-04 18:39:31', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Les phases de la lune au cycle 3', 'Pourquoi la Lune brille-t-elle?\r\nTantôt ronde, tantôt fin croissant, la Lune change d''aspect tout au long du mois lunaire. Pourquoi?  Étant une boule suspendue dans l''espace, la Lune est toujours à moitié éclairée par le Soleil. La Lune tournant autour de la Terre, cette partie éclairée change continuellement d''apparence, d''où les phases de la Lune. Pour mieux visualiser ce phénomène, le site te propose d''utiliser une démonstration interactive.', 'musée de la civilisation', ' sciences astronomie cycle3 lecture expériences observations ', 'http://www.mcq.org/societe/phases_lunaires/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (711, 1, '2006-08-04 18:44:30', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Site &quot;les débrouillards&quot;', 'Un site pour faire des sciences. Des défis, des magazines en sciences, des expériences. Le Webbabillard avec des sciences en question.', 'club débrouillard', ' sciences magazines défis actualités ', 'http://www.lesdebrouillards.qc.ca/applicationWeb/pages/publique/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (712, 1, '2006-08-04 18:48:04', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'A quoi servent les emballages ? Un site de France5 pour le cycle 3', 'Un site dédié pour les emballages. De nombreuves vidéos. Tu peux rentrer dans le labo-interactif.Utilisable dès le cycle 3 (CE2,CM1,CM2)', 'France 5 Education', ' sciences environnement citoyenneté lecture emballages ', 'http://education.france5.fr/MINTE/MINTE10901/page_10901_71560.cfm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (713, 1, '2006-08-04 18:51:35', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Instruments et musiques du Moyen Age', 'Tu cliques sur un instrument du moyen âge  et tu as directement sur sa page, toutes les informations.\r\nTu peux écouter quelques extraits d''enregistrements de référence où chaque instrument est mis en valeur. Ces enregistrements sont de simples citations, d''une durée inférieure à 45 secondes.Représentation d''instruments dans l''art du moyen âge ( enluminures ...)', 'Christian Brassy', ' musique écoute histoire moyen âge enluminures ', 'http://www.instrumentsmedievaux.org/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (714, 1, '2006-08-04 18:59:13', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 9 ', ' 10 ', ' 4 ', 'Banque d''images sur les instruments de musique du moyen-âge', 'Une banque d''images sur les instruments de musique au moyen âge faites par l''auteur du site.', 'Christian Brassy', ' musique écoute histoire moyen âge ', 'http://www.instrumentsmedievaux.org/pages/depart.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (715, 1, '2006-08-04 19:58:28', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le labo d''instruments pour le cycle 2 et le cycle 3', 'Pour tester et écouter des instruments en ligne.\r\nFais l''expérience en ligne de tous vos instruments préférés! \r\nDans ce Labo d''instruments tu peux  écouter et manipuler « virtuellement » tous les instruments de l''orchestre.\r\nTu pourras te familiariser  avec l''orchestre tout entier. Pour le cycle 2 (CP,CE1) et le cycle 3 (CE2,CM1,CM2)', 'Centre national des Arts', ' musique instruments écouter manipuler cycle2 cycle3 ', 'http://www.artsalive.ca/fr/mus/instrumentlab/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (716, 1, '2006-08-04 20:04:17', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 11 ', ' 10 ', ' 4 ', 'Ecouter les baladiffusions et Posdcats', 'Répertoire de baladodiffusion du CNA.  Un balado est un fichier audio qu''il est possible de télécharger en format MP3 ou dans un format de balado rehaussé (AAC). On peut le télécharger et l’écouter en cliquant sur un lien dans une page Web.', 'Centre national des Arts', ' musique podcast baladodiffusion ', 'http://www.artsalive.ca/fr/mus/musicresources/podcasts.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (717, 1, '2006-08-04 20:07:27', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Des grands compositeurs en musique', 'Sélectionne le compositeur qui t'' intéresse le plus. Les textes rédigés à son sujet te  transporteront dans un autre pays et une autre époque.', 'Centre national des Arts', ' musique compositeurs ', 'http://www.artsalive.ca/fr/mus/greatcomposers/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (718, 1, '2006-08-04 20:21:33', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 13 ', ' 4 ', 'Flût@lors pour des cours de flûte en ligne', 'Tu apprends à jouer de flûte en ligne. Tu as les doigtés', 'webphonie@iquebec.com', ' musique instruments flûte ', 'http://www.csdraveurs.qc.ca/musique/flutalors/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (719, 1, '2006-08-04 20:29:38', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Les instruments du monde', 'Tu as découvrir un musée virtuel d''instruments de musique riche d''environ 150 pièces. Cette collection unique représente le fruit de nombreux voyages : du violon Er-hu chinois, en passant par la flûte Ney turque, la harpe Seng de Birmanie, près de 33 pays sont ainsi représentés.Tous ces instruments sont classés par continent puis par pays, mais vous pouvez également les rechercher suivant leur mode de mise en vibration', 'Alexis AUBRY', ' musique monde écoute ', 'http://www.instrumentsdumonde.com/index.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (720, 1, '2006-08-04 20:37:46', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'L''ABC décoiffé du dragon ailé', 'ABCdaire du moyen âge est très beau à voir. C''est un véritable chef d''oeuvre. Tu cliques sur une lettre ... une enleminure apparaît à l''écran avec un mot qui commence avec cette  lettre. Cela bouge .. c''est surprenant et surtout très beau. Utilisable du cycle 1 au cycle 3  selon les usages visés. Site permettant aussi un travail en arts', 'Bibliothèque de Rouen', ' ABCdaire lettres enleminures moyen âge abécédaire cycle1 cycle2 cycle3 ', 'http://www.haute-normandie.culture.gouv.fr/carte_blanche/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (721, 1, '2006-08-04 20:49:13', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 19 ', ' 4 ', 'ABCdaire en anglais', 'Un ABCEdaire très beau visuellement. Tu cliques sur une lettre. Puis ... effet de surprise... les lettres bougent et représentent un mot. Exploitable en arts .', 'bemboszoo.com', ' lettres ABCdaire anglais abécédaire ', 'http://www.bemboszoo.com/Bembo.swf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (722, 1, '2006-08-04 21:02:35', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Décodart ... Le sens caché de l''art', 'Décodart analyse, compare, explore des peintures de maîtres.Nature morte -La Joconde -Léonard de Vinci-Arcimboldo- Lumières dans la nuit-Picasso - Le rêve de Pierrot', 'France 5 Education', ' arts visuels analyse regard ', 'http://education.france5.fr/decodart/home.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (723, 1, '2006-08-04 21:04:30', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 11 ', ' 10 ', ' 4 ', 'Le cirque Plume', 'Site sur le cirque Plume. C''est plein de surprises et beau à voir.\r\n&quot;Le spectacle de cirque est un spectacle vivant.Le spectacle du Cirque Plume est fait par des vivants pour des vivants ;\r\nIl est joyeux, coloré, profond, poétique, sale, brouillon, précis, il est comme la vie.&quot; Bernard Kudlack', 'Cirque Plume - Tania Rinnert - Galliot.com 2Jean-Marie Jacquet, membre fondateur du Cirque Plume', ' cirque lecture ', 'http://www.cirqueplume.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (724, 1, '2006-08-04 21:16:25', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 10 ', ' 4 ', 'Paul Gaughin expliqué aux enfants', 'Un site de France 5 pour découvrir Paul Gaughin, son oeuvre, sa vie. Tu pourras visionner une vidéo , jouer en ligneret tester tes connaissances avec un quiz.Utilisable dès le cycle 3(CE2,CM1,CM2)', 'France 5 et la réunion des musées nationaux.', ' arts visuels Paul Gaughin vidéo ', 'http://education.france5.fr/gauguin/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (725, 1, '2006-08-04 21:19:55', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 10 ', ' 4 ', 'Miro  pour le cycle 2 et le cycle 3', 'Tu peux découvrir les oeuvres et la vie de Miro. Tu pourras accéder à une vidéo et un labo interactif.\r\nLe texte est simple et abordable dès le CE1. Exploitable au cycle 3(CE2,CM1,CM2)', 'Centre Pompidou et France 5', ' arts visuels Paul Miro vidéo ', 'http://education.france5.fr/miro/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (726, 1, '2006-08-04 21:25:48', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 10 ', ' 10 ', ' 4 ', 'Mystérieuse muraille de Chine', 'Pars à la découverte du muraille de Chine, extraordinaire construction. Ton parcours est jalonné d''énigmes que tu dois résoudre pour progresser.', 'France 5 Education', ' Chine géographie histoire lecture ', 'http://education.france5.fr/chine/jeux/muraille.swf', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (727, 1, '2006-08-04 21:27:35', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'L''aventure des écritures', 'Tout savoir sur les écritures.', 'BNF', ' écriture lettres histoire ', 'http://classes.bnf.fr/dossiecr/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (728, 1, '2006-08-04 21:30:17', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Petite histoire des écritures', 'Des textes assez simples et des illustrations sur les différentes écritures et alphabets', 'Patrick Bresson', ' écriture histoire alphabets ', 'http://perso.orange.fr/bressonpa/lesecritures.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (729, 1, '2006-08-04 21:33:34', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 12 ', ' 10 ', ' 3 ', 'Site sur les marionnettes', 'Présentation de différentes marionnettes.\r\nVous pouvez découvrir les multiples façons d''animer les marionnettes —', 'civilisations.ca', ' marionnettes théâtre langage oral expression ', 'http://www.civilisations.ca/arts/ssf/ssf00fra.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (730, 1, '2006-08-04 21:37:09', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'Reportages sur l''Himalaya', 'Site de France 5 avec des reportages sur l''Himalaya. Des interviews, de belles photos.', 'France 5 Education', ' géographie reportage lecture Himalaya ', 'http://www.france5.fr/himalaya/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (731, 1, '2006-08-04 21:43:45', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Récréalire', 'Des projets d''écriture et de lecture en ligne. Des textes de lecture de romans  et des questionnaires.Bienvenue dans le Coin des Auteurs et des Illustrateurs !  Cette zone présente de jeunes auteurs et illustrateurs pour la jeunesse.  Venez les découvrir !  Certains d''entre eux participent à des projets d''écriture en direction  des enfants.  Venez les rencontrer !', 'recrealire.net', ' lecture écriture questionnaires romans projets albums contes ', 'http://www.recrealire.net/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (732, 1, '2006-08-04 22:00:05', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 1 ', 'Des livres qui cliquent', 'Des exemples de pistes pour aborder la littérature Jeunesse à l''école; les albums; les oeuvres longues.\r\nActivités pédagogiques autour de la littérature Jeunesse', '/ecoles.uneq.qc.ca', ' littérature  lecture écriture romans livres albums ', 'http://ecoles.uneq.qc.ca/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (733, 1, '2006-08-04 22:07:34', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Cyberpoésies pour le cycle 2 et le cycle 3', 'Des poésies animées à lire à l''écran. Des jeux en ligne.', 'cyberpoesie.ne', ' poésies lecture jeux ', 'http://www.cyberpoesie.net/html/enfants.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (734, 1, '2006-08-11 10:01:07', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 16 ', ' 4 ', 'Le site Astro-nature', 'Avec ce site, on pourra observer le ciel et le comprendre\r\n-Observer notre étoile : le Soleil\r\n-La durée du jour. La durée de la nuit.\r\n -Les saisons sur la Terre.\r\n -La Lune et ses phases.\r\nDes documents intéressants pour aborder l''astronomie au cycle 3', 'Stephane.bourge@libertysurf.fr', ' astronomie lune étoile saisons jour:nuit ', 'http://stbourge.chez-alice.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (735, 1, '2006-08-11 15:29:07', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Un site pour apprendre le Français en s''amusant', 'Ce site permettra à des jeunes pratiquant déjà le français comme langue seconde ou langue étrangère, de continuer à progresser.Il peut être utilisé en classe avec l''aide du professeur ou bien en travail individuel à la maison.\r\nConvient également aux parents qui apprennent le français.', 'FR3 ,TV5, Apple, Radio Junior', ' FLE français langue orale écrite ', 'http://www.adodoc.net/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (736, 1, '2006-08-11 15:33:13', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Radio junior, la radio pour les enfants', 'Un site de radio dédié aux enfants.\r\nPour rechercher un sujet, un dossier, une recette, un artiste ou tout autre chose, tape un mot ou une phrase dans la case ci-dessous et ensuite, appuie sur le bouton &quot;chercher&quot;.Possibilité d''écouter des émissions pour les enfants.', 'radiojunior', ' radio moteur recherche dossier actualités ', 'http://www.radiojunior.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (737, 1, '2006-08-11 15:49:04', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Ecole de FLE en ligne', 'Bonjour de France, le magazine pour étudier le français en ligne, vous propose une série d''images centrée sur un thème comportant des éléments &quot;hypertexte&quot; qui renvoie à une explication et suivie d''un jeu afin de tester votre mémorisation. Les textes et exercices peuvent être librement utilisés en cours de français.\r\nUtilisable pour les parents de primo-arrivants qui veulent apprendre le français.', 'Ecole Azurlingua', ' FLE langue grammaire conjugaison vocabulaire compréhension ', 'http://www.bonjourdefrance.com/index/indexapp.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (738, 1, '2006-08-11 16:16:00', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Voyage virtuel au Maroc', 'Un site pour découvrir des villes et des particularités du Maroc.\r\nEcrit en Français. Utilisable pour l''ELCO', 'David Metivier', ' Maroc ELCO B2i ', 'http://davidmetivier.ifrance.com/index.html/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (739, 1, '2006-08-11 16:21:58', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le site du réseau In-Terre-Actif', 'Le Réseau In-Terre-Actif est un outil d’éducation à la citoyenneté permettant aux jeunes d’être sensibilisés aux enjeux mondiaux actuels et de jouer un rôle en solidarité internationale. C’est un lieu d’échange d’informations, d’engagement, d’animation, de jeux et de libre circulation des idées en faveur du développement durable et d’un monde plus juste et fraternel.Le Réseau In-Terre-Actif s’avère aussi être une boîte à outils pour les enseignants (3e cycle du primaire et secondaire). Il offre du matériel pédagogique portant sur des thématiques reliées, de près ou de loin, à l’exercice de la citoyenneté et à la solidarité internationale. Tu peux envoyer des articles à la salle de presse', 'in-terre-actif.com', ' citoyenneté solidarité presse actualités ', 'http://www.in-terre-actif.com/fr/index.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (740, 1, '2006-08-11 16:31:32', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel &quot;Je lis puis j''écris&quot;  Entraînement à l''écriture pour le cycl', 'Je lis puis j''écris proposé gratuitement par l’association PragmaTICE permet de s''entraîner à saisir précisément un mot, une expression, une phrase, un texte, un nombre, etc... après les avoir lus et mémorisés. \r\nLes éléments à saisir sont contenus dans un fichier texte (TXT) dont le nom est celui qui est attribué à l’exercice. Ce dernier se compose d’un ou plusieurs items (en général des phrases) qui sont présentés soit dans l’ordre aléatoire (par défaut), soit dans un ordre déterminé (s’il est inscrit ORDRE sur la première ligne du fichier texte). Chaque item peut être segmenté en empans afin d’être mémorisé puis saisi en plusieurs temps.', 'Eric Chenavier, Frédéric Mathy et Cédric Thivind', ' écriture lecture mémorisation copie dictée cycle2 cycle3 ', 'http://pragmatice.net/jelispuisjecris/telechargement.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (741, 1, '2006-08-11 16:39:26', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel  &quot;J''écoute puis j''écris&quot; pour le cycle 2 et le cycle 3', 'Le logiciel  &quot;J''écoute puis j''écris&quot;  est offert par l''association PragmaTICE. Il est gratuit et librement diffusable à certaines conditions. Ce logiciel permet de s''entraîner à saisir précisément un mot, une expression ou un nombre après l''écoute d''un message sonore, en s''aidant éventuellement d''une image et/ou d''un texte d''accompagnement. Si le message sonore est strictement l''expression à saisir, le logiciel entraîne l''élève à écrire sous la dictée en respectant l''orthographe. Principe du logiciel : l''élève écoute un message sonore, puis saisit sa réponse.', 'association PragmaTICE', ' dictée orthographe ORL ', 'http://pragmatice.net/jecoutepuisjecris/telechargement.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (742, 1, '2006-08-11 17:07:51', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Atelier d’écriture de contes de la BNF', 'L’atelier d’écriture de contes de la bibliothèque nationale de France vous propose d’inventer vous-même un récit à partir d''une sélection d''ingrédients d''un conte. Ecrivez-le ou improvisez en racontant l''histoire autour du fil conducteur et des illustrations qui vous sont proposés. Vous choisissez :\r\n    * Le héros ou l''héroine\r\n    * Le départ\r\n    * L''arrivée\r\n    * La raison\r\n    * Les épreuves\r\n    * Un pays inconnu\r\n    * Les rencontres\r\n    * L''objet magique\r\n    * La récompense\r\n    * La morale', 'BNF', ' contes écriture récits ', 'http://expositions.bnf.fr/contes/pedago/atelier/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (743, 1, '2006-08-11 17:13:27', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Les météorites : 2 aventures en bande dessinée interactive', '«Les météorites, l’aventure commence» et « Les météorites, l’aventure continue» sont deux bandes dessinées interactives réalisées en Flash.Une réalisation intéressante et de qualité pour les jeunes de 10 ans et plus...', 'Bienvenue Welcome Multimédia -Ministère de la culture et de la communication du Québec-Uni Mac-Gill', ' astronomie lecture bande dessinée ', 'http://meteorites.bw.qc.ca/main.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (744, 1, '2006-08-11 17:53:22', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 2 3 11 22 ', ' 1 ', 'Audioster ...un logiciel  pour apprendre l''anglais', 'Audioster est un nouveau service destiné aux apprenants de l''anglais qui souhaitent améliorer leur compréhension orale. Des exercices de compréhension basés sur des reportages de radios anglo-saxonnes à télécharger tous les jours. Les leçons traitent de sujets d''actualité très variés. En fin de cycle', 'Edulang', ' langue vivante anglais compréhension orale ', 'http://audioster.com/?langue=fr', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (745, 1, '2006-08-11 22:23:21', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 19 ', ' 4 ', 'Le logiciel Plopp ... un logiciel pour la 3 D pour les enfants', 'Un logiciel payant qui est une petite merveille permettant aux élèves de créer des dessins en 3 dimensions.\r\n14,95 EUR . Le logiciel est pour l''instant en Allemand mais les logos sont très évocateurs pour une utilisation fonctionnelle du logiciel.', 'Editeur Terzio', ' arts visuels logiciel payant ', 'http://www.planet-plopp.de/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (746, 1, '2006-08-12 00:01:12', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 2 3 11 22 ', ' 3 ', 'Site Educnet pour les langues vivantes', 'Portail pour l''enseignement des LV.\r\nDonnées générales sur l''enseignement des langues. De nombreux liens.', 'gouvernement Education Nationale', ' langues vivantes ', 'http://www.educnet.education.fr/dossier/langues/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (747, 1, '2006-08-12 00:04:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Alvéo un atelier de langue vivante en école ouverte', 'Si tu veux t’amuser, te distraire, créer tout en pratiquant des langues vivantes, suis-nous !\r\nTu te  retrouveras aux quatre coins de ce site et tu pourras  suivre des aventures.Tu cliques sur le drapeau de ton choix (ANGLAIS, ESPAGNOL, ALLEMAND)', 'CRDP de Toulouse', ' langues vivantes anglais espagnol allemand ', 'http://www.crdp-toulouse.fr/alveo/sommaire.php3', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (748, 1, '2006-08-12 00:08:50', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 3 ', ' 2 3 11 22 ', ' 1 ', 'Banque d''outils d''aide à l''évaluation diagnostique en langues vivantes', 'La plupart des outils proposés sont destinés aux élèves de cycle 3 (CM) et de 6e. Ils évaluent la compréhension de l''oral, compétence linguistique la plus sollicitée à l''école primaire. Ils s''appuient sur les nouveaux programmes de l''école (BOEN Hors Série n° 4 du 29 août 2002.', 'gouvernement Education Nationale', ' langues  vivantes Cycle3 évaluations ', 'http://www.education.gouv.fr/stateval/newoutil/langues.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (749, 1, '2006-08-12 00:12:54', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 2 3 11 22 ', ' 4 ', 'Jeux en Anglais-Word et pictures de la BBC', 'Un site dela BBC pour s''entraîner. Des jeux \r\nBuild a sandcastle and get to know the common spelling patterns for the Year 2 vowel phonemes.\r\nHelp Salty Sam write a postcard and get used to using words containing alternative spelling patterns for vowel phonemes in sentences.\r\nWatch how Magic Pencil writes some words containing vowel phonemes. Then have a go yourself.\r\nHave some fun playing the games and doing the activity sheets.', 'BBC', ' langues vivantes jeux anglais cycle3 ', 'http://www.bbc.co.uk/schools/wordsandpictures/index.shtml', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (750, 1, '2006-08-12 00:16:02', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 ', ' 2 3 11 22 ', ' 1 ', 'Comment construire une progression en langues vivantes au cycle 3', 'Un forum riche en pistes et en réflexions pour construire une progression ou une programmation.', 'Primlangue CIEP', ' langues vivantes progression ', 'http://www.primlangues.education.fr/forums/list.php?f=1', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (751, 1, '2006-08-12 09:44:33', '0000-00-00 00:00:00', 0, 0, ' ', ' 9 ', ' ', ' 1 ', 'Un espace en ligne pour des photos', 'Un espace dédié aux photographies.\r\nPossibilité de déposer des photos mais aussi de faire des recherches.', 'flickr.com', ' photos images albums ', 'http://www.flickr.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (752, 1, '2006-08-12 10:59:00', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 9 ', ' 2 3 11 22 ', ' 4 ', 'Dictionnaire en images (French-english)', 'Un dictionnaire avec des thèmes simples pour l''école élémentaire', 'enchantedlearning.com', ' langues vivantes images anglais ', 'http://www.enchantedlearning.com/languages/french/Hisfor.shtml', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (753, 1, '2006-08-12 11:05:52', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'S''entraîner en mathématiques avec un logiciel en ligne', 'Un site en anglais mais utilisable dans les classes. Entraînement pour les 4 opérations. La division;la soustraction;l''addition', 'edbydesign', ' mathématiques cycle2 cycle3 division opération soustraction addition logiciel gratuit ', 'http://www.edbydesign.com/maths/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (754, 1, '2006-08-12 11:10:22', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 5 ', ' 9 ', ' 1 ', 'Des puzzles en ligne à trois niveaux', 'Générateur de puzzles en ligne. L''élève choisit une image parmi les thèmes proposés. Il choisit son niveau de difficulté. Puis il reconstitue son puzzle.', 'edbydesign.com', ' jeux puzzle ', 'http://www.edbydesign.com/scrambler/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (755, 1, '2006-08-12 11:40:50', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 2 3 11 22 ', ' 4 ', 'Le site BabyKB.com pour apprendre l''anglais', 'Tu rentres dans une maison. Tu peux choisir une chambre que tu as envie de visiter.Tu cliques sur les objets de la chambre. On te dit en anglais ce que cela représente. On te dit des phrases.', 'BabyKB.com', ' vocabulaire anglais maison ', 'http://www.BabyKB.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (756, 1, '2006-08-12 12:35:50', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 2 3 11 22 ', ' 4 ', 'Ecouter des histoires en Anglais', 'Une série de libres lus par l''auteur du site', 'Stephen Cosgrove', '  ', 'http://www.bookpop.com/index2.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (757, 1, '2006-08-13 22:29:26', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 3 ', 'Google Scholar', 'Un moteur de recherche pour des articles de fonds et des productions  universitaires.\r\nGoogle Scholar permet d''effectuer facilement une recherche étendue portant sur des travaux universitaires. Vous pouvez, à partir d''un emplacement unique, explorer un grand nombre de domaines et de sources : articles revus par des comités de lecture, thèses, livres, résumés analytiques et articles. Ces travaux peuvent provenir de sources telles que des éditeurs scientifiques, des sociétés savantes, des référentiels de prépublication, des universités et d''autres organisations de recherche.', 'Google', ' pédagogie didactique enseignement moteur recherche ', 'http://scholar.google.com/schhp?hl=fr', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (758, 1, '2006-08-15 16:59:03', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel  1000 mots pour apprendre à lire', 'Ce logiciel propose 14 exercices de lecture (écoute, reconnaissance globale, décomposition syllabique) qui viennent enrichir les activités proposées au fil des leçons des principales méthodes de lecture qu''on trouve dans les classes. S''appuyant sur un imagier visuel et sonore de 1200 mots, il est fourni avec un éditeur qui permet de l''adapter au niveau des apprenants. Logiciel destiné aux enfants de CP mais aussi de GS, de CE1 et aux primo-arrivants. On le trouve également souvent en rééducation orthophonique. Logiciel en shareware.', 'Campaner', ' logiciel shareware lecture cycle2 ', 'http://perso.orange.fr/jm.campaner/jmc_1000MOTS/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (759, 1, '2006-08-15 17:01:23', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Thematic un ENT pour l''orthographe', 'Un environnement de travail complet pour développer et mutualiser des exercices par l''intermédiaire du Web. Le logiciel est fourni dans un premier temps avec une progression d''ORL pour le cycle III.\r\nIl est fourni avec 89 fichiers qui couvrent le programme d''ORL du premier trimestre du CE2. Cependant c''est principalement un programme auteur qui permet de créer et mutualiser des exercices et des progressions par l''intermédiaire du WEB. La licence shareware donne accès à la totalité des éditeurs d''exercices et à environ 300 exercices supplémentaires pour l''ORL au cycle III.30 Euros pour la licence', 'Campaner', ' logiciel shareware ORL orthographe ENT cycle3 payant ', 'http://perso.orange.fr/jm.campaner/jmc_Thematic/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (760, 1, '2006-08-15 17:06:59', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel de contrôle de lecture silencieuse', 'Pour inciter les élèves de cycle III et de Collège à aller au bout d''un roman. Idéal lorsqu''on n''a qu''un seul ordinateur en fond de classe. Le logiciel en version 32 bits réseau avec l''éditeur de questions pour créer ses propres questionnaires en plus des 212 romans disponibles.', 'Campaner', ' logiciel shareware ORL lecture ENT cycle3 ', 'http://perso.orange.fr/jm.campaner/jmc_cls/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (761, 1, '2006-08-15 17:09:00', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel  WEXR - les exerciseurs', 'En version 32 bits, ces exerciseurs multi-disciplines pour les cycles II, III et le Collège, sont répartis en 8 modules indépendants : Apparie, Associe, Désordre, Questionnaire, Reconstitution de texte/autodictée, Relie, exercices à Trous et Souligne. Ils sont fournis avec 627 exercices de base du CP au Collège. Ils intègrent une gestion des groupes d''élèves, des bilans ainsi qu''un éditeur permettant de saisir de nouveaux exercices.', 'Campaner', ' logiciel shareware ORL lecture cycle2 cycle3 gratuit ', 'http://perso.orange.fr/jm.campaner/jmc_Wexr/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (762, 1, '2006-08-15 17:11:58', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 9 ', ' 4 ', 'Logiciel  Edusprint', 'Dans Edusprint, deux joueurs d’âges différents s’affrontent par l''intermédiaire de questions adaptées sans que le plus jeune soit nécessairement pénalisé. Le jeu se présente sous la forme d’un circuit de course de voitures, chacun des deux pilotes s’efforçant de parvenir à la victoire en répondant à un questionnaire multimédia à choix multiple tout au long du parcours.', 'http://perso.orange.fr/jm.campaner/jmc_Edusprint/index.htm', ' logiciel shareware ORL lecture cycle2 cycle3 gratuit histoire géographie ', 'http://perso.orange.fr/jm.campaner/jmc_Edusprint/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (763, 1, '2006-08-15 17:36:09', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 10 ', ' 1 ', 'Présente3 un logiciel pour produire des CD-Roms', 'PRESENTE3 permet de réaliser des présentations multimédia destinées à être gravées sur un cédérom', 'Jean-Marc Campaner', ' multimédia logiciel shareware ', 'http://perso.orange.fr/jm.campaner/jmc_Presente3/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (764, 1, '2006-08-15 17:38:16', '0000-00-00 00:00:00', 0, 0, ' ', ' 8 ', ' ', ' 2 ', 'Loto des associations pour organiser un loto', 'Pour organiser un Loto au profit de votre coopérative, votre foyer socio-éducatif ou votre association, le logiciel (version 32 bits) génère des cartons de loto (à usage unique ou non) tous différents et permet de les imprimer à raison de 2, 3 ou 6 par feuille ainsi qu''une fiche de contrôle des gagnants.\r\nSi un ordinateur est présent dans la salle, il peut aussi contrôler automatiquement les gagnants en cours de partie et même assurer le tirage en évitant les ex-aequos !', 'Jean-Marc Campaner', ' coopérative loto logiciel shareware ', 'http://perso.orange.fr/jm.campaner/jmc_Loto/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (765, 1, '2006-08-15 17:41:12', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 4 ', 'Logiciel RomansTic', 'RomansTic travaille la compréhension du texte, mais aussi des notions orthographiques et grammaticales à partir de la littérature Jeunesse. Le premier roman à disposition est &quot;Un Serpent dans la Peau&quot; de Philippe Tassel, qui est également disponible gratuitement en téléchargement sur son site. Utilisable au cycle 3.', 'Jean-Marc Campaner', ' logiciel lecture roman freeware cycle3 ', 'http://perso.orange.fr/jm.campaner/jmc_RomansTic/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (766, 1, '2006-08-15 17:45:59', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 8 ', ' 4 ', 'Exploiter le roman &quot; Un serpent dans la peau&quot;', '&quot;Un serpent dans la peau&quot; (écrit par Philippe Tassel et illustré par Martine Belot) est un petit roman de 27 pages, réparti en cinq chapitres, pour les enfants à partir de 8 ans. Il vous est possible de télécharger des fiches de lecture, de dialoguer par courrier électronique avec l''auteur ou l''illustratrice...Prix du roman : un euro.\r\nRésumé : Rien ne va pour Pierre : tout le monde lui en veut ! C’est décidé, il va vivre dans SON monde à lui, sans personne pour l’ennuyer, avec tous les objets dont il a envie. Il n’y aura personne pour le gronder s’il fait des bêtises. Univers merveilleux ou vrai cauchemar ? Exploitable au cycle 3.', 'Philippe Tassel  Edition &quot;CRDP de Besançon&quot;', ' littérature roman cycle3 payant ', 'http://crdp.ac-besancon.fr/cddp70/serpent.htm', 'fr.png', 'N', 0, 'O');
