INSERT INTO `resource_online` VALUES (788, 1, '2006-08-26 17:21:15', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 16 ', ' 1 ', 'A la découverte des vivants', 'un scénario pour travailler sur le thème &quot;des vivants&quot;\r\nÀ la page d''accueil, cliquer sur &quot; Vivants &quot; + &quot; Vivant ou non &quot;. Vous trouverez les titres suivants : 1- Qu''est-ce qu''un vivant ? 2- Découvre les vivants ! 3- Les humains 4- Les animaux 5- Les plantes 6- Les vivants jouent à cache-cache !', 'station05.qc.ca', ' biologie vivant animaux plantes sciences ', 'http://station05.qc.ca/csrs/bouscol/propositions.asp?act=listepropo&amp;pkpropositions=3052', 'fr.png', 'N', 0, 'O');
