INSERT INTO `resource_online` VALUES (790, 1, '2006-08-26 17:50:29', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 10 ', ' 1 ', 'A la découverte des animaux', 'Scénrios pédagogiques.\r\nLes animaux dans leur milieu.\r\nReconnaître des caractéristiques de la vie animale\r\n- Titre du site : Bêtes pas bêtes +', 'Pierre Poirier et Sylvie Lussier', ' biologie animaux milieu sciences lecture ', 'http://station05.qc.ca/csrs/bouscol/propositions.asp?act=listepropo&amp;pkpropositions=2914', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (791, 1, '2006-08-26 17:56:22', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 10 ', ' 1 ', 'A la recherche des dinosaures', 'Ressources Internet - À la recherche des dinosaures (Projet TIC))\r\nhttp://ecoles.csbf.qc.ca/recit2/scenarios/dino.htm\r\n Objectifs et/ou Compétences\r\nÀ partir d’une lecture basée sur le thème «Les dinosaures et la préhistoire», j’ai ciblé leur intérêt en leur proposant de réaliser une recherche deux à deux.', 'dfortier@csbf.qc.ca', ' préhistoire histoire dinosaures ', 'http://station05.qc.ca/csrs/bouscol/propositions.asp?act=listepropo&amp;pkpropositions=3263', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (792, 1, '2006-08-26 18:00:32', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 10 ', ' 1 ', 'A la découverte du panda géant au cycle 3', 'Ressources Internet Le Panda géant http://www3.sympatico.ca/ahorth/\r\nObjectifs et/ou Compétences : Exploration de l’Internet.\r\nLecture d’un texte informatif et repérage d’informations précises.\r\nIl s’agit d’un site d’intérêt sur le panda géant. On y trouve sous forme de fiches, les informations relatives à ce mammifère.', 'richard_therrien@csrs.qc.ca', ' lecture cycle3 recherche animaux biologie ', 'http://station05.qc.ca/csrs/bouscol/propositions.asp?act=listepropo&amp;pkpropositions=2915', 'fr.png', 'N', 0, 'O');
