INSERT INTO `resource_online` VALUES (794, 1, '2006-08-26 18:10:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 10 ', ' 1 ', 'Déclic sciences technologie -Situations problèmes en sciences', 'À tire-d’aile. Cette problématique permet aux élèves de se familiariser avec le fonctionnement des machines qui se déplacent grâce à l’action de l’air.Situations d’apprentissage en sciences au cycle 3\r\nCette section contient différents type de situations d’apprentissage que vous pouvez utiliser en science et technologie.  Problématiques pour le  -  1er cycle   -  2e cycle  -  3e cycle', 'Alexandre Gaudreau', ' sciences techologie air ', 'http://www.cspi.qc.ca/cpp/sciences/index.php', 'fr.png', 'N', 0, 'O');
