INSERT INTO `resource_online` VALUES (806, 1, '2006-08-26 20:30:59', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 8 ', ' 4 ', 'French chat pour apprendre le Français FLE', 'French Chat est destiné à promouvoir les échanges entre francophones et non-francophones, et aider ces derniers à parfaire leur maîtrise de la langue française. Ici vous rencontrerez des gens venus des quatre coins du monde, des gens de tous âges et de toutes cultures, des gens qui ont en commun de parler ou d''apprendre à parler la langue française.', '', ' FLE communication orale français ', 'http://french-chat.com/', 'fr.png', 'N', 0, 'O');
