INSERT INTO `resource_online` VALUES (808, 1, '2006-08-26 22:02:13', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 20 ', ' 3 ', 'EDUMUSIC La liste de diffusion sur l''éducation musicale à l''école', 'Objet de la liste :\r\nLa liste edumusic constitue un organe de liaison entre ces différents intervenants en permettant l''échange d''idées et de démarches pédagogiques. Elle rend possible l''expression et la solution de problèmes rencontrés sur le terrain et consolide les fondements de l''enseignement de la musique à l''école.', 'Christian Wild', ' musique formation ', 'http://membres.lycos.fr/jsb/page18.shtml', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (809, 1, '2006-08-26 22:05:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 20 ', ' 3 ', 'Tutoriaux pour le son', 'Le site cestfacile.org propose des tutoriaux pour les formats MP3, Ogg vorbis, MPC, Wav. Les logiciels présentés sont Cdex et Audacity.', 'cestfacile.org', ' AUDIO MP3 WAV  OGG VORBIS MPC son logiciel ', 'http://www.cestfacile.org/audio.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (810, 1, '2006-08-26 22:08:52', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 20 ', ' 3 ', 'NoteWorthy Composer, un éditeur de partition gratuit', 'NoteWorthy Composer est essentiellement dédié à la production et l''impression de partitions musicales. On peut le comparer à un petit traitement de texte musical.\r\nUn logiciel gratuit pour les musiciens et compositeurs.', 'jean-jacques.freycon@ac-lyon.fr', ' Logiciel shareware gratuit musique partition ', 'http://membres.lycos.fr/jsb/page18.shtml', 'fr.png', 'N', 0, 'O');
