INSERT INTO `resource_online` VALUES (816, 1, '2006-08-26 22:56:08', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 13 ', ' 4 ', 'Cats Family un jeu de carte pour développer le calcul mental', 'Edités par Cat’s Family, Addi Cat’s et Multipli Cat’s sont 2 jeux de cartes à la fois ludiques et éducatifs, dont l’un des objectifs est d’entraîner petits et grands au calcul mental, soit des additions, soit des multiplications. Le but du jeu est de donner toutes ses cartes à ses adversaires en tapant judicieusement sur la souris, carte placée au centre de la table. Ces 2 jeux développent le calcul mental, la concentration, la mémoire et la rapidité. 13,5€ le jeu', 'Catsfamily', ' calcul mental additions mutiplications ', 'http://www.catsfamily.net/main/accueil.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (817, 1, '2006-08-26 23:00:25', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 20 ', ' 4 ', 'Tralalère un site musical', 'Tralalere.com est un site ludo-culturel et musical destiné aux enfants.Adapté aux 3-10 ans,  le site est personnalisé en fonction du profil de l''enfant.', 'www.tralalere.net', ' musique culture ', 'http://www.tralalere.com/', 'fr.png', 'N', 0, 'O');
