INSERT INTO `resource_online` VALUES (820, 1, '2006-08-26 23:06:17', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 20 ', ' 3 ', 'Glossaire des instruments de musique du monde', 'Ce glossaire reprend les principaux instruments de musique traditionnels cités dans les ouvrages consacrés à ce genre musical.', 'Centre d''Information des Musiques Traditionnelles et du Monde Paris', ' musique monde  glossaire ', 'http://www.cimt.irma.asso.fr/instruments/index.php', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (821, 1, '2006-08-26 23:14:17', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 11 ', ' 20 ', ' 1 ', 'Radio classique pour écouter des morceaux classiques', 'Une radio dédiée au classique.\r\nPossibilité de rechercher une oeuvre dans un moteur de recherche.\r\nLes morceaux présentés sont légendés.', 'Radio Classique', ' musique écoute radio ', 'http://www.radioclassique.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (822, 1, '2006-08-26 23:18:24', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 20 ', ' 3 ', 'Bibliothèque en ligne de l''IRMA', 'Le Centre de documentation et de ressources de l’irma propose, en téléchargement ou en lien, une sélection d’ouvrages dont il dispose.\r\nMusiques enregistrées\r\n\r\n    * Economie du secteur phonographique\r\n    * Production, édition et distribution indépendantes\r\n    * Nouvelles technologies\r\n\r\nSpectacles\r\n\r\n    * Reglementations et économie du spectacle\r\n    * Spectacles et développement local\r\n    * Intermittence\r\n\r\nMédia\r\n\r\n    * Audiovisuel\r\n    * Radio\r\n\r\nCréation et droits d’auteur\r\n\r\n    * La création artistique\r\n    * Code de la propriété intellectuelle et droits d’auteur\r\n    * Gestion collective des droits d’auteur et des droits voisins\r\n    * Oeuvres du domaine public\r\n    * Droits d’auteur et téléchargement', 'IRMA', ' musique:textes droits auteurs voisins ', 'http://www.irma.asso.fr/rubrique.php3?id_rubrique=6', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (823, 1, '2006-08-26 23:22:30', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 11 ', ' 20 ', ' 1 ', 'Musical''Ecoles pour le chant choral', 'Un site pour chanter. Un répertoire. Du karaoké. Des pistes pour le chant chorale.', 'jlepeintre@aei.ca', ' musique chants chorale ', 'http://j.lepeintre.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (824, 1, '2006-08-26 23:27:27', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 20 ', ' 1 ', 'Logiciels de musique de Métronimo', 'Ces programmes ludo-éducatifs permettent de tester sa culture musicale, d''apprendre le solfège, les instruments, les compositeurs classiques.\r\n    *  Mini Tétronimo et Les instruments de musique sont gratuits\r\n    * Les autres programmes sont vendus chacun 22 euros.', 'metronimo', ' logiciel musique payant ', 'http://www.metronimo.com/fr/logiciels/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (825, 1, '2006-08-26 23:29:22', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 20 ', ' 4 ', 'Logiciel pour apprendre à reconnaître les instruments de musique', 'Le jeu consiste à déplacer le nom d''un instrument sur l''image correspondante. \r\n\r\nParmi 24 instruments, le joueur doit reconnaître:\r\n\r\n    * les instruments à vent\r\n    * les instruments à cordes\r\n    * les percussions\r\n    * tous les instruments à partir de leur nom\r\n    * les instruments à partir du nom de leur famille (vent, cordes, percussions)\r\n\r\nChaque section possède 3 niveaux différents (les instruments sont les mêmes, mais le temps pour les reconnaître est différent)', 'metronimo', ' musique instrument jeux ', 'http://www.metronimo.com/fr/logiciels/index.php?page=instruments', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (826, 1, '2006-08-26 23:31:35', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 20 ', ' 4 ', 'Logiciel &quot;Le livre du compositeur&quot;', 'Le livre des compositeurs est un logiciel éducatif dont l''objectif est de vous faire apprendre le nom des plus grands compositeurs de musiques classiques. Ce programme contient 104 compositeurs, 192 portraits, dont 22 caricatures, et 328 musiques.\r\nIl est constitué de 2 parties : une partie ludique, sous forme de jeu du pendu, et une partie encyclopédie.\r\nLa section &quot;encyclopédie&quot; permet d''écouter les musiques, de voir les portraits, l’année de naissance, l’année du décès et la nationalité des 104 compositeurs. Les compositeurs sont classés par ordre alphabétique.\r\nLa section &quot;Jeux&quot; permet de découvrir les compositeurs par l''intermédiaire d''un jeu proche du jeu du pendu.', 'metronimo', ' logiciel gratuit musique compositeur ', 'http://www.metronimo.com/fr/logiciels/index.php?page=livre', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (827, 1, '2006-08-26 23:34:30', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 20 ', ' 4 ', 'Métronimo Quiz un logiciel pour tester sa culture musicale', 'Métronimo Quiz - Version 2.30 est un questionnaire contenant plus de 600 questions liées à l''histoire de la musique, aux compositeurs, à la théorie de la musique, aux termes musicaux.\r\nPour jouer, vous devez d''abord télécharger l''interface du jeu, puis ensuite installer les questions.\r\nCe programme permet de jouer seul contre l''ordinateur. Dans ce cas vous pouvez soit vous entraîner, soit essayer de faire le score le plus élevé. Il est aussi possible de jouer à 2 ou à 3 en même temps.', 'metronimo', ' musique quiz compositeurs ', 'http://www.metronimo.com/fr/logiciels/index.php?page=quiz', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (828, 1, '2006-08-26 23:37:37', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 20 ', ' 3 ', 'Un site pour télécharger les partitions', 'Cet annuaire référence des pages contenant des partitions gratuites. Elles proviennent de musiques tombées dans le domaine public. Vous pouvez donc imprimer les partitions et interpréter la musique sans devoir payer de droit d''auteurs. Vous pouvez  utiliser le moteur de recherche ou choisir une catégorie. Les partitions sont classées par compositeur et par instrument.', 'metronimo', ' musique partition compositeurs instruments ', 'http://partitions.metronimo.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (829, 1, '2006-08-26 23:39:41', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 20 ', ' 4 ', 'Jeux de quiz de reconnaissance de musique', '*   Reconnaissance de musiques\r\n    * Lecture de notes\r\n    * Les prénoms de chanteurs français\r\n    * Les compositeurs\r\n    * Les instruments\r\n    * Divers', 'metronimo', ' musique:quiz compositeurs instuments chanteurs ', 'http://www.metronimo.com/fr/jeux/quiz/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (830, 1, '2006-08-26 23:41:47', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 20 ', ' 1 ', 'portraits de compsiteurs en musique-Puzzle', 'Méga Puzzle avec des portraits de compositeurs', 'metronimo', ' puzzles compositeurs musique ', 'http://www.metronimo.com/fr/jeux/megapuzzle/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (831, 1, '2006-08-26 23:43:15', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 20 ', ' 4 ', 'Mots mêlés sur le thème de la musique', 'liquez sur le thème de votre choix parmi ceux proposés ci-dessous :\r\n\r\n    * Les compositeursExemple\r\n    * Les instruments\r\n    * Le jazz\r\n    * Les danses\r\n    * La chanson française (A-C)\r\n    * La chanson française (D-L)\r\n    * La chanson française (M-Z)\r\n    * Les contemporains de Beethoven\r\n    * Les contemporains de Mozart\r\n    * Les opéras de Puccini\r\n    * Les œuvres orchestrales de Maurice Ravel\r\n    * Les œuvres symphoniques de George Gershwin\r\n    * Le Carnaval des Animaux de Camille Saint-Saëns\r\n    * Chopin et le piano\r\n    * Les opéras de Verdi\r\n    * Les opéras de Rossini', 'metronimo', ' musique jeux mots mêlés ', 'http://www.metronimo.com/fr/jeux/index.php?page=motscaches', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (832, 1, '2006-08-26 23:46:09', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 20 ', ' 4 ', 'Jeux du pendu pour la musique', '5 jeux où vous devez deviner les noms d''instruments, de compositeurs, de chanteurs, de musiciens de jazz, de danses avant que Métronimo ne soit pendu\r\nChoisissez le thème du pendu :\r\nLes compositeurs\r\nLes instruments\r\nLe jazz\r\nLes danses\r\nLa chanson française', 'metronimo', ' musique jeux compositeurs instruments danses jazz chanson ', 'http://www.metronimo.com/fr/jeux/pendu/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (833, 1, '2006-08-26 23:48:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 20 ', ' 4 ', 'Logiciel de mémoire - reproduire des séquences sonores', 'Reproduire les séquences sonores en suivant les couleurs', 'metronimo', ' logiciel mémoire musique attention ', 'http://www.metronimo.com/fr/jeux/index.php?page=memogame', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (834, 1, '2006-08-26 23:51:52', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 20 ', ' 4 ', 'Logiciel pour jouer au xylophone', 'Possibilité de jouer au xylophone en ligne.', 'metronimo', ' musique instrument xylophone ', 'http://www.metronimo.com/fr/jeux/index.php?page=xylophone', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (835, 1, '2006-08-26 23:56:26', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 20 ', ' 3 ', 'Annuaire de musique', 'Diffusion - Interprètes - Radios - Sonorisation - Spectacles - Studios\r\nAnalyses - Apprendre un instrument - Enseignement à distance - Formation - Théorie\r\nEditeurs de partitions - Educatifs - MP3 - Séquenceurs\r\nFormes et genres - Les compositeurs - Les époques - Musique de film\r\nrique - Amérique du nord - Amérique latine, centrale, du Sud - Asie - Europe - Moyen-Orient\r\nChanson française - Country - Danse - M.A.O. - Musique électronique - Musique mécanique - Pop, rock - Punk, hardcore, hardrock, métal - Rap, Hip hop - Reggae', 'metronimo', ' musique annuaire ', 'http://www.metronimo.com/fr/annuaire/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (836, 1, '2006-08-26 23:58:13', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 20 ', ' 1 ', 'Dictionnaire pratique et historique de la musique', 'Les définitions proviennent du dictionnaire pratique et historique de la musique par Marie BOBILLIER, - en littérature Michel BRENET, - édité en 1926. Il n''y a donc pas de termes en rapport avec  le jazz, le rock, la musique contemporaine, etc. Si vous ne trouvez pas ici les renseignements dont vous avez besoin, consultez l''annuaire de Métronimo ou l''annuaire dmoz. Certaines définitions ont été modifiées pour rester d’actualité. Vous pouvez télécharger le dictionnaire original au format pdf sur le site Gallica (Bibliothèque Nationale de France).', 'metronimo', ' dictionnaire:musique histoire ', 'http://dictionnaire.metronimo.com/index.php?a=index&amp;d=1', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (837, 1, '2006-08-27 00:00:21', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 20 ', ' 1 ', 'Partitions de chansons du domaine public', 'ous trouverez ici quelques partitions de chansons appartenant au domaine public. Vous êtes libre de les télécharger, les imprimer et les exécuter. Chaque partition comporte au minimum une portée pour la voix, les paroles et un accompagnement de piano.\r\n\r\n    * Cadet Rousselle\r\n    * C''est le roi Dagobert\r\n    * Fanchon (elle aime à rire, elle aime à boire)\r\n    * Au clair de la lune\r\n    * L''invincible Malbrough (Malbrough s''en va en guerre)\r\n    * Les noces de Figaro (l''air est le même que celui de L''invincible Malbrough)\r\n    * Ah! vous dirai-je maman!\r\n    * La marseillaise\r\n    * Plaisir d''amour', 'metronimo', ' chants partitions musique ', 'http://www.metronimo.com/fr/partitions_chansons/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (838, 1, '2006-08-27 00:02:59', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 20 ', ' 1 ', 'Site de Mauro Giuliani et de la guitare', 'Partitions\r\nIntegral\r\nSolos\r\nDuos\r\nTrios/Quatuors\r\nConcertos\r\nGuitare et Piano\r\nGuit et Flute ou Violon Chant et Guit ou Piano\r\nHistoire\r\nLe 18ème siècle\r\nLe 19ème siècle\r\nGuitares Anciennes\r\nVidéos\r\nRossiniane Op 121\r\nAllegro Maesto Op 30\r\nVariations Op 107\r\nVariations Op 45', 'metronimo', ' musique partitions vidéos guitare ', 'http://www.metronimo.com/fr/annuaire/site-musique/index.php?url=http://maurogiuliani.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (839, 1, '2006-08-27 00:10:37', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 20 ', ' 4 ', 'Happy Note! pour apprendre le solfège', 'En téléchargeant un jeu video educatif Happy Note! vous ou votre enfant allez - enfin ! - apprendre le solfège sans devoir appréhender l''heure du cours de musique ! Happy Note! Lecture Musicale Clé de Sol et Clé de Fa. Un jeu video pour apprendre à lire la musique en s''amusant cours de solfege\r\nConçu par des professionnels de l''enseignement musical, chaque logiciel de musique Happy Note! est un vrai jeu vidéo (score, vies, dynamite, cartoon, Hi-Score, etc.), à la fois ludique et pédagogique.\r\nJeu pour les écoles de musique ou des activités après la classe. ( Le solfège ne fait pas partie des programmes 2002)', '', ' solfège musique logiciel ', 'http://www.happynote.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (840, 1, '2006-08-27 00:15:33', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 8 ', ' 4 ', 'La radio enfant', 'La radio enfant.Cliquez pour agrandissement\r\nLecture d''une histoire à deux. Pour les 6 à 12 ans - Faire de la radio à l''école? Trucs pour réaliser une activité radio à l''école!Faire de la radio dans tes périodes de loisirs? Des explications pour la radio à l''école.', 'radioenfant.ca', ' radio expression orale oral ', 'http://radioenfant.ca/index.cfm?Repertoire_No=442865855&amp;Voir=menu_liste3&amp;M=581', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (841, 1, '2006-08-27 00:19:11', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 20 ', ' 1 ', 'Logiciels pour traiter le MP3', 'OUTILS MP3\r\nVous trouverez ici tous les outils permettant la création, la manipulation, la conversion et la diffusion de mp3.', 'zicweb.com', ' MP3 sons musique ', 'http://www.zicweb.com/scripts/get.php?lg=fr&amp;i=29', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (842, 1, '2006-08-27 00:23:19', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 20 ', ' 1 ', 'Du jazz pour les petits', '#\r\nEnregistrer avec des enfants de CM1 non-sélectionnés en fonction de leurs compétences musicales un CD de qualité sur des musiques jazz, blues, swing, et bossa.\r\n# Faire écrire aux 51 enfants (30 garçons et 21 filles) les paroles de plusieurs chansons ( le blues des examens, j''ai pas fait mes devoirs, le blues des sports )\r\n#\r\nRencontrer des musiciens professionnels, les côtoyer, et SURTOUT : prendre du plaisir à jouer et chanter ENSEMBLE.', 'charromero&quot;@rogers.com;j.lepeintre@free.fr', ' musique jazz production ', 'http://dujazzpourlespetits.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (843, 1, '2006-08-27 00:27:09', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 20 ', ' 4 ', 'Musenvol un logiciel en ligne pour la musique', 'Des activités autour des instruments de musique.\r\nD''amusantes activités musicales pour les élèves du primaire et d''utiles outils pédagogiques pour les pédagogues.', 'Éditions de l''Envolée', ' musique instruments ', 'http://www.musenvol.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (844, 1, '2006-08-27 09:25:51', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Jouer en ligne avec Mathador', 'Mathador est un jeu de société de mathématiques.Le jeu est un parcours sur plateau où l''on avance à coups de dés multifaces pour parcourir 63 cases en calculant vite et bien, et en répondant à des petits problèmes. Avec Mathador, le calcul mental a ôté sa blouse grise IIIème République et redevient attrayant.Contrairement au jeu plateau où on joue à plusieurs, dans ce jeu en ligne vous jouez seul, contre le temps.Le joueur part avec un capital de 1000 points à faire fructifier !', 'Eric Trouillot', ' mathématiques jeu calcul mental ', 'http://www.mathador.fr/crdp/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (845, 1, '2006-08-27 09:32:33', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 1 ', ' 8 ', ' 3 ', 'Sites pour l''éducation à l''image', 'Une page du CRDP de Besançon.', 'images', ' images médias ', 'http://crdp.ac-besancon.fr/RESSOURC/webedit/cav.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (846, 1, '2006-08-27 09:38:25', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 1 ', 'Les photos des pages jaunes', 'Les &quot;pages jaunes&quot; offrent la possibilité de voir la photo de son immeuble, de son école... en se déplaçant sur un plan. Activité possible : refaire virtuellement le trajet de son domicile à l''école. Pour obtenir une photo, choisissez une ville, entrez un numéro et une rue, puis cliquez sur &quot;rechercher&quot;.Ou bien cliquez directement sur une ville de la carte de France', 'pages jaune', ' géographie plan villes ', 'http://photos.pagesjaunes.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (847, 1, '2006-08-27 09:44:23', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 9 ', ' 4 ', 'Logiciel du  jeu des 5 erreurs pour le cycle 1', 'L''enfant doit cliquer sur chacune des 5 différences qui existent entre les deux dessins. Le programme d''installation inclut 10 dessins, vous pouvez en charger d''autres ci-dessous.', 'Thierry Collard', ' jeu attention logiciel cycle1 ', 'http://thierry.collard.free.fr/jeu5erreurs.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (848, 1, '2006-08-27 09:54:41', '0000-00-00 00:00:00', 0, 0, ' ', ' 8 ', ' ', ' 1 ', 'TBI cache', 'Une fonction de tableau interactif SANS tableau interactif (mais avec un vidéoprojecteur). \r\nCe petit utilitaire permet de simuler la fonction &quot;cacher-dévoiler progressivement l’écran&quot; présente sur certains tableaux interactifs. Un vidéoprojecteur suffit. Le logiciel est gratuit et léger.', '', ' vidéoprojecteur TBI logiciel ', 'http://www.ac-amiens.fr/inspections/80/amiens5/tice/tbicache/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (849, 1, '2006-08-27 09:57:14', '0000-00-00 00:00:00', 0, 0, ' ', ' 11 ', ' ', ' 1 ', 'CDex un logiciel pour gérer des fichiers sons', 'CDex permet d’extraire les pistes audios d’un CD et de les enregistrer sur votre disque dur au format WAV ou MP3. Il permet également de convertir un fichier wav en fichier mp3 et réciproquement. Un son enregistré avec le magnétophone Windows sera ainsi converti en mp3 . idéal pour alléger les fichiers sons.', 'Laurent Desport', ' sons logiciel:utilitaires ', 'http://alecole.educ.cg86.fr/tice/article.php3?id_article=97', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (850, 1, '2006-08-27 09:59:37', '0000-00-00 00:00:00', 0, 0, ' ', ' 9 ', ' ', ' 1 ', 'Xnview un logiciel visualiseur, convertisseur de fichiers graphiques', 'XnView est un utilitaire très simple pour visualiser, redimensionner, découper, les images. \r\nIl peut également les renommer et les convertir par lot. Il offre la possibilité de réaliser rapidement un diaporama ou une mise en page html de vos photos sous forme de vignettes. Idéal pour une mise en ligne rapide de photos à un format identique', 'Laurent Desport', ' logiciel images utilitaires ', 'http://alecole.educ.cg86.fr/tice/article.php3?id_article=96', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (851, 1, '2006-08-27 10:03:21', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 1 ', 'Sélection de logiciels de mathématiques pour le TBI', 'Logiciels adaptés à une utilisation avec Tableau Blanc Intéractif \r\nMathématiques :\r\n\r\nDecompte : numération - cycle 2\r\n\r\nDoigts : numération - cycle 2\r\n\r\nTableau : tableau double-entrée - cycle 2\r\n\r\nTableaux : \r\nApprenti géomètre\r\n\r\nDéclic : géométrie plane - cycle 3\r\n\r\nPoly : géométrie dans l’espace - cycle 3\r\n\r\nTracenpoche \r\n\r\n\r\nGéomobile', '', ' logiciel TBI mathématiques ', 'http://alecole.educ.cg86.fr/tice/article.php3?id_article=69', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (852, 1, '2006-08-27 10:04:53', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'ressources en ligne en géométrie pour le Cycle 3 et la 6 ème', 'GÉOMÉTRIE EN LIGNE \r\n \r\n\r\nConstruction de la perpendiculaire une droite donnée (passant par un point sur la droite) (à l''équerre) \r\nConstruction de la perpendiculaire une droite donnée (passant par un point sur la droite) au compas \r\n\r\nMesurer un angle au rapporteur \r\n\r\nTracé de la bissectrice d''un angle \r\n\r\nTracé de la médiatrice d''un segment \r\n\r\nConstruction d''un triangle équilatéral \r\n\r\nConstruction d''un triangle isocèle \r\n\r\nConstruction d''un parallélogramme', 'onlineformapro', ' mathématiques géométrie ', 'http://www.onlineformapro.com/espaces/formateur/pedago/peda/signetmath8.asp', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (853, 1, '2006-08-27 10:07:32', '0000-00-00 00:00:00', 0, 0, ' ', ' 11 ', ' ', ' 1 ', 'Des tutoriels pour utiliser Audacity', 'Un éditeur de son : gratuit, simple et en licence libre\r\n\r\nPratique pour découper les fichiers sonores, s’enregistrer avec un micro ou enregistrer le son d’une radio web...', 'Cédric Couvrat', ' logiciel libre sons tutoriels ', 'http://alecole.educ.cg86.fr/tice/article.php3?id_article=74', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (854, 1, '2006-08-27 10:10:34', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Sébran un logiciel pour le cycle 2 et le cycle 3', 'le logiciel Zébran propose 12 activités : \r\n  dénombrement \r\n  addition \r\n  soustraction \r\n  multiplication \r\n  lecture : association image-&gt;mot \r\n  pendu \r\n  memory \r\n  clavier', 'Cédric Couvrat', ' logiciel dénombrement addition soustraction multiplication lecture mémory cycle2 cycle3 ', 'http://alecole.educ.cg86.fr/tice/article.php3?id_article=46', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (855, 1, '2006-08-27 10:12:28', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 7 ', ' 1 ', 'Puzzmat', 'PUZ-MAT est un générateur de puzzles pour la maternelle et le cycle 2.\r\nA partir de n’importe quelle image ou photo scannée :\r\n  réalisez facilement un puzzle de 4 à 36 pièces, \r\n  avec ou sans modèle, \r\n  avec ou sans rotation des pièces, \r\n  jouez à l’écran, ou bien \r\n  imprimez-le à la taille de votre choix pour en faire un puzzle carton.&quot;', 'Cédric Couvrat', ' puzzle jeux logiciel cycle1 cycle2 ', 'http://alecole.educ.cg86.fr/tice/article.php3?id_article=10', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (856, 1, '2006-08-27 10:16:29', '0000-00-00 00:00:00', 0, 0, ' ', ' 9 ', ' ', ' 1 ', 'Réduire des photos en 1 clic avec EasyThumbnail', 'Traîte des images par lot ou à l’unité, en 1 clic droit, pour les réduire en gardant une qualité correcte (essentiellement pour mettre en ligne ou envoyer par mail) \r\nA télécharger ici : http://www.fookes.net/ftp/free/EzThmb_Setup.exe', 'Nicolas Faure', ' images logiciel freeware traitement ', 'http://alecole.educ.cg86.fr/tice/article.php3?id_article=99', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (857, 1, '2006-08-27 10:20:43', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 2 3 11 22 ', ' 4 ', '&quot;Oui Dire&quot; un logiciel gratuit de langue', 'Un logiciel gratuit d’apprentissage des langues vivantes (anglais ou allemand). Le contenu : Imagier sonore, Compréhension, Discrimination auditive, Familiarisation avec le vocabulaire de base écrit, Dictée sonore à partir de l’imagier, Comptines, Quizz personnalisable, Visite culturelle du pays, Accès au studio d’enregistrement à tout moment, etc.', 'CORINNE ET JEAN-MICHEL BARDIN', ' langues vivantes anglais allemand logiciel gratuit freeware ', 'http://cmbardin.free.fr/Lookhere.zip', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (858, 1, '2006-08-27 10:23:35', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 8 ', ' 1 ', 'Le logiciel lectra', 'Shareware français.Didacticiel d''entraînement à la lecture basé sur une série d''exercices générés automatiquement par le programme. Logiciel ouvert: les textes utilisés sont saisis par l''enseignant. \r\nLogiciel ouvert : les textes utilisés sont saisis par l’enseignant. Dans la version enregistrée, l’utilisateur peut personnaliser les fichiers des mots Intrus. Suivi personnalisé des résultats de l’élève.Exercices proposés : closure, reconstitution, mots outils, mots éclairs, phrase mélangée, phrase incomplète, phrase sans espaces, lecture rapide, mots identiques, l’intrus, ordre alphabétique, memory (images), memory (mots), texte mélangé, texte à corriger.', 'Michel Brun', ' lecture logiciel cycle2 cycle3 ', 'http://www.lectramini.com/lectra.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (859, 1, '2006-08-27 10:26:23', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Le logiciel Lectra-Mini pour le cycle 1 et le cycle 2', 'Utilisable à partir de la Grande Section de maternelle et du CP ce didacticiel d’entraînement à la lecture est basé sur une série d’exercices générés automatiquement par le programme. Ce logiciel est ouvert : L’enseignant saisit les textes de son choix et ajoute des images s’il le souhaite. C’est un excellent complément à une lecture d’albums.', 'BRUN-VILLANI', ' logiciel lecture maternelle CP GS albums ', 'http://www.lectramini.com/atelier.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (860, 1, '2006-08-27 10:28:29', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 9 ', ' 4 ', 'Un logiciel pour le cycle 1 et le cycle 2 : La ligne magique', 'Ludo-Educatif pour apprendre aux enfants de plus de 5 ans à compter, à colorier et à utiliser la souris.\r\nL''enfant doit relier les points consécutifs pour découvrir une image.Il peut alors la colorier.\r\nIl est possible d''imprimer l''image de points, l''image finale ou l''image finale coloriée.\r\nLes images peuvent être affichées de façon aléatoire ou linéaire.\r\nAide en ligne complète.', 'Tom Cat Soft', ' logiciel numération ', 'http://www.tomcatsoft.com/products/lignemagique/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (861, 1, '2006-08-27 10:31:59', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel Gagne à 10 pour le cycle 1', 'Gagne à 10, un logiciel pour compter jusqu''à 10 en utilisant les cartes à points.\r\nCe jeu est inspiré des travaux de Roland Charnay ''Dix dans la boîte''.', 'IA Amiens', ' numération calcul ', 'http://www.ac-amiens.fr/inspections/80/amiens5/tice/gagnea10/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (862, 1, '2006-08-27 10:35:24', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 8 ', ' 4 ', 'Le logiciel Cache-mots pour le cycle 2 et le cycle 1', 'Cachemot est un logiciel de lecture créé par Jean-Luc Grenard. Il propose à l''enfant d''identifier les mots masqués par des caches de taille variable selon le niveau de difficulté choisi.\r\nIl propose à l''enfant d''identifier les mots masqués par des caches de taille variable selon le niveau de difficulté choisi.\r\nLe logiciel est ouvert, il permet d''utiliser très facilement ses propres listes de mots (voir aide). Cachemot sauvegarde les résultats de chaque enfant.', 'Jean-Luc GRENARD', ' lecture cycle2 gratuit ', 'http://jlgrenar.free.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (863, 1, '2006-08-27 10:39:15', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Le logiciel Apprenti-géomètre pour le cycle 3', 'Apprenti Géomètre est un logiciel interactif conçu comme une aide à l''enseignement des mathématiques. Il a été réalisé au terme d''une recherche menée par une équipe de chercheurs du CREM, à la demande du Ministère de la Communauté française de Belgique.', 'CREM Belgique', ' géométrie mathématiques logiciel cycle3 ', 'http://enseignement.be/geometre/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (864, 1, '2006-08-27 10:50:53', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 13 ', ' 8 ', ' 4 ', 'le logiciel Logotype', 'Logotype 1.2 est un logiciel qui permet de créer un petit dictionnaire interactif. L''utilisateur saisit des articles en précisant leur nom. Des liens hypertextes se créent automatiquement lorsqu''un texte contient le nom d''un article.\r\nOn peut très facilement ajouter, supprimer et modifier des articles, imprimer, voire exporter le contenu d''un article au format rtf. La rubrique lettres de l''académie de Grenoble propose par exemple une version Logotype-lettres contenant un ensemble d''articles à l''usages des élèves et des enseignants de lycée.', 'Logotype est développé dans le cadre du Lycée Ouvert de l''académie de Grenoble', ' logiciel dictionnaire ', 'http://www.ac-grenoble.fr/lettres/articles.php?lng=fr&amp;pg=296', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (865, 1, '2006-08-27 11:25:54', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 16 ', ' 4 ', 'Free-Mind un logiciel pour organiser sa pensée.', 'Le logiciel Freemind, proposé sous licence GNU General Public Licence, permet de créer des cartes heuristiques (littéralement, cartes du cerveau) de façon à la fois intuitive et interactive. La mise en forme des textes, entièrement repensée, permet de mettre en exergue une hiérarchie, un plan, une suite d’idées, des liens hypertextes. Grâce aux cartes créées, on obtient une visualisation plus intuitive et plus complexe d’un projet que ne le permettrait le simple système de titres et sous-titres des traitements de textes.', 'Goerg Mueller', ' méthode cartes heuristiques organisation pensée logiciel libre gratuit ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=297', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (866, 1, '2006-08-27 12:00:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 19 ', ' 4 ', 'Inspiration (logiciel Reconnu d''Intérêt Pédagogique)', '&quot;Les plans et diagrammes proposés par Inspiration vous aident à mieux comprendre les concepts et les informations.\r\nEn mode graphique, vous pouvez créer et utiliser facilement les réseaux de concepts, les diagrammes et autres présentations graphiques.En mode plan, vous pouvez voir vos idées et pensées instantanément transformées en plan de travail de rédaction, vous pouvez changer facilement l’ordre et le classement des idées&quot;\r\nsite des logiciels reconnus d''interêt pédagogique : http://www.educnet.education.fr/res/rip/prodI.htm', 'Demar&lt;que Editeur', ' organisation pensée méthodogie RIP logiciel payant ', 'http://www.demarque.com/demarque/francais/boutique/visualiser.asp?numero=49', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (867, 1, '2006-08-27 12:03:12', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 10 ', ' 4 ', 'Logiciels RIP', 'Produits multimédias éducatifs autorisés à utiliser la marque semi-figurative RIP\r\n&quot; Reconnu d''intérêt pédagogique par le Ministère de l''Éducation nationale &quot; \r\nListe classée par ordre aphabétique des produits \r\nListe des sociétés ayant présenté au moins un produit au guichet permanent', 'Ministère qui a établi la liste', ' Logiciels RIP mutimédias CD-Roms ', 'http://www.educnet.education.fr/res/rip/prodI.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (868, 1, '2006-08-27 12:34:33', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel libre &quot;A nous les nombres&quot; pour le cycle 1 et le cycle 2', 'Logiciel libre, abordable à partir du cycle 1, qui permet de travailler la construction des nombres.\r\nIl regroupe les activités suivantes :\r\nCalapa : situations d’énumérations\r\nBarques : situations de dénombrement\r\nCalapa : situations de dénombrement et écritures additives\r\nConstellation : activité de systématisation, construction de collections equipotentes\r\nNombre : activité de systématisation, construction de collections equipotentes\r\nOrdinal : travail sur l’ordinal', 'AbulEdu et son', ' numération nombres Cycle1 cycle2 ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=134', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (869, 1, '2006-08-27 12:44:39', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel libre &quot;Calcul&quot; pour le cycle 3 et le cycle 2', 'Logiciel d’entraînement aux mathématiques - Calcul rapide et calcul mental', 'Auteur : l’équipe de développement Le Terrier d’AbulÉdu Version : 1.19', ' Calcul mental rapide logiciel libre cycle2 cycle3 ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=135', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (870, 1, '2006-08-27 12:46:15', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel libre &quot;Dr Géo&quot;', 'Dr. Geo est un logiciel de géométrie interactive. Il permet de créer des figures géométriques et de les manipuler interactivement en respectant leurs contraintes géométriques. Il est utilisable dans des situations d’enseignement/apprentissage avec des élèves du primaire ou du secondaire', 'Hilaire Fernandez', ' géométrie logiciel dynamique cycle3 libre gratuit ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=133', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (871, 1, '2006-08-27 12:49:25', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel libre &quot;J''ai compris&quot; du cycle 1, au cycle 2; cycle 3', 'GCompris est un ensemble de logiciels couvrant des domains d’apprentissage variés : maîtrise de la souris et du clavier, entraînement à la lecture, aux langues vivantes, aux mathéma.Le codage par niveau de difficulté (de 1 à 4 étoiles) et le regroupement par champ d’activités participent également à l’autonomie de l’enfant face à GCompris. http://gcompris.net/', 'Bruno Coudoin', ' mathématiques sciences ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=137', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (872, 1, '2006-08-27 12:54:10', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Geonext  un logiciel de géométrie dynamique', 'Geonext est un logiciel de géométrie dynamique permettant un accès expérimental aux concepts de la géométrie et pouvant constituer une alternative à l’utilisation de Cabri-géomètre', 'Le département de didactique et mathématiques de l’université de Bayreuth', ' gémoétrie logiciel cycle3 libre gratuit ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=132', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (873, 1, '2006-08-27 12:56:35', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel libre JTANS ou tangramm', 'jTans est un logiciel permettant de s’initier et de pratiquer le tangram. Le tangram est un jeu de réflexion comparable au puzzle : il s’agit de reconstituer une silhouette donnée à partir de 7 formes imposées (2 grands triangles,1 moyen et 2 petits,1 carré et 1 parallélogramme).Toutes les pièces doivent être utilisées pour reproduire le modèle proposé et elles ne doivent pas se chevaucher.', 'Philippe Banwarth', ' logiciel libre JTANS gratuit géométrie ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=136', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (874, 1, '2006-08-27 12:58:31', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Le logiciel libre &quot;Calcul mental&quot; pour le cycle 2 et le cycle 3', 'Ce logiciel est édité par les éditions ACCES. Ce logiciel permet le calcul mental sur les points du programme en mathématiques\r\n\r\n  Additions 0 à 10, 0 à100 \r\n  ajouter 10,100,1000 \r\n  nombres décimaux. \r\n  Soustractions 0 à 10 \r\n  0 à 100, soustraire 10,100,1000, nombres décimaux. \r\n  Multiplications : tables par 25,50,75,100 \r\n  double d’un nombre, nombres décimaux. \r\n  Divisions : tables, quotient et reste, moitié d’un nombre, nombres décimaux', 'Editions ACCES', ' Calcul mental rapide logiciel libre cycle2 cycle3 gratuit ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=415', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (875, 1, '2006-08-27 13:00:30', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel libre &quot;Mesures&quot;', 'Logiciel libre qui permet d’aborder les points suivants des programmes 2002.\r\n  Mesures de longueur \r\n  mesures d’aires \r\n  volumes \r\n  Lecture de l’heure', 'ACCES', ' logiciel libre mesures longueur aires volumes temps ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=416', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (876, 1, '2006-08-27 13:03:00', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Projet &quot;mathenpoche&quot;', 'MathEnPoche est un projet soutenu par l’association Sésamath.En accord avec sa charte, les ressources disponibles sur le site de MathenPoche sont gratuitement mises à disposition de la communauté éducative par des enseignants en exercice. Elles ont pour vocation d’être enrichies continuellement par les pratiques des collègues, parents ou élèves qui les utiliseront.\r\n\r\nLe niveau 6ème est sous licence GPL et il en sera de même pour les autres niveaux au fur et à mesure de leur financement. Un niveau Cycle 3 est en préparation.', 'Association Sésamath', ' mathématiques:outils ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=284', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (877, 1, '2006-08-27 13:05:19', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel pour réviser les tables', 'Logiciel libre et gratuit permettant la révision des tables de multiplication', '?', ' mathématiques tables  addition multiplication ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=279', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (878, 1, '2006-08-27 13:07:06', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel libre &quot;Suites&quot; pour le cycle  2 et le cycle 3', 'Suites est un logiciel destiné au cycle 2 et au cycle 3 pour travailler en numération sur les suites des nombres (entiers, décimaux) arithmétiques et géométriques ou monotones irrégulières.', 'AbulEdu et son équipe', ' logiciel libre cycle2 cycle3 ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=504', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (879, 1, '2006-08-27 13:09:36', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel libre Symcolor', 'Symcolor est un logiciel destiné à la construction du principe de symétrie axiale chez l’enfant à partir de la grande section en maternelle.', 'l’équipe de développement Le Terrier d’AbulÉdu', ' gémoétrie mathématiques logiciel libre symétrie ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=505', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (880, 1, '2006-08-27 13:11:28', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel libre Tuxmath', 'TuxMath est un logiciel de calcul mental (addition, soustraction, multiplication, division. L’originalité réside dans le scénario qui sous-tend la résolution des opérations par l’utilisateur. Ce dernier est en effet dans la peau de Tux (pingouin mascotte) et a pour objectif de détruire les opérations qui arrivent du ciel et menacent de détruire les villes qu’il doit protéger', 'Sam &quot;Criswell&quot; Hart et Bill Kendrick', ' logiciel libre cycle2 cycle3 opérations calcul mental ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=79', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (881, 1, '2006-08-27 13:13:00', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Tux Maths Scrabble', 'Tux Maths Scrabble est un jeu mathématique où l’enfant joue contre Tux. Ce n’est pas un scrabble avec des lettres mais avec des chiffres. Ici, pas de lettres, mais des chiffres et des opérateurs, le but étant de créer des égalités : 6+4=10 ; mais 6+4=5+5 (plus de nombres donc plus de points gagnés)... au prochain tour, si je le peux, je peux rajouter 9+6+4=5+5+9...', 'Asymptopia', ' Calcul mental rapide logiciel libre cycle2 cycle3 ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=414', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (882, 1, '2006-08-27 13:17:02', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Logiciel libre XLOGO pour apprendre à programmer en logo', 'L’utilisateur doit déplacer un objet appelé « tortue » sur l’écran à l’aide de commande aussi simples que avance, recule, tournedroite .... A chaque déplacement, la tortue laisse un trait derrière elle et ainsi on peut créer des dessins.', 'xlogo@free.fr', ' logiciel libre logo programmation ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=160', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (883, 1, '2006-08-27 13:19:27', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 7 ', ' 4 ', 'Logiciel libre &quot;comparaison&quot; pour le cycle 1', 'Comparaison propose un exercice de discrimination visuelle. Il s’agit de reconnaître et de sélectionner, dans une série de 6, le carré colorié de manière identique au modèle situé à gauche, en début de ligne. La simplicité de l’exercice permet de l’utiliser sans connaissance préalable de l’outil informatique. L’application ne comporte qu’un seul type d’exercice, un seul niveau de jeu.', 'Project Omega', ' logiciel libre discrimination visuelle ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=211', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (884, 1, '2006-08-27 13:21:27', '0000-00-00 00:00:00', 0, 0, ' ', ' 8 ', ' ', ' 4 ', 'Logiciel libre &quot;labyrinth&quot; pour le cycle 1', 'Labyrinthe est un logiciel ludo-éducatif. Il s’agit de trouver son chemin au travers d’un labyrinthe et de rallier un point déterminé. Les configurations de jeu sont de plus en plus complexes à mesure que le joueur parvient au but et enchaîne les parties.', 'Project Omega', ' mathématiques chemin repérage labyrinthe cycle1 ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=217', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (885, 1, '2006-08-27 13:23:14', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 7 ', ' 4 ', 'Logiciel libre &quot;labyrinthe caché&quot; pour le cycle1', 'Labyrinthe Caché est un logiciel ludo-éducatif. Il s’agit de trouver son chemin au travers d’un labyrinthe et de rallier un point déterminé. Les chemins des labyrinthes ne sont pas visibles si bien que la progression se fait par &quot;essais-erreurs&quot;. Les configurations de jeu sont de plus en plus complexes à mesure que le joueur parvient au but et enchaîne les parties.', 'Project Omega', ' mathématiques cycle1 maternelle labyrinthe ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=216', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (886, 1, '2006-08-27 13:24:34', '0000-00-00 00:00:00', 0, 0, ' ', ' 8 ', ' ', ' 4 ', 'Logiciel libre &quot;KDE&quot;', 'Suivre la série de sons et de clignotements aussi longtemps que vous pouvez. Compléter les algorithmes pour gagner.', 'Albert Astals Cid', ' logiciel libre gratuit mémoire ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=454', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (887, 1, '2006-08-27 13:26:37', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 7 ', ' 4 ', 'Logiciel libre Mémo Domino pour le cycle 1', 'Mémo-Dominos est un logiciel ludo-éducatif. Il s’agit d’un jeu de memory à base de carrés où figurent des constellations de couleurs différentes et aux dispositions variables.Il est possible de paramétrer la difficulté des parties en fonction de 2 critères : le nombre d’images à mettre en paires et le temps de visualisation des images une fois qu’elles sont retournées', 'Project Omega Janick Cerfontaine Maître formateur La Rochelle pour le scénario pédagogique', ' mathématiques mémoire mémory logiciel libre cycle1 ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=215', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (888, 1, '2006-08-27 13:28:47', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 7 ', ' 4 ', 'Logiciel libre &quot;Mémo-images&quot; pour le cycle 1', 'Mémo-Images est un logiciel ludo-éducatif. Il s’agit d’un jeu de memory à base d’images. Il est possible de parmétrer la difficulté des parties en fonction de 2 critères : le nombre d’images à mettre en paires et le temps de visualisation des images une fois qu’elles sont retournées', 'Auteur : EPI (portage : Project Omega )', ' logiciel libre mémoire gratuit maternelle cycle1 ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=214', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (889, 1, '2006-08-27 13:31:33', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 7 ', ' 4 ', 'Logiciel libre - Mon petit Labyrinthe pour le cycle 1', 'Un logiciel pour agir .. dans des labyrinthes', 'Pascal Peter', ' cycle1 mathématiques géométrie labyrinthe ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=375', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (890, 1, '2006-08-27 13:33:44', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 7 ', ' 4 ', 'Logiciel libre &quot;Mulot&quot; pour le cycle 1 et la maternelle', 'Mulot est un logiciel qui propose des activités de manipulation fine de la souris telles que survoler, cliquer, parcourir un chemin ou glisser/déposer. Il permet de faire la liaison entre le plan horizontal (plan de la souris) et le plan vertical (plan de l’écran d’ordinateur).', 'AbulEdu et son équipe', ' logiciel libre gratuit souris maternelle cycle1 ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=210', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (891, 1, '2006-08-27 13:35:39', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 7 ', ' 4 ', 'Logiciel libre &quot;piles&quot; en maternelle', 'Piles est un logiciel ludo-éducatif. Le principe consiste à reproduire à l’identique une pile de 4 disques colorés à partir des disques de 5 autres piles que l’on peut déplacer.', 'Project Omega', ' logiciel libre maternelle gratuit ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=212', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (892, 1, '2006-08-27 13:38:27', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 8 ', ' 7 ', ' 4 ', 'Logiciel libre PySyCache pour le cycle 1 et le cycle 2', 'PySyCache est un logiciel éducatif destiné aux jeunes enfants (4-7 ans) ayant pour objectif de leur apprendre : à déplacer la souris à cliquer avec les boutons de souris', 'Vincent DEROO', ' logiciel libre gratuit maternelle cycle2 ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=354', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (893, 1, '2006-08-27 13:41:41', '0000-00-00 00:00:00', 0, 0, ' ', ' 8 ', ' ', ' 4 ', 'Utiliser le logiciel libre OpenOffice à l''école maternelle ou au cycle 2', 'Utiliser Open Office au cycle 2 pour réaliser un album de lecture SCHAAF Armands - Ecole de Farébersviller Moselle\r\nUtiliser Open office.org en maternelle', 'Sun Microsystems et la communauté des développeurs  Armands Schaff Di Franco Jeanine', ' traitement textes cycle1 cycle2 logiciel libre gratuit ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=181', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (894, 1, '2006-08-27 13:43:54', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 8 ', ' 13 ', ' 4 ', 'Xboard un logiciel libre pour jouer aux échecs', 'Xboard est une interface graphique permettant de jouer aux échecs,', 'Tim Mann - Version : 4.2.7', ' logiciel libre échecs ', 'http://www.logiciels-libres-premierdegre-sceren.fr/article.php3?id_article=218', 'fr.png', 'N', 0, 'O');
