INSERT INTO `resource_online` VALUES (904, 1, '2006-08-27 18:07:29', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 16 ', ' 3 ', 'Le site pour tout savoir sur les phasmes', 'Un site dédié aux insectes &quot;phasmes&quot;. A utiliser en cas d''élevage en classe. A utiliser dès le cycle 3.', 'phasme.com', ' biologie élevage phasme lecture cycle3 ', 'http://www.phasmes.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (905, 1, '2006-08-27 18:11:11', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 16 ', ' 3 ', 'Le site la main à la pâte', 'Le site La main à la pâte est destiné à aider enseignants, formateurs, scientifiques et institutionnels à mettre en place un enseignement des sciences de qualité à l’école primaire. Vous y trouverez des activités de classe, des documents scientifiques ou pédagogiques, des outils d’échange et de travail collaboratif.', 'INRP + Ministère', ' sciences projets ', 'http://www.inrp.fr/lamap/', 'fr.png', 'N', 0, 'O');
