INSERT INTO `resource_online` VALUES (910, 1, '2006-08-27 18:27:03', '0000-00-00 00:00:00', 0, 0, ' 4 5 ', ' 5 ', ' 18 ', ' 4 ', 'Un jeu sur les dangers dans la rue Cycle 2', 'Un site pour apprendre à éviter le danger dans la rue.', 'cyberechos', ' danger sécurité citoyenneté ', 'http://cyberechos.creteil.iufm.fr/cyber4/Actualite/rue/rue.htm#jeu', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (911, 1, '2006-08-27 18:32:10', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le journal des enfants pour le cycle 3', 'Le JDE est un hebdomadaire [un journal qui sort une fois par semaine].Des articles d''actualités à lire dès le cycle 3.\r\nUn dossier par semaine à lire en ligne.', 'JDE', ' presse médias actualités cycle3 ', 'http://www.jde.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (912, 1, '2006-08-27 18:55:02', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le journal &quot;les clés de l''actualité Junior&quot;', 'Envie de mieux comprendre un événement d''actualité ou besoin de faire des recherches pour réaliser un exposé? Tu entres ton mot clé ou tes mots clés et tu as accès à tous les articles publiés sur le sujet de ton choix. Tu lis, tu sélectionnes, tu imprimes…\r\nEt maintenant tu peux préparer ton exposé.', '“Les Clés de l’Actualité Junior” est une revue du Groupe Milan Presse.', ' actualité presse lecture documents journal ', 'http://www.lesclesjunior.com/Recherche/Index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (913, 1, '2006-08-27 18:59:27', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 8 ', ' 4 ', 'Le journal &quot;le p''tit môme&quot; actu pour le cycle2', 'Un journal pour se tenir informé .\r\nP''tits Mômes-Actu est un journal d''actualité créé par des élèves de cycle 2. Il s''inspire dans son organisation de la longue expérience de Mômes-Actu en cycle 3.\r\nLes élèves correspondants de presse envoient leurs articles dans le ferme espoir de les voir édités.\r\nLa classe rédactrice en chef collecte tous ces textes, les lit, les critique, les range suivant leur pertinence et décide de leur publication.', 'Inspection de la Meuse', ' actualités presse cycle2 journal ', 'http://www.ac-nancy-metz.fr/IA55/momes-actu/ptitmomesactu/nouveau/default.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (914, 1, '2006-08-27 19:05:55', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' ', ' 19 ', ' 1 ', 'Le site France 5', 'Le site Internet de France 5 permet aux téléspectateurs de retrouver des programmes déjà diffusés et de compléter leurs connaissances dans les domaines les plus divers.\r\nDes émissions et des logiciels en ligne', 'France 5', ' lecture arts ', 'http://www.france5.fr/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (915, 1, '2006-08-27 19:13:37', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 4 ', ' 13 ', ' 3 ', 'Site du Bulletin de la Pmev', 'le site du Bulletin de la Pmev vous propose de nombreuses ressources.\r\nLa Pmev propose une organisation spécifique de la classe répondant à quelques principes précis, avec un objectif très simple : gagner en efficacité et en confort de travail. Elle n''est pas une solution miracle mais un remède réfléchi à un certain nombre de difficultés aujourd''hui rencontrées dans les classes. Ce site vous présente par ses témoignages, mais aussi par la mise à disposition d''outils, comment, chaque jour, des enseignants travaillent dans leur classe avec leurs élèves. \r\nDes outils sur la production d''écrits et l''ORL. Des outils en géométrie', 'PMEV', ' organisation progressions ORL expression mathématiques géométrie ', 'http://www.lebulletinpmev.com/', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (916, 1, '2006-08-27 19:18:27', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 2 4 9 ', ' 9 ', ' 1 ', 'Le site &quot;Histoire en primaire&quot; pour le cycle 2 et le cycle 3', 'Des contenus sur l''histoire de France \r\nDes programmations pour mettre en oeuvre le programme \r\n Des démarches pour enseigner l''histoire \r\n Des outils pour enseigner l''histoire \r\n Des liens utiles \r\n Une histoire de France \r\nChronologie interactive \r\nDes QCM pour jouer', 'Marc Girot', ' histoire frise cycle2 cycle3 ', 'http://histoireenprimaire.club.fr/index.html', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (917, 1, '2006-08-27 19:20:41', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 7 ', ' 10 ', ' 4 ', 'De Napoléon à Bonaparte', 'Textes à choisir pour étudier Napoléon.', 'équipe Technologies Nouvelles du Rectorat de la Corse', ' histoire Napoléon Bonaparte ', 'http://www.ac-corse.fr/expos_autres/napoleon/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (918, 1, '2006-08-27 19:25:41', '0000-00-00 00:00:00', 0, 0, ' 7 8 ', ' 5 ', ' 10 ', ' 4 ', 'Le site de l''espiègle', 'Un centre européen pour de jeunes reporters de 11 ans à 17 ans\r\nClasses-presse : Les élèves vivent une semaine comme de vrais reporters.\r\nClasses-internet : Une semaine pour que les élèves connaissent mieux internet et réalisent un web-magazine\r\nAteliers du journal : Pour apprendre à s’exprimer par l’écrit, pour trouver sa place dans son environnement\r\nSéjours jeunes reporters : Pendant les vacances, les 11-17 ans voient le monde en reporters', 'espiègle', ' presse médias actualités cycle3 ', 'http://www.espiegle.org/', 'fr.png', 'N', 0, 'O');
