INSERT INTO `resource_online` VALUES (929, 1, '2006-08-30 20:26:14', '0000-00-00 00:00:00', 0, 0, ' ', ' ', ' ', ' 3 ', 'Le Bulletin officiel BO', 'Mentor vous permet de retrouver les références des textes depuis 1987 et l''intégralité des textes depuis janvier 1998.\r\nPour obtenir les textes antérieurs à 1998, contactez l''un des 180 lieux d''accueil du réseau C.N.D.P.', 'Ministère', ' BO bulletin officiel règlementation règlements textes ', 'http://www.education.gouv.fr/pid285/le-bulletin-officiel.html', 'fr.png', 'N', 0, 'O');
