INSERT INTO `resource_online` VALUES (944, 1, '2006-09-06 22:45:57', '0000-00-00 00:00:00', 0, 0, ' 2 ', ' 4 ', ' 7 ', ' 3 ', 'Dossier maternelle du CDDP de Haut-Rhin', 'Le langage au coeur des apprentissages \r\nVivre ensemble \r\nAgir et s''exprimer avec son corps \r\nDécouvrir le monde \r\nLa sensibilité, l''imagination, la création \r\nDocuments pour préparer la classe \r\nLa liaison GS/CP \r\nTICE en maternelle \r\nL''accueil des moins de trois ans \r\nLes relations entre l''école et la famille \r\nTraces d''activité en maternelle \r\nComptines en maternelle \r\nClasses maternelles bilingues', 'CRDP de Strasbourg', ' maternelle liaison cycle1 liaison GS CP ', 'http://www.crdp-strasbourg.fr/cddp68/maternelle/index.htm', 'fr.png', 'N', 0, 'O');
INSERT INTO `resource_online` VALUES (945, 1, '2006-09-06 22:50:08', '0000-00-00 00:00:00', 0, 0, ' ', ' 11 ', ' ', ' 4 ', 'Base de sons en format Ogg Vorbis', 'Bruitages, Echantillons, des sons classés en 7 catégories. Ces éléments sonores sont au format Ogg Vorbis.Lle site met à disposition une liste de liens vers des échantillons de musiques et de bruitages. Tous les éléments sonores sont la propriété de leur auteur.Ils sont distribués sous la Licence Art Libre', 'Lunerouge', ' sons ogg Vorbis ', 'http://www.lunerouge.org/spip/rubrique.php3?id_rubrique=29', 'fr.png', 'N', 0, 'O');
