INSERT INTO humour_items VALUES ('630','2','Le chien a beau avoir quatre pattes, il ne peut emprunter deux chemins à la fois.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('631','2','Le coassement des grenouilles n\'empêche pas l\'éléphant de boire.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('632','2','Le mensonge donne des fleurs mais pas de fruits.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('633','2','Lorsque tu joue du tam tam à coté d\'un sourd, de temps en temps tape sur sa tête pour qu\'il comprenne que ce ne sont pas des grimaces que tu fais mais plutôt de la musique.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('634','2','Lorsque tu ne sais pas où tu vas, regarde d\'où tu viens.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('635','2','Si la préhistoire a précédé l\'histoire, il n\'a jamais été démontré que la prescience ait précédé la science.<br/><br/>Pierre Dac');
INSERT INTO humour_items VALUES ('636','2','Même au plus sombre de la nuit, l\'aube est à portée de main.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('637','2','N\'insulte pas le crocodile avant d\'avoir traversé la rivière.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('638','2','Parce qu\'il vit dans l\'eau, on ne voit jamais les larmes du poisson qui pleure.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('639','2','Quand un arbre tombe, on l\'entend ; quand la forêt pousse, pas un bruit.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('640','2','Que celui qui n\'a pas traversé ne se moque pas de celui qui s\'est noyé.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('641','2','Si en te baignant tu as échappé au crocodile, prends garde au léopard sur la berge.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('642','2','Tant que les lions n\'auront pas leurs propres historiens, les histoires de chasse ne pourront que chanter la gloire du chasseur.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('643','2','Un vieil homme qui meurt est une bibliotheque qui brule.<br/><br/>Proverbe africain');
INSERT INTO humour_items VALUES ('644','2','Se venger d\'une offense, c\'est se mettre au niveau de son ennemi ; la lui pardonner, c\'est se mettre au dessus de lui.<br/><br/>Proverbe anglais');
INSERT INTO humour_items VALUES ('645','2','La vérité ne peut être contenue dans un seul rêve.<br/><br/>Proverbe arabe');
INSERT INTO humour_items VALUES ('646','2','Ce qui est passé a fui ; ce que tu espères est absent ; mais le présent est à toi.<br/><br/>Proverbe arabe ');
INSERT INTO humour_items VALUES ('647','2','Ce n\'est pas l\'eau déjà écoulée qui fera tourner la meule.<br/><br/>Proverbe arabe ');
